css:
@namespace url(http://www.w3.org/1999/xhtml);

/* RPOL.net Leopard-Skin Blue */
/* Using a Pattern Set Under License from Obsidian Dawn */
/* Style by: adrasteia1 */
/* Web: http://www.roleplayersarchive.net */

@-moz-document regexp("https?://((www\.?)?rpol\.net|rpol.hyphenit.com.au).*") {

    body, th, tr, td, a {
           font-family: Verdana, Geneva, sans-serif !important;
    }
    
    body {
           background-color: #fbf0b2 !important;
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-leopardskin-blue/leopardbg.jpg') !important; 
        }
    
    body, th, tr, td, span.normal_text {
           color: #ffffff !important;
    }
  
    table {
           background-color: #272726 !important;
    }
  
    th {
           background-color: #514747 !important;
    }
    
    td {
           background-color: #272726 !important;
    }
    
    td.quote {
           background-color: #424241 !important;
    }
    
    td.left.message {
           background-color: #272726 !important;
           border-bottom: 1px solid #424241 !important;
    }
    
    td.left.message {
           border-right: 1px solid #424241 !important;
    }
    
    td.left.message_alt {
           border-right: 1px solid #424241 !important;
    }
    
    td.left.message_alt {
           border-bottom: 1px solid #424241 !important;
    }

    td.right.sm.alink-colour {
           background-color: #272726 !important;
    }
   
    td.center.sm {
           background-color: #272726 !important;
    }

    a, a:link, a:visited, .link-colour, .msg a:hover:before, .msg a:hover:after {
           color: #11e2e4 !important;
            
    }
    
    a:hover, a:active, .alink-colour, .msg a:before, .msg a:after, a.button_on  {
           color: #FF3333 !important;
    }
 
    .border {
           border-color: #000000 !important;
	}
    .info_table {		
           border: 1px solid #000000 !important;
           background-color: #272726 !important;
	}
    table.info_box {
           border: 1px solid #272726 !important;
           background-color: #272726 !important;
	}
    #header, #footer {
           border: 2px solid #000000 !important;
    }
   
    .red, .red th, .red td {
            color: #FF3333 !important;
    }
    .blue, .blue th, .blue td {
            color: #3f7ba9 !important;
    }
    
    span.alink-colour {
            background-color: #272726 !important;
    }
    
    .stickyblock {
            background-color: #272726 !important;
    }
    
    .info_box {
            background-color: #272726 !important;
            color: #ffffff !important;
            border: #000000 !important;
    }
    
    table.selection_table td {
            border-bottom: 1px solid #424241 !important;
    }
    
    table.info_table th {
            border: none !important;
    }
    
    table.message_table th.left {
            border: 1px solid #272726 !important;
    }
    
    fieldset legend {
            color: #ffffff !important;
    }
    
    #form-menu-container.right #form-menu.message a {
            background-color: #000000 !important;
    }
    
    #wrapper:after {
            content:"Style by adrasteia1. Pattern by Obsidian Dawn - http://www.obsidiandawn.com" !important;
            font-size: 10px !important;
            float: right !important;
            width: 600px !important;
            color: #000000 !important;
    }   
      
}
