css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("elohell.net") {
#content-container .ckontent{
width: 450px !important;
}

#content-container .ckontent .box .plugin
{
width: 500px !important;
}

#content-container .ckontent .box
{
overflow: visible !important;
border: none !important;
}

#content-container .ckontent .box h1
{
display: none;
}

.ckontent .box > .comments {
    width: 416px !important;
}

#content-container .ckontent .box .social
{
display: none !important;
}

div iframe
{
    display: none !important;
}

.ckontent iframe
{
    display: block !important;
}

img.playButton[style]
{
    left: 170px !important;
}

}
