Benget Immanuel Nata
http://bentinata.wordpress.com/
*/

@namespace url(http://www.w3.org/1999/xhtml);


@-moz-document domain("facebook.com") {

/* Global Changes */
body{
background-color: #008000 !important;
}

a, strong{
color: #004000 !important;
}

button, .uiButton{
background-image: url("http://i.imgur.com/ewsBn6I.png") !important;
border: none !important;
}

/* Navigation Bar */
#blueBar{
background-color: #60c040 !important;
border-bottom: 2px solid #008000 !important;
box-shadow: none !important;
}

.jewelButton{
background-image: url("http://i.imgur.com/o3NztGQ.png") !important;
}

#pageLogo a:hover, .jewelButton:hover, .jewelButton:active, .navLink:hover, .navLink:active{
border-radius: 5px 5px 0px 0px !important;
background-color: #008000 !important;
}

#pageLogo a:focus, .jewelButton:focus, .navLink:focus{
background-color: #60c040 !important;
}

.uiTypeahead, .uiSearchInput{ /* Search Box */
border: none !important;
}

.headerTinymanPhoto{ /* Small Pic */
border: none !important;
border-radius: 2px !important;
}

.navLink:after{
background-color: #008000 !important;
}

.navSubmenu:hover{
background-color: #60c040 !important;
border-style: solid none !important;
border-color: #60c040 !important;
}

/* Left Collumn */
#leftCol{
background-color: #f0fff0 !important;
border: none !important;
}

.fbxWelcomeBoxBlock{
position: relative;
left: -2px;
border-color: #008000 !important;
border-style: solid solid solid none !important;
border-width: 1px 4px 1px 0 !important;
}

.sortableItem{
border: none !important;
}

.uiSideNav .selectedItem .item, .uiSideNav .selectedItem .item:hover{
background-color: #60c040 !important;
border: none !important;
}

.uiSideNav .item:hover{
background-color: #c0ffa0 !important;
border: none !important;
}

/* Middle Collumn */
#contentCol{
background-color: #008000 !important;
border: none !important;
}

#pagelet_composer{
background-color: #60c040 !important;
border-radius: 0 10px 0 0 !important;
}

.openToggler .uiSelectorButton, .openToggler .uiSelectorButton:active, .openToggler .uiSelectorButton:focus, .openToggler .uiSelectorButton:hover{
background-color: #c0ffa0 !important;
}

.uiStreamStory{
background-color: #c0ffa0 !important;
border: none !important;
}

.storyContent .commentable_item{
display: none !important;
}

.uiStreamStory:hover{
background-color: #f0fff0 !important;
}

.uiStreamStory:hover .commentable_item{
display: inline !important;
}

.selectedStoryIndicator{
background-color: #008000 !important;
}

.UIActionLinks_bottom button.as_link, .UIActionLinks_bottom .uiLinkButton input, .UIActionLinks_bottom .uiLinkButton input:hover{
color: #008000 !important;
}

.UFIBlingBoxSprite{
background-image: url("http://i.imgur.com/RtzkFT3.png") !important;
}

/* Right Collumn */
#rightCol{
background-color: #e0ffd0 !important;
}

.fbFeedTicker .actorName, .fbFeedTickerStory .passiveName{
display: none !important;
}

.tickerStoryClicked, .tickerStoryClicked:hover, .tickerStorySelected, .tickerStorySelected:hover{
background-color: #e0ffd0 !important;
border: none !important;
}

.fbFlyoutDialog .tickerDialogBody{
box-shadow: none !important;
}

.fbRemindersStory:hover{
background-color: #e0ffd0 !important;
}


/* Chat */
.fbNub.highlightTab .fbNubButton{ /* new message allert */
background-color: #008000 !important;
}

.fbNubFlyout{
box-shadow: none !important;
}

.fbNubFlyoutTitlebar{
background-color: #60c040 !important;
border-radius: 5px 5px 0px 0px !important;
border-color: #008000 !important;
border-width: 1px 8px 0px 1px !important;
}

.uiCloseButton:active, .uiCloseButton:focus, .uiCloseButton:hover, .openToggler .uiCloseButton, .openToggler .uiCloseButton:active, .openToggler .uiCloseButton:focus, .openToggler .uiCloseButton:hover{
background-color: #008000 !important;
}

.uiMenuItem a:hover, .uiMenuItem a:active, .uiMenuItem a:focus, .uiMenuItem .itemAnchor:hover, .uiMenuItem .itemAnchor:active, .uiMenuItem .itemAnchor:focus{
background-color: #e0ffd0 !important;
border-color: #e0ffd0 !important;
border-style: solid none solid !important;
}

.fbChatOrderedList a:hover{
background-color: #e0ffd0 !important;
}

/* Timeline */

.fbTimelineScrubber .selected a, .fbTimelineScrubber .selected a:hover{
border-left: 4px solid #008000 !important;
}

.fbTimelineScrubber a:hover{
border-left: 4px solid #c0ffa0 !important;
}

/* Ads */
#pagelet_side_ads{
background-color: #e0ffd0 !important;
padding-left: 10px !important;
}


}
