css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("demo.piwik.org"), domain("piwik.vis4.net")
 {
.page, .pageWrap { background: #fff!important; }

#dashboardWidgetsArea {
   margin: -10px!important;
}

.nav_sep { border-color: #fff!important; }

.widget {
   border-color: #fff!important;
   padding: 10px!important;
   background: transparent!important;
}

.ui-sortable-helper {
   box-shadow: 0 0 20px rgba(30,0,0,.45)!important;
   border-radius: 5px!important;
}

.widgetTop {
   background: #fff!important;
   margin-bottom: 10px!important;
}

.widgetName {
   color: #7E7363!important;
   text-shadow: none!important;
   font-family: Open Sans!important;
   font-weight: 300!important;
   font-size: 24px!important;
}

.dataTableFooterIcons {

}

table.dataTable th, 
table.dataTable th.columnSorted,
table.dataTable td.labeleven,
table.dataTable td.columneven {
   background: #fff!important;
   border-color: #fff!important;
}

.dataTable th + th {
   text-align: right!important;
} 

.dataTable td + td {
   text-align: right!important;
} 
.dataTable th.first {
   color: #fff!important;
}

table.dataTable td, table.dataTable td.columnodd {
   border-color: #fff!important;
   background: #fff!important;
}

html body .dataTableAllColumnsWrapper tr td {
   border-bottom: 1px solid #e2e0e0!important;
}


.dataTable .dataTableFooterIcons,
.dataTable .datatableRelatedReports,
.dataTable .dataTableSearchPattern
 {
  display: none!important;
}

.dataTable:hover .dataTableFooterIcons,
.dataTable:hover .datatableRelatedReports,
.dataTable:hover .dataTableSearchPattern {
  display: block!important;
}

}
