css:
@-moz-document domain("speedrunslive.com") {
.container {
width: auto;
padding: 0;
margin-top: 116px;
}
header {
position: fixed;
width: 100%;
top: 0;
left: 0;
background-size: cover;
}
header .container {
margin-top: 0;
}
#logo {
max-width:100%;
background-size:contain;
}
#headerright {
position: absolute;
right: 0;
}
iframe {
width: 100%;
}
#streamList {
clear: both;
}
span.closechat {
width: 20px !important;
height: 14px !important;
color: rgba(0,0,0,0.5) !important;
box-shadow: 0;
border-width: 0;
text-shadow: 0 !important;
background-color: grey;
background-image: none !important;
background-image: url('http://cdn4.iconfinder.com/data/icons/brightmix/128/monotone_arrow_right.png') !important;
background-size: contain;
text-indent: 999px;
overflow: hidden
}
h1#featuredtitle {
padding-left: 1em;
padding-right: 1em;
}
span.description p {
max-width: 300px;
}
.twitchstreamer {
margin-top: 0;
width: 33%;
height: 100px;
background-size: cover;
}
.twitchstreamer:nth-child(2n - 1) {
margin-right: 0;
}
#featured .featuredstreamcontainer {
width: 67%;
//width: 100%;
margin: 0;
}
#featured .featuredstreamcontainer .biggerFeaturedStream {
//width: 67%;
width: 100%;
margin: 0;
}
#featured .featuredstream object {
height: 600px;
}
#featured .biggerFeaturedStream object, .biggerFeaturedStream{
width: 100% !important;
}
nav ul {
clear: both;
}
nav ul li {
display: inline;
margin: 0;
}
nav ul li a {
margin: 0;
color: #F7E279;
background: none;
background-image: none;
background-color: none;
-webkit-border-radius: 0 !important;
border-radius: 0 !important;
border-width: 0px 1px 0px 0px !important;
}
nav ul li a:hover, nav ul li a:active, nav ul li a:focus, {
margin: 0;
background: none;
background-image: none;
background-color: grey;
-webkit-border-radius: 0 !important;
border-radius: 0 !important;
border-width: 0px 1px 0px 0px !important;
}
#featured .featuredchat {
width: 33%;
//min-width: 300px;
margin: 0 !important;
}
#featured .featuredchat iframe {
height: 650px;
}

@media screen and (max-width: 800px) {
.twitchstreamer {
width: 49.5%;
}
}
}

@-moz-document domain("twitch.tv"), domain("twitchtv.com") {
#chat_lines {
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;
    color:#ccc!important;
}
#twitch_chat{
    background: none repeat scroll 0% 0% rgb(0, 0, 0)!important;
    color:#ccc!important;
}
body {
background: #000;
padding-left:5px;
color: #ccc !important;
}

#small_notice {
background: black;
font-size: 10px;
border-bottom: 1px solid #fff;
}

#right_col .content #chat_line_list { color: #ccc !important; }
}
