css:
@-moz-document domain('boards.4chan.org'){
    @font-face { 
    	font-family: 'Conv_gentium';
    	src: url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/gentium.eot');
    	src: local('?'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/gentium.woff') format('woff'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/gentium.ttf') format('truetype'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/gentium.svg') format('svg');
    	font-weight: normal;
    	font-style: normal;
    }
    @font-face {
    	font-family: 'Conv_playtime';
    	src: url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/fonts/playtime.eot');
    	src: local('?'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime.woff') format('woff'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime.ttf') format('truetype'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime.svg') format('svg');
    	font-weight: normal;
    	font-style: normal;
    }
    @font-face {
    	font-family: 'Conv_playtime';
    	src: url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime_bold.eot');
    	src: local('?'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime_bold.woff') format('woff'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime_bold.ttf') format('truetype'), url('https://dl.dropbox.com/u/33009650/KS-chan/fonts/playtime_bold.svg') format('svg');
    	font-weight: bold;
    	font-style: normal;
    }
    body {
    	font-family:'Conv_playtime' !important;
    	font-size: 11pt !important;
    	background: url('https://dl.dropbox.com/u/33009650/KS-chan/images/bg.png') no-repeat fixed right bottom #EEF2FF !important;
        -webkit-background-size: 100%; /*for webKit*/
        -moz-background-size: 100%; /*Mozilla*/
        -o-background-size: 100%; /*opera*/
        background-size: 100%; /*generic*/
    	
    }
    .postInfo , .file , blockquote , #qr , #updater , #watcher {
    	color:#FFFFFF;
        border:5px solid transparent !important;
        background-color:transparent !important;
        background:url('https://dl.dropbox.com/u/33009650/KS-chan/images/semitransparent.png') !important;
        background-size:100% 100% !important;
        background-repeat:no-repeat !important;
        -moz-border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/border.png') 5 5 stretch !important; /* Old Firefox */
        -webkit-border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/border.png') 5 5 stretch !important; /* Safari */
        -o-border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/border.png') 5 5 stretch !important; /* Opera */
        border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/border.png') 5 5 stretch !important;
    }
    .file {
        margin:0px 0px 0px 0px !important;
        position:relative !important;
        top:4px !important;
    }
    blockquote {
        margin:3px 0px 0px 20px !important;
        padding:6px 8px 4px 8px !important;
    }
    div.post , div.postContainer {
        overflow:visible !important;
    }
    div.post.reply {
        border:none !important;
        background-color:transparent !important;
    }
    div.inline {
        border:none !important;
        background-color:transparent !important;
    }
    div#qp {
        border:none !important;
        background-color:transparent !important;
    }
    div.post.reply a , div.op a , #watcher a {
        color:rgb(249,215,147) !important;
    }
    .fileText , .dateTime {
        color:rgb(188,180,160) !important;
    }
    span.subject {
        color:rgb(207,184,48) !important;
    }
    span.name , span.postertrip {
        color:rgb(98,146,118) !important;
    }
    a.fileThumb img {
        border:15px solid transparent !important;
        background-color:transparent !important;
        background:url('https://dl.dropbox.com/u/33009650/KS-chan/images/imageborder.png') !important;
        background-size:100% 100% !important;
        background-repeat:no-repeat !important;
        -moz-border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/imageborder.png') 40 40 stretch !important; /* Old Firefox */
        -webkit-border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/imageborder.png') 40 40 stretch !important; /* Safari */
        -o-border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/imageborder.png') 40 40 stretch !important; /* Opera */
        border-image:url('https://dl.dropbox.com/u/33009650/KS-chan/images/imageborder.png') 40 40 stretch !important;
    }
}
