css:
/* main page and body */

body {
   background-color: #111 !important;
}
h1,h2,h3,h4,h5 {
   color: #ccc !important;
}
input, textarea {
   background-color: #1a1a1a !important;
   color: #ccc !important;
}
input[type="text"], input[type="password"] {
   background-color: #222 !important;
   color: #ccc !important;
   border-color: #494949 !important;
}
.gf-documents h2 {
   background: none !important;
   border-top: 2px solid #494949 !important;
}
.gf-documents .gf-documents-chapter {
   background: none !important;
   border-top: 1px solid #494949 !important;
}

/* header */

.gf-site-header {
   background: #1a1a1a !important;
}
.gf-notification-icon b, .gf-page-notifications-list time b {
   background-color: #444 !important;
}
.gf-notification-icon b:hover, .gf-page-notifications-list:hover time b:hover {
   background-color: #666 !important;
}
#bugmuncher-button {
   background: #333 !important;
   color: #ccc !important;
   border: none !important;
   box-shadow: #080808 0px 0px 1px 2px !important;
}

/* footer */

.gf-footer {
   background: #1a1a1a !important;
}
.gf-footer .gf-footer-inner-wrapper::before, .gf-footer::before {
   background: none !important;
}

/* comments & news */

.gf-flow-list-content .gf-flow {
   background-color: #222 !important;
   border: none !important;
}
.gf-seed-media-selector {
   background-color: #222 !important;
   border-color: #494949 !important;
}
.gf-layout-intra .gf-page-game-profile .gf-page-content .gf-page-body {
   border-color: #494949 !important;
}
.gf-layout-intra .gf-page-game-profile .gf-page-content aside {
   background: none !important;
}
.gf-age-restriction-icons {
   background: none !important;
   border-top: 1px solid #494949 !important;
   border-bottom: 1px solid #494949 !important;
}
.gf-flow ul.gf-blip-list > li > section.gf-blip::before {
   background: none !important;
   border-top: 1px solid #494949 !important;
}
.gf-flow-list > ul > li::before {
   background: #111 !important;
}
.gf-attached-media.gf-media-internal-image figure {
   background: #111 !important;
}
.gf-attached-media.gf-media-internal-image figure:hover {
   background: #333 !important;
}
.gf-flow ul.gf-blip-list > li > section.gf-blip .gf-blip-body, .gf-attached-media .gf-source-domain {
   color: #aaa !important;
}

/* documents */

.gf-documents p, .gf-documents .gf-documents-chapter li {
   color: #ccc !important;
}

/* profile */

.gf-reflection .gf-reflection-flip::before {
   background: -moz-linear-gradient(rgba(17,17,17,1) 60%,rgba(17,17,17,.8) 100%) !important;
   background: -webkit-linear-gradient(rgba(17,17,17,1) 60%,rgba(17,17,17,.8) 100%) !important;
}
.gf-site-header.gf-pinned-userpic + .gf-page-content .gf-aside-icon .gf-reflection-flip::before, .gf-site-header.gf-pinned-userpic + .gf-page-content .gf-profile-bar .gf-useravatar-link .gf-reflection.gf-reflection-added .gf-reflection-flip::before {
   background: -moz-linear-gradient(rgba(26,26,26,1) 60%,rgba(26,26,26,.8) 100%) !important;
   background: -webkit-linear-gradient(rgba(26,26,26,1) 60%,rgba(26,26,26,.8) 100%) !important;
}
.gf-layout-intra .gf-page-flow-user-my .gf-page-content .gf-page-body {
   border-color: #494949 !important;
}
.gf-layout-intra .gf-page-flow-user-my .gf-page-content aside {
   background: none !important;
}
.gf-seed-editor .gf-editing-area textarea {
   border-color: #494949 !important;
}
.gf-profile-puller b:last-child::before, .gf-profile-puller b:last-child::after {
   background: transparent !important;
   -moz-transform: rotate(15deg) !important;
   -webkit-transform: rotate(15deg) !important;
}
.gf-profile-puller b:last-child::after {
   -moz-transform: rotate(-15deg) !important;
   -webkit-transform: rotate(-15deg) !important;
}
.gf-wrapper.gf-profile.gf-profile-view .gf-table h5 .gf-user-verified-warface {
   color: #ccc !important;
}

/* notification */

.gf-page-notifications-list h4 {
   background: none !important;
   border-top: 1px solid #494949 !important;
   padding-top: 20px !important;
   padding-bottom: 20px !important;
}
.gf-page-notifications-list b {
   color: #aaa !important;
}
.gf-page-notifications-list li {
   background: none !important;
   border-bottom: 1px solid #494949 !important;
}
.gf-page-notifications-list li:last-child {
   background: none !important;
   border-bottom: none !important;
}

/* dashboard */

.gf-dashboard > ul > li > div {
   background: #222 !important;
}
.gf-dashboard > ul > li > div .gf-reflection .gf-reflection-flip::before {
   background: -moz-linear-gradient(rgba(34,34,34,1) 60%,rgba(34,34,34,.8) 100%) !important;
   background: -webkit-linear-gradient(rgba(34,34,34,1) 60%,rgba(34,34,34,.8) 100%) !important;
}
.gf-dashboard-slot-my-profile .gf-userpicture figcaption {
   color: #ccc !important;
}
.gf-social-circle.gf-reflection {
   overflow: hidden !important;
}

/* games */

.gf-gamercards-list figcaption {
   color: #ccc !important;
}

/* intro card */

.gf-intro-card {
   background-color: #222 !important;
   border: none !important;
}

/* popup */

.gf-popup-scrollable-body {
   background-color: #222 !important;
   border: none !important;
}
.gf-popup-scrollable-body .gf-seed-editor .gf-seed-editor-bottom-bar {
   border-color: #494949 !important;
}
.gf-popup-scrollable-body .gf-seed-editor .gf-editing-area textarea {
   background-color: #1a1a1a !important;
   color: #ccc !important;
   border-top: 1px solid #494949 !important;
   border-bottom: 1px solid #494949 !important;
}

/* public cloud */

div.gf-site-header .gf-page-headline {
   color: #333 !important;
}
.gf-icon.gf-icon-reflection.gf-icon-reflection-ffffff::before {
   background: -moz-linear-gradient(90deg, rgba(17,17,17,1) 60%,rgba(17,17,17,.8) 100%) !important;
   background: -webkit-linear-gradient(90deg, rgba(17,17,17,1) 60%,rgba(17,17,17,.8) 100%) !important;
}
.gf-username.gf-user-verified-warface {
   color: #fff !important;
}
.gf-layout-intra .gf-page-content .gf-page-body {
   border-color: #494949 !important;
}
aside {
   background: none !important;
}
