css:
@-moz-document domain('twitter.com'), url-prefix('https://twitter.com'), url-prefix('http://twitter.com') {
#page-outer {
direction: rtl!important;
}

.account-summary .avatar {
right: 0!important;
}
.stream-item-header .avatar, .permalink-header .avatar {
float: right!important;

margin-right: -58px!important;
margin-left: 0px!important;
}

.tweet-form .tweet-button .tweet-counter {
margin-right: 5px!important;
font-family: "B Yekan"!important;
font-size: 13px!important;
}

.account-summary-small .content {
margin-right: 42px!important;
}

.account-summary .content {
margin-rigth: 58px!important;
margin-left: 20px!important;
}

.profile-card.profile-header {
float: left!important;
}

.module .chev-right {
left: 12px!important;
}

#suggested-users {
