}

.account-summary .content {
margin-right: 58px!important;
margin-left: 20px!important;
}

.js-tweet-text {
font-family: tahoma !important;
line-height: 22px!important;
}

.dismiss {
float: left!important;
}

input[type="text"], textarea, .tweet p, .fullname, .js-tweet-text {font-family:tahoma !important;}

body.ms-windows, body.ms-windows label, body.ms-windows input, body.ms-windows textarea, body.ms-windows select, body.ms-windows button
{font-family:tahoma !important;
font-size:12px!important;}

label, input, textarea, div[contenteditable="true"], select
{font-family:tahoma !important;
font-size:12px;}

.stats a strong {
