	/* SPAN OF CATEGORIES CURRENT LOCATION */
#yan-breadcrumbs {background:#000000;}
#yan-breadcrumbs li {color:#77aaff !important;}
#yan-breadcrumbs li a{color:#aa77ee !important;}
#yan-wrap   /* THE PAGE */
        {background:#000000;
        width:950px !important;
        height:1200px;}
#member-container /* MY PROFILE CONTAINER */
        {background:#001111 !important;
        border:2px solid #113322 !important;
	width:800px;}
#member-card
	{border:0px !important;}
#member-card #member-wrapper  /* MY PROFILE CONTAINER OUTLINE */
        {border:0px solid #113322 !important;
        width:500px !important;}
#member-card #member-share-settings
        {background:#000000 !important;
        border:0px !important;}
#member-card .member-current-level  /* LEVEL */
        {background:#193923 !important;
        text-align:center;
        font-size:18px !important;
        color:#8866bb !important;
        text-shadow:2px 3px 1px #000000;
        border-top:2px solid #779988 !important;
        border-left:3px solid #446655 !important;
        border-right:3px solid #334444 !important;
        border-bottom:2px solid #000000 !important;}
#member-card #member-share-settings
        {background:#001111 !important;}
#member-card .member-level-info td  /* HOME PAGE - NUMERALS OF POINTS */
        {background:#001111 !important;
        font-size:17px !important;
        border:0px !important;
        color:#11aa88;}
#member-card .member-level-info th
        {background:#001111 !important;
        border:0px !important;
        color:#11aa66;}
#member-card #member-percent    /* BEST ANSWER PERCENT BOX */
        {background:#553388 !important;
        border:2px solid #8866cc !important;
        width:96px;}
#member-card .picture		 /* PROFILE AVATAR */
        {border:0px !important;}
#member-card h1			 /* PROFILE USERNAME TEXT */
        {color:#000000 !important;
	font-size:30px !important;
        text-shadow:1px 1px 5px #ffffff;}
#yan-user-profile-card.yui-module li.total	 /* POPUP PROFILE CARD */
        {padding-top:20px !important;
	background:#001122 !important;}
#yan-user-profile-card,		/* POPUP PROFILE SUMMARY */
#yan-user-profile-card .ft,
#yan-user-profile-card.yui-module .ft ul a,
#yan-user-profile-card.yui-module li.best,
#yan-user-profile-card.yui-module ul.best,
#yan-user-profile-card.yui-module li.total
        {background:#182328 !important;
	border:0px !important;}
#yan-user-profile-card.yui-module
        {border:2px solid #55aa88 !important;
	padding:8px !important;}
        
.profile .photo,	/* AVATARS IN LISTS */
#yan-mystatus .photo    /* STATUS BOX AVATAR */
        {border:0px !important;
        padding:0px !important;}
.user a {font-weight:bold;
	color:#aa77ee !important;}
#yan-answers .shown li  /* ANSWERS TEXT */
        {padding:0px !important;}
#yan-answers .answer
        {padding:8px 0px 0px 0px !important;
        border-bottom:2px solid #225544;}
#yan-answers .hd h3 strong
        {color:#aa55cc !important;
        padding-left:50px !important;}
.yan-sort {border:none !important;}
.yan-sort .active .selected {border:none !important;}
p.cta, div.cta  /* MORE ANSWERS BUTTON */
        {text-align:center !important;}
.cta span {background:#114433 !important;}
#yan-activity		/* ACTIVITY HISTORY LIST */
        {color:#99aaff;
        background:#001111;
        border:2px solid #113322;
        line-height:1.0 !important;
        width:800px;
	text-align:center;}
#activity-detail
	{margin:0px !important;
	width:800px;}
#activity-detail li
	{list-style-type:none !important;}
.questions li		/* QUESTIONS TEXT IN THE LIST */
        {font-size:14px;
        background:#000000 !important;
        padding:0px !important;}
.questions li a		/* QUESTIONS TEXT STYLE */
        {color:#99CC99 !important;
        font-weight:bold;}
.questions,
.questions h3,		/* QUESTION TITLE WIDTH */
.questions li div
        {width:850px;}
.questions li div,
#yan-question,		/* THE QUESTION BOX */
#yan-answers li
        {background:#000000 !important;
        padding:0px !important;}
.qa-container .meta	/* POST TIMESTAMPS */
        {color:#ee6699 !important;
        padding:0px 0px 0px 40px !important;
        font-weight:bold;}
.answer-util li a	/* THUMBS RATING BOXES */
        {background:#cceedd !important;
        font-size:14px;
        color:#66bbcc !important;}
.rate-up .rate-button
	{background:#112222 !important;}
.rate-down .rate-button
	{background:#112222 !important;}
.utils-container ul li a
       {background:#112222 !important;
        border:2px solid #336644;}
.utils-container ul
	{border:0px !important;}
#M8YrMnPEMXkK3XLw96e2 #answer-tools-G25XqHPyaa ul li
		/* MY ANSWERS THUMBS ONLY. Unique selector required for individual accounts. */
        {background:#113322 !important;
        font-size:14px !important;
        color:#88ddff;
        border:2px solid #336644;}
#yan-main
	{border:0px !important; color:#bbbbff;}
#yan-main a
	{color:#bbffee !important;}
.qa-container .content,
.qa-container div.reference
        {color:#aaddcc;}
h1, h2, h3, h4
        {color:#8888ff !important;}
#yan-content .pagination a       /* OTHER PAGE NUMBERS */
        {border:2px solid #339966 !important;
        color:#55ccff !important;}
#yan-content .pagination span	/* CURRENT PAGE NUMBER */
        {background:#000000 !important;
        border:2px solid #339966;
	color:#eeaadd !important;} 
#yan-categories			/* CATEGORIES CONTAINER */
        {background:#000000 !important;
        border:0px !important;}
#yan-categories li a		/* CATEGORIES TEXT */
        {color:#66dd99 !important;
        font-weight:bold !important;}
#yan-categories li span		/* SELECTED CATEGORY - PAGE TOP */
        {background:#223326 !important;
        color:#bb99ee !important;
        border:0px !important;}
#yan-categories .current a	/* CURRENT CATEGORY HIGHLIGHTED IN LIST */
        {background:#770000 !important;
        border:2px solid #bb1111 !important;}
#yan-categories .current        /* HIGHLIGHTED CATEGORY TEXT */
        {color:#aaccdd !important;}
.questions .meta a		/* CATEGORY AND NAME IN QUESTIONS FEED */
        {color:#882266 !important;
        font-size:11px;
        font-weight:normal;}
#yan-mystatus,
#yan-related,
#yan-myincentives		/* AVATAR BADGE TOP RIGHT */
        {background:#000000 !important;
        border:0px !important;
        color:#dd7755;}

            /********** BLACK BACKGROUND STUFF **********/
body,
#yan,       /* INNER BODY */
.other-questions-inner,
.ask-flow,
#yan-answers .answer.low-rating		/* HIDDEN ANSWERS RATED LOW */
        {background:#000000 !important;}

	/******************** USELESS STUFF I'VE HIDDEN ***********/
#yuhead-hd,	/* PAGE HEADERS */
#yuhead-bd,	/* PAGE HEADER */
#yan-nav,	/* PAGE HEADER */
#yan-banner,    /* PAGE HEADER */
#yan-header-searchbox,  /* PAGE HEADER SEARCH FORM */
#yan-what-we-like,	/* WHAT WE LIKE BOX */
.yan-profile-data-wrapper,
#yan-question-tools,
#askedInCountry,
#yan-leaderboard,
#yan-earn-points,
#yan-extend-question,
#yan-persistent-links,
#yan-choose-best-answer,
#yan-report-quiz,
#yan-sponsored,
#yan-qt .hd,
#yan-qt .bd,
#yan-qt,
.updates-disclosure,
#member-card #member-about-text,/* ABOUT ME PROFILE LINK */
.tabbed-content,		/* ACTIVITY HISTORY LIST HEADING */
#yan-answer-search-inline,
#yan-related .marketing-ad,	/* ADVERTISEMENT ON RIGHT COLUMN */
#yan-kp,			/* KNOWLEDGE PARTNER BOX */
#yan-connections,		/* ANSWERS CONNECTIONS BOX ON BOTTOM OF PROFILE */
.report-abuse,			/* REPORT ANSWERS LINK */
.star,
#yan-confirm-cta .ft,		/* 3 RECOMMENDED QUESTIONS BOX PAGE TOP */
#yan-confirm-cta .bd,		/* THANK YOU FOR ANSWER BOX */
.carousel,
#ft /* PAGE FOOTER */
        {display:none !important;}
