css:
/* 
 * Name		: 	UserCSSLoader_Menu Icons.css
 * Author	:	iwo
 * Created	:	Jan 18, 2013 more iconic menu items
 * note		:	warranty void with improper usage LOL!   
 * Update_url		: 
 * Description	: 给iwo汉化的Griever的UserCSSLoader样式管理器脚本添加菜单项图标
 * Usage		:	导入到你的Profile下Chrome子目录或者粘贴至该目录的userChrome.css文件中。
 */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

menuitem[label="打开CSS文件夹"][oncommand="UCL.openFolder();"] {
   list-style-image: url("chrome://browser/skin/downloads/buttons.png")!important;
   -moz-image-region: rect(16px 48px 32px 32px) !important;/*右上(y,x)左下(y,x)/**/
}
menuitem[label="打开CSS文件夹"][oncommand="UCL.openFolder();"] .menu-iconic-icon {
   height: 18px!important;
   width: 18px!important;
   image-rendering: auto !important;
}

menuitem[label="重新加载选中的css样式"][oncommand="UCL.rebuild();"] {
   list-style-image: url("chrome://browser/skin/downloads/buttons.png")!important;
   -moz-image-region: rect(32px 48px 48px 32px) !important;
}
menuitem[label="重新加载选中的css样式"][oncommand="UCL.rebuild();"] .menu-iconic-icon {
   height: 18px!important;
   width: 18px!important;
   image-rendering: auto !important;
}

menuitem[label="在userstyles.org检索当前网页样式"][oncommand="UCL.searchStyle();"] {
   list-style-image: url("chrome://global/skin/icons/Search-glass.png")!important;
   -moz-image-region: rect(0px 32px 16px 16px) !important;
}

menu[label="新建与编辑css样式..."][id="usercssloader-write-edit-css"]/**/ {
   list-style-image: url("chrome://browser/skin/places/libraryToolbar.png")!important;
   -moz-image-region: rect(16px 16px 32px 0px) !important;
}

menu[label="新建css样式..."][id="usercssloader-write-css"]/**/,menuitem[label="新建用户css样式(外部编辑器)"][oncommand="UCL.create();"]{
   list-style-image: url("chrome://browser/skin/places/libraryToolbar.png")!important;
   -moz-image-region: rect(16px 16px 32px 0px) !important;
}

menu[label="编辑css样式..."][id="usercssloader-edit-css"]/**/,menuitem[label="编辑userChrome.css文件"][oncommand="UCL.editUserCSS('userChrome.css')"]{
   list-style-image: url("chrome://browser/skin/tabview/edit-light.png")!important;
}
/* TheEnd */
