css:
@-moz-document domain("facebook.com"), domain("www.facebook.com") {
body{
background: url("http://farm9.staticflickr.com/8311/8031134128_695e90de68.jpg") #fff top center fixed !important;
}

a,
h1,
h2,
h3,
h4,
h5
{color: #b924ff !important;}

.fbNubButton {
background-color:#9000ff !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
}

}
#blueBar{
height:38px !important;
background: #DDA0DD repeat-x !important;
background-size: 100% 51px !important;
  -webkit-box-shadow: 0px 0px 10px rgba(0,0,0,0.4) !important;
  -box-shadow: 0px 0px 10px rgba(0,0,0,0.4) !important;
opacity: 1;
}

.jewelButton:hover, .topNavLink a:hover {
background-color: #D8BFD8 !important;
}

.navSubmenu a:hover {
color: #660066 !important;
}

.navigation a, .blueName {
color: #660066 !important;
}

.fbNubFlyoutTitlebar {
background-color:#e293ff !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
}

#BeeperBox { bottom:50px !important }

#pageNav a:hover, #pageNav a:focus, #pageNav a:active  { background-color:#D8BFD8 !important; color:#ffffff !important;
}

#pageLogo a    { background:url(http://4.bp.blogspot.com/-cjB2GNQUk8Y/UPbztDtFbxI/AAAAAAAAAKA/RdGjkR_Rcdo/s1600/icons.png) no-repeat scroll -21px 0 !important; }


#fb_menubar
{
background:#c26cfb !important;
}

.navHeader,
.navHeader a { color:#881ad2 !important }

.item { border-radius:5px 0 0 5px; }

.uiSideNavCount {
background: -moz-linear-gradient(top,  rgba(241,231,103,1) 0%, rgba(254,182,69,1) 100%) !important;
border:1px solid rgba(254,182,69,1);
color:#fff !important;
text-shadow:1px 1px 0 rgba(0,0,0,0.5) }


.ticker_stream .fbFeedTickerStory:nth-child(2n+2),
.navHeader {
background-color:#eef !important;
border-radius:5px 0 0 5px;
margin:0 !important;
padding:5px
}

.profile-friends { margin:7px 0 15px !important }
.profile-friends li {
background-color:#eef !important;
border-radius:5px 0 0 5px;
padding:3px;
}



.attachmentSelectForm { background-color:#eee !important }
.uiButton { border-radius:3px !important }


.profile-friends li:nth-child(2n+2) { background-color:#ededef !important }

.uiPhotoThumb { border-radius:5px !important }
img { border-radius:3px }

.relationshipSection { padding:0 !important }
.uiHeader {
background-color:#c26cfb !important;
border-radius:5px 0 0 5px;
padding:3px 5px !important }

.uiHeaderTitle span,
.uiHeaderTitle,
.uiHeader a { color:#fff !important }

.uiHeader .fcg { background-color:#eef !important; }


.uiUfi .ufiItem { border-radius:5px !important }

.fbChatOrderedList {
background-color:#e293ff !important;
box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) !important;
color: #FFFFFF !important;
border: #333333 !important;
}
