css:
@-moz-document domain("anond.hatelabo.jp") {
@font-face {
  font-family: 'YakuHanJP';
  font-style: normal;
  font-weight: 300;
  src: url(https://cdn.rawgit.com/qrac/yakuhanjp/master/dist/fonts/YakuHanJP/YakuHanJP-DemiLight.woff2) format('woff2'),
  url(https://cdn.rawgit.com/qrac/yakuhanjp/master/dist/fonts/YakuHanJP/YakuHanJP-DemiLight.woff) format('woff'),
  url(https://cdn.rawgit.com/qrac/yakuhanjp/master/dist/fonts/YakuHanJP/YakuHanJP-DemiLight.eot);
}

#headtable,
#breadcrumbs, .navigation, #afc, *[class*=afc], form[action*="google"],
#rectangle-middle, .double-rectangle
{ display: none !important; }

#hatena-anond { width: 960px !important; margin: 2em auto !important; }
div.body>div.section { width: 900px; margin: auto; }

h2 { background-color: #568BCA !important; color: #fff; }
h2>a,h3>a { text-decoration: none !important; }
a.keyword { border: none !important; color: #333333 !important; }
a { color: #2C6EBD !important; }
a:visited { color: purple !important; }
div.section h3 span.sanchor { color: #568BCA !important; }
div.section h3 { margin: 1.5em 0 !important; }
div.body blockquote { border: 1px solid #ABC5E5 !important; }
#globalheader div#bannersub table td { background: #568BCA; }
#globalheader div#bannersub table td a { color: #fff !important; }
.body .section p.share-button { margin-top: 5em !important; }

body, #body {
  background-color: #f8f8f8 !important; color: #333333 !important;
}
div.body {
  font-family: 'YakuHanJP', sans-serif;
  font-size: 16px;
  background-color: #fbfbfb !important; color: #333333 !important;
  border: 1px solid #ABC5E5 !important;
}
.box-curve {
  background-color: #fdfdfd !important;
  box-shadow: 1px 1px 2px rgba(28,28,28,0.1) !important;
}
div.refererlist ul {
  background-color: #f8f8f8 !important;
}
div.caption {
  border-bottom: 1px solid #ABC5E5 !important;
}
div.section h4 {
  border-bottom: 1px solid #ABC5E5 !important;
  border-left: 5px solid #ABC5E5 !important;
  margin: 3em 0 1em !important;
}
div.section h5 {
  margin: 1.5em 0 .5em
}
div.section p {
  margin-top: .5em;
  text-indent: 0;
}

br+br+br{background:red;display:none;}
}
