css:
@-moz-document domain("d.hatena.ne.jp") {
@font-face {
  font-family: 'YakuHanJP';
  font-style: normal;
  font-weight: 300;
  src: url(https://cdn.rawgit.com/qrac/yakuhanjp/master/dist/fonts/YakuHanJP/YakuHanJP-DemiLight.woff2) format('woff2'),
  url(https://cdn.rawgit.com/qrac/yakuhanjp/master/dist/fonts/YakuHanJP/YakuHanJP-DemiLight.woff) format('woff'),
  url(https://cdn.rawgit.com/qrac/yakuhanjp/master/dist/fonts/YakuHanJP/YakuHanJP-DemiLight.eot);
}

div.hatena-body>div.sidebar
,div.hatena-body>div.leftbar
,div.ad
,ins
,*[id*=afc]
{ display: none !important; }

* {
  box-sizing: border-box;
}

body {
  font-family: 'YakuHanJP', sans-serif;
  font-size: 16px;
  color: #333;
  background: #f5f5f5;
}

body > #simple-header,
body > div.adminmenu,
body > div.hatena-body,
body > h1 {
  width: 800px;
  margin: 0 auto;
  border: 0;
}

h1 {
  background: none;
  height: auto;
  color: #333;
  font-size: 18px;
  padding: 40px;
  text-align: center;
}

div.calendar,
table.calendar {
  display: none;
}

.hatena-body {
  width: 800px;
  padding: 40px;
  background: none;
  background-color: #fff;
  border: 0;
  box-shadow: 0 1px 2px #ddd;
}

div.header {
  border: 0;
}

div.total {
  border: 0;
}

div.main {
  width: auto;
  float: none;
  margin: 0 !important;
  padding: 0 !important;
  position: static;
  top: 0;
  left: 0;
  background: none;
}

.main div.calendar,
.main div.day,
.main div.day h2 {
  width: auto;
  margin: 0;
  padding: 0 20px;
  background: none;
  border: 0;
}

.main div.day h2 {
  padding-top: 2em;
}
.main div.day h2 .date {
  font-size: 1.5em;
}

#days {
  background-color: inherit;
}

div.day {
  border: 0;
}

div.body {
  font-size: inherit;
  border: 0 !important;
  background: none !important;
}

div.section {
  width: auto !important;
  margin: 0;
  background: none;
}

div.sidebar {
  position: static;
  top: 0;
  left: 0;
  width: auto;
}

div.hatena-module {
  border: 0;
  background: none;
}

.section > h3,
.section > h4 {
  margin: 3em 0 1em;
  padding: .5em;
  line-height: 1.5;
  text-indent: 0;
}

a.keyword {
  border-bottom: none !important;
}

.refererlist ul li {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.section * {
  color: #333 !important;
}

blockquote {
  padding: 5px 15px;
  font-size: 14px;
  background-color: #fafafa;
  border: 0;
  border-left: 4px solid #ddd;
}
.section blockquote * {
  color: #666 !important;
}

.body .section pre {
  width: auto;
  border: 0;
  font-size: 14px;
  letter-spacing: 0;
  background-color: #fafafa;
}
}
