css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://weibo.com/"){
.WB_global_nav .gn_header{width:830px!important;}
.WB_global_nav{background:#000!important;opacity:0.7}
.WB_global_nav .gn_bg{background:none!important;}
.gn_title {display:none}
.gn_title[node-type=home]{display:block}
.W_main {width:830px!important;background:#eee}
.W_main_c {background:#fff}
#Box_center {background:#fff}
#Box_right>div{display:none}
#Box_right #pl_rightmod_myinfo{display:block}
.W_main_bg {position:relative}
.W_main_bg .W_main_l {width: 230px!important;right: 0;top:0;float:none;position: absolute;}
.WB_left_nav>div {display:none}
#pl_leftnav_common,#pl_leftnav_group {display:block}
.WB_left_nav .lev a:hover, .WB_left_nav .lev2 a:hover, .WB_left_nav .lev2 a.lev_curr, .WB_left_nav .lev2 a.lev_curr:hover,.WB_left_nav .lev_curr {background-image:none!important}
.W_main_a {background:#eee}

.B_fans_myconn .W_main_r,
.B_favor_me .W_main_r {display:none}

.B_index .W_main_l,
.B_send_me .W_main_l,
.B_closefriend  .W_main_l{top: 165px;border-top: 1px solid #ccc;}

.global_footer {width:790px!important;}
.global_footer dl.list {width:142px}

.send_weibo .S_textb,.send_weibo .S_txt2>a,.gn_logo,#pl_content_biztips {display:none!important}
.send_weibo .S_txt2 {color:#fff}
.send_weibo .title {background:none}
.S_txt2 span {color:#999}
.S_txt2 .S_error {color:red}
.WB_global_nav .gn_nav {margin:0!important;}

.profile_tabbig{width:830px!important;}
.pul_list_big,.pul_list_fansbig{width:209px!important;height:auto!important;}
.pul_list_big .pul_item,.pul_list_fansbig .pul_item{margin:0 8px 20px 0!important;}
#pl_profile_cover{display:none;}
.W_profile_bg{margin-top:135px;}
.pf_info_right{display: inline;position: relative;top: -170px;}
#pl_profile_extraInfo .pf_verified {display: inline;position:relative;
top: 10px;}
#pl_profile_extraInfo .pf_star_info p{
width: 160px;
display: inline-block;
color: #966;
position: relative;
top: -14px;
text-align:left;
}
#pl_profile_extraInfo .pf_star_info p a{color:#966;}
#pl_profile_extraInfo .pf_star_info p a:hover{color:#c66;}

#pl_profile_extraInfo .pf_verified_info{display: inline;}
#pl_profile_extraInfo .pf_verified_info br{display:none;}
#plc_profile_header .profile_top .pf_info_right{float:none!important;width:100%!important;}

.S_profile .W_main_2r {width:230px!important;}
.S_profile .profile_right_module{margin:-1px 0!important;border:none!important}
.S_profile .prm_app_pic .piclist {width:230px}
.S_profile .prm_app_pic .piclist li {margin:10px 34px 0 0}

.global_footer dl.list {display: none!important;}
.speacc_user{visibility:hidden;display:none;}
#pl_profile_modulealbum{overflow:hidden;}
#pl_profile_modulealbum .prm_app_pic{width:230px;}
.global_footer .other_link{margin:0!important;}
.global_footer{padding-top:0!important;}
.gn_header .gn_person .gn_setting[node-type="member"]{display:none;}
#WB_webim .W_layer[node-type="tips"]{display:none;}
.pf_info_left_border{border-right-width:0px!important;}
.group_read[node-type="feed_nav"] .lock_r[node-type="activity_nav"],.W_tips[node-type="feed_list_shieldKeyword"]{display:none!important;}

div.WB_feed_type[data-mark]{border-bottom:1px solid #e6e6e6;}
div.WB_feed_type[data-mark] .WB_feed_datail,
div.WB_feed_type[data-mark] .WB_screen,
div.WB_feed_type[data-mark] fieldset,
.WB_feed .popular_buss {display:none!important;}

}
