css:
@-moz-document domain("codecademy.com") {
.container {
  width: 80% !important;
  margin: auto !important;
}

#section a {
  color: white !important;
-webkit-border-image: none;
-webkit-transition-delay: 0s;
-webkit-transition-duration: 0.30000001192092896s;
-webkit-transition-property: all;
-webkit-transition-timing-function: cubic-bezier(0.6, 0.1, 0.15, 0.8);
-moz-border-image: none;
-moz-transition-delay: 0s;
-moz-transition-duration: 0.30000001192092896s;
-moz-transition-property: all;
-moz-transition-timing-function: cubic-bezier(0.6, 0.1, 0.15, 0.8);
border-image: none;
transition-delay: 0s;
transition-duration: 0.30000001192092896s;
transition-property: all;
transition-timing-function: cubic-bezier(0.6, 0.1, 0.15, 0.8);

}


.active .name a, .name a:hover {
  background: #1e1e1e !important;
  border-radius: 10px !important;
  border-bottom: #F66E00 1px solid !important;
  text-shadow: 0 0 10px white !important;
}


#exercises_view{
  width: inherit !important;
}

#exercise_sidebar {
  width: 50% !important;
}

#exercise_sidebar .exercises {
  width: 100% !important;
  margin-right: 30px !important;
}

h2 {
  color: white !important;
}

label {
  background: transparent !important;
  border-radius: 5px !important;
  padding: 5px 5px !important;
  color: #fff !important;
  margin-right: 10px !important;
  z-index: 999 !important;
}

label.selected {
  background-color: #a88249 !important; 
}

.instruction p, .instruction li {
  color: white !important;
  font-family: 'Ubuntu'!important;
  font-size: 16px;
}

.learner, p, li, b {
  font-family: 'Noticia Text' !important;
  color: #CBBAAF!important;
}


/* Fixes the position of the console */
.affix, .affix-top {
  top: auto !important;
  bottom: auto !important;
  position: fixed !important;
}

a.button.gray.show_hint, button.gray:hover, .button.gray:hover, .resumator-button:hover, .resumator-apply-button:hover 
{
  background-image: -webkit-linear-gradient(#7d5d3b,#634b30) !important;
  background-image: -moz-linear-gradient(#7d5d3b,#634b30) !important;
  background-image: linear-gradient(#7d5d3b,#634b30) !important;
}
}

