@-moz-document domain("codecademy.com") {
body {
  background: url('http://img27.imageshack.us/img27/2097/backgroundin.png') repeat !important;
}

}

@-moz-document domain("codecademy.com") {
 .ace_gutter {
  background: #232323 !important;
  color: #E2E2E2 !important;
}

.ace-tm .ace_gutter_active_line {
  background: DarkSlateGray !important;
}

.ace-tm span.ace_keyword {
  color: yellow !important;
}

.ace-tm span.ace_identifier {
  color: LightSalmon !important;
}

 .ace_print-margin {
  width: 1px !important;
  background: #232323 !important;
}

 .ace_scroller, .ace-tm, pre {
  background-color: #141414 !important;
}

 .ace_text-layer {
  color: #F8F8F8 !important;
}

 .ace_cursor {
  border-left: 2px solid #A7A7A7 !important;
}

 .ace_overwrite-cursors .ace_cursor {
  border-left: 0px !important;
  border-bottom: 1px solid #A7A7A7 !important;
}

 .ace_marker-layer .ace_selection {
  background: rgba(221, 240, 255, 0.20) !important;
}

.ace_multiselect .ace_selection.ace_start {
  box-shadow: 0 0 3px 0px #141414 !important;
  border-radius: 2px !important;
}

 .ace_marker-layer .ace_step {
  background: rgb(102, 82, 0) !important;
}

 .ace_marker-layer .ace_bracket {
  margin: -1px 0 0 -1px !important;
  border: 1px solid rgba(255, 255, 255, 0.25) !important;
}

 .ace_marker-layer .ace_active-line {
  background: rgba(255, 255, 255, 0.031) !important;
}

 .ace_gutter-active-line {
  background-color: rgba(255, 255, 255, 0.031) !important;
}

 .ace_marker-layer .ace_selected-word {
  border: 1px solid rgba(221, 240, 255, 0.20) !important;
}

 .ace_invisible {
  color: rgba(255, 255, 255, 0.25) !important;
}

 .ace_keyword,
 .ace_meta {
  color: #CDA869 !important;
}

 .ace_constant,
 .ace_constant.ace_character,
 .ace_constant.ace_character.ace_escape,
 .ace_constant.ace_other,
 .ace_markup.ace_heading,
 .ace_support.ace_constant {
  color: #CF6A4C !important;
}

 .ace_invalid.ace_illegal {
  color: #F8F8F8 !important;
  background-color: rgba(86, 45, 86, 0.75) !important;
}

 .ace_invalid.ace_deprecated {
  text-decoration: underline !important;
  font-style: italic !important;
  color: #D2A8A1 !important;
}

 .ace_support {
  color: #9B859D !important;
}

 .ace_fold {
  background-color: #AC885B !important;
  border-color: #F8F8F8 !important;
}

 .ace_support.ace_function {
  color: #DAD085 !important;
}

 .ace_markup.ace_list,
 .ace_storage {
  color: #F9EE98 !important;
}

 .ace_entity.ace_name.ace_function,
 .ace_meta.ace_tag,
 .ace_variable {
  color: #AC885B !important;
}

 .ace_string {
  color: PaleTurquoise !important;
}

 .ace_string.ace_regexp {
  color: #E9C062 !important;
}

.ace_comment, .ace-tm span.ace_comment {
  font-style: italic !important;
  color: NavajoWhite !important;
}

 .ace_variable {
  color: #7587A6 !important;
}

 .ace_xml-pe {
  color: #494949 !important;
}

 .ace_markup.ace_underline {
  text-decoration: underline !important;
}

 .ace_indent-guide {
  background: url(data:image/png!important;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAACCAYAAACZgbYnAAAAEklEQVQImWMQERH5zzBz5sz/AA5EBAYqeZXWAAAAAElFTkSuQmCC) right repeat-y !important;
}
}

@-moz-document domain("codecademy.com") {
.jqconsole *{
  font-family: 'Source Code Pro' !important;
}

.jqconsole .print {
  color: snow !important;
}

.jqconsole .log 
{
  background-color: #2e2e2e!important;
}
}
