css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("groups.yahoo.com") {

img { display: none !important; visibility: hidden !important}
iframe { display: none !important; }
#div {  padding:0px !important; margin:0px !important; }

.ygrp-titlebar { display: none !important; }
#ygrp-titlebarhr { display: none !important; }
.top { display: none !important; }
.corner-top { display: none !important; }
.corner-left { display: none !important; }
.ygrp-header { display: none !important; }
.ygrp-gnav { display: none !important; }
#ygma { display: none !important; }
#ygmaproplinks { display: none !important; }
#ygrp-sponsored-links { display: none !important; }
.ygrp-adh { display: none !important; }
.ygrp-adu { display: none !important; }
.ad_slug_table { display: none !important; }
.ad_slug { display: none !important; }
.ad_slug_font { display: none !important; }
.footerbase { display: none !important; }
.doc-layout-foot { display: none !important; }
#doc-foot { display: none !important; }

#ygrp-menu .ygrp-contentblock {padding:0px 0 !important;}

.ygrp-contentblock { padding:0px 0px !important; }

div.ygrp-container-border { border: none !important; border-color:red !important; }

td .viewright a:link, a:visited { color:navy !important; }
.inactive { color:#5A5A5A !important; }

td .ygrp-blank { display: none !important; }
td .margin { display: none !important; }

.datatable {
	border-width: 1px 1px 1px 1px !important;
	border-spacing: 5px !important;
	border-style: none none none none !important;
	border-color: gray gray gray gray !important;
	border-collapse: separate !important;
	background-color: rgb(255, 250, 250) !important;
}
.datatable th {
	border-width: 1px 1px 1px 1px !important;
	padding: 1px 1px 1px 1px !important;
	border-style: outset outset outset outset !important;
	border-color: gray gray gray gray !important;
	background-color: white !important;
	-moz-border-radius: 6px 6px 6px 6px !important;
}
.datatable td {
	border-width: 1px 1px 1px 1px !important;
	padding: 1px 1px 1px 1px !important;
	border-style: outset outset outset outset !important;
	border-color: gray gray gray gray !important;
	background-color: white !important;
	-moz-border-radius: 6px 6px 6px 6px !important;
}

td .msgnum { background: #b2b2b2 !important; padding: 0px !important; white-space: normal !important; }

td .message { background: #EBEBEB !important; font-size: 120% !important; padding:2% !important;}
td .message a:link { color:navy !important; }
td .message a:visited { color: #5A5A5A !important; text-decoration: line-through !important; }

td .info { background: #295C7D !important; color:#ffffa6 !important; padding: 0px !important; white-space: normal !important; }
td .info a:link, a:visited { color:#ffffff !important; }

td .date { background: #b2b2b2 !important; padding: 0px !important; white-space: normal !important; }
td .date a:link, a:visited { color:navy !important; }

td .msg { background: #FBFFFE !important; font-size: 140% !important; }
td .msg a:link { color:navy !important; text-decoration: underline !important; }
td .msg a:visited { color: #5A5A5A !important; text-decoration: line-through !important; }

.ygrp-thumbnail img { display: inline !important; visibility: visible !important; }
.ownmod img { display: inline !important; visibility: visible !important; }
.selected img { display: inline !important; visibility: visible !important; }

}
