css:
@namespace url(http://www.w3.org/1999/xhtml);

/* RPOL.net Crystal Aurora Style */
/* Style by: adrasteia1 */
/* Web: http://www.roleplayersarchive.net */

@-moz-document regexp("https?://((www\.?)?rpol\.net|rpol.hyphenit.com.au).*") {

    body, th, tr, td, a {
           font-family: Verdana, Geneva, sans-serif !important;
    }
    
    body {
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-crystal-aurora/crystalaurorabg.jpg') !important;
           background-repeat: repeat !important; 
           background-color: #070c2a !important;
           color: #ffffff !important;
    }
    
    span.normal_text {
           color: #ffffff !important;
    }
    
    th.center.border_0010 {
           border: none !important;
    }
         
    td.left.message {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
           border: 1px solid #ffffff !important;
    }
    
    td.left.message td {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
    
    td.left.message td.quote {
           background-color: #424241 !important;
    }

    
    td.left.message_alt {
           background-color: #301643 !important;
           border: 1px solid #ffffff !important;
    }    
    
    td.left.message_alt table.message_table td {
           background-color: #000000 !important;
    }
    
    th.left, th.right, th.center {
           border: none !important;
    }
    
    tbody th {
           background-color: #102611 !important;
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-crystal-aurora/thback_1400px.jpg') !important;
           background-repeat: repeat-y !important;
           border: none !important;
           color: #ffffff !important;
    }
    
    tbody td {
           color: #ffffff !important;
    }
    
     td.left, td.right, td.center {
           color: #ffffff !important;
    }
      
    table.selection_table {
           margin-top: 15px !important;
           box-shadow: 5px 10px 15px #000000;
    }
    
    table.selection_table th {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
    
    table.selection_table th.left, th.center {
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-crystal-aurora/thback_1400px.jpg') !important;
           background-color: #102611 !important;
           background-repeat: repeat-y !important;
    }
  
    table.selection_table td {
           background-color: #2d152e !important;   
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
  
    table.selection_table.round_top th {
           background-image: url('http://www.roleplayersarchive.net/web_themes/images/rpol-crystal-aurora/thback_1400px.jpg') !important;
           background-repeat: repeat-y !important;
           background-color: #102611 !important;
    }
    
    table.selection_table a.clr img {
           background-color: #b281bd !important;
    }
    
    a, a:link, a:visited, .link-colour, .msg a:hover:before, .msg a:hover:after {
           color: #11e2e4 !important;    
    }
    
    a:hover, a:active, .alink-colour, .msg a:before, .msg a:after, a.button_on  {
           color: #FF3333 !important;
    }
 
    .border {
           border-color: #000000 !important;
    }
	
    .info_table {		
           border: 1px solid #000000 !important;
           background-color: #272726 !important;
    }
    table.info_box {
           border: 1px solid #000000 !important;
           background-color: #2d152e !important;
    }
    .info_box {
           border: 1px solid #272726 !important;
           background-color: #424241 !important;
    }
    table.info_table td {
           background-color: transparent !important;
	   /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
    table.info_table td.sm {
           background-color: #272726 !important;
    }
	
    table.info_table td.right {
	   background-color: #272726 !important;
    }
	
    table.info_table tr td {
	   background-color: #272726 !important;
    }
	
    table.info_table table.center td.right input.invis.med {
           color: #ffffff !important;
    }
	
    #header, #footer {
           border: 2px solid #000000 !important;
    }
   
    #header td {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
   
    #footer td {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
   
    .red, .red th, .red td {
           color: #FF3333 !important;
    }
    .blue, .blue th, .blue td {
           color: #3f7ba9 !important;
    }
    
    span.alink-colour {
           background-color: #272726 !important;
    }
        
    .stickyblock {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }
    
    td#stickylist.stickylist {
           background-color: #854188 !important;
    } 
   
    #wrapper:after {
           content:"RPOL.net Crystal Aurora Style by adrasteia1" !important;
           font-size: 9px !important;
           float: right !important;
           width: 300px !important;
    }   
    
    legend {
           color: #ffffff !important;
    }  

    #composediv.niftybox  table#composetable td.right {
           color: #ffffff !important;
    }

    #composediv.niftybox  table#composetable td.left {
           color: #ffffff !important;
    }

    tbody.info_table td {
           color: #000000 !important;
    }

    table.med.center tr.info_table td {
           color: #ffffff !important;
           background-color: #2d152e !important;
    }

    tr.info_table td {
           color: #ffffff !important;
           background-color: #2d152e !important;
    }
    
    tbody tr label {
           color: #ffffff !important;
    }
    
    tbody tr td.smc.sm.left {
           color: #000000 !important;
    }
    
    .niftybox {
           background-color: #2d152e !important;
           /*added to accompany b/g colour.*/
           color: #ffffff !important;
    }

    td.left.message_alt td.quote {
           background-color: #000000 !important;
    }
    img#i_sec1,img#i_sec2,img#i_sec3,img#i_sec4,img#i_sec5,img#i_sec6,img#i_sec7,img#i_sec8,img#i_sec9 {
           background-color: #b281bd !important;
    }
    #form-menu-container.right #form-menu.message a {
           background-color: #000000 !important;
    }
    table tbody.info_table tr td {
           background-color: #272726 !important;
           color: #ffffff !important;
    }
    center form.form {
           color: #ffffff !important;
    }
}
