css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^https?://s.*\\.ogame\\..*") {

    /* Correctifs Antigame */

    a.ago_menu_setting_help{
       border: none !important;
    }

    #ago_menubutton_logo{
        width: 27px !important;
        left: 7px !important;
        top: 1px !important;
        position: relative !important;
    }

    .ago_planets_arrow_planet, 
    .ago_planets_arrow_moon{
       display: none !important;
    }

    .ago_improve  #planetdata .overlay {
        background: transparent url(http://skins.aifesdespets.fr/rebuild_d/overlay_planet_antigame.png) no-repeat !important; 
        top: 2px !important;
        height: 219px !important;
        width: 375px !important;
        left: -5px !important;
    }
    
    #rechts .smallplanet:hover,
    .ago_feedback_planet{
    border: none !important;
    box-shadow: none !important;
    }

    .ago_mode_construction #planetdata td{
       padding: 2px 0px !important;
    }

    .ago_eventlist_table{
        background: none !important;
        border: none !important;
    }

    .ago_planets_construction,
    .ago_planets_construction_moon{
        background: url(http://skins.aifesdespets.fr/rebuild_d/construction_antigame.png) 0px -4px no-repeat !important;
        width: 170px !important;
        height: 25px !important;
        left: auto !important;
        top: -5px !important;
        right: -170px !important;
        padding-top: 8px !important;
        padding-left: 4px !important;
        z-index: 9999 !important;
    }

    .ago_planets_construction_moon {
        top: 25px !important;
        z-index: 9999 !important;
    }

    #rechts #planetList .planet-name{
    padding-top: 5px !important;
    }

    #planetdata #planetOptions {
    padding-top: 30px !important;
    }

    .ago_menu_info{
    background: none !important;
    border: none !important;
    width: 175px !important;
    }

    .ago_display_arrow{
    right: 35px !important;
    background: url(http://skins.aifesdespets.fr/rebuild_d/open_close.png) !important;
    }

    table#eventContent tr.ago_eventlist {
    border: none !important;
    background-image: url(http://skins.aifesdespets.fr/rebuild_d/event_list_odd.png) !important;
    }

    #eventContent tr.ago_eventlist > td,
    #eventContent tr.ago_eventlist .ago_eventlist_fleet td  {
    border: none !important;
    }

    .ago #description .txt_box{
    position: relative !important;
    width: 647px !important;
    left: 0 !important;
    }
    
    .ago_jumpgate_improve .homeIcon {
        top: 64px !important;
        left: 230px !important;
    }

    /* Page fleet1 */

    #fleet1 .ago #planet, 
    #fleet2 .ago #planet,
    #fleet3 .ago #planet{
    background: url(http://skins.aifesdespets.fr/rebuild_d/detail_250.png) !important;
       height: 200px !important;
    }

    #fleet1 .ago #planet.shortHeader, 
    #fleet2 .ago #planet.shortHeader,
    #fleet3 .ago #planet.shortHeader{
       height: 80px !important;
    }

    #fleet1 .ago #planet a.toggleHeader,
    #fleet2 .ago #planet a.toggleHeader,
    #fleet3 .ago #planet a.toggleHeader{
        background: url(http://skins.aifesdespets.fr/rebuild_d/open_close.png) !important;
        background-position: 0px 20px !important;
    }

    #fleet1 .ago #planet a.toggleHeader:hover,
    #fleet2 .ago #planet a.toggleHeader:hover,
    #fleet3 .ago #planet a.toggleHeader:hover{
       background-position: 20px 20px !important;
    }

    #fleet1 .ago #planet.shortHeader a.toggleHeader,
    #fleet2 .ago #planet.shortHeader a.toggleHeader,
    #fleet3 .ago #planet.shortHeader a.toggleHeader{
       background-position: 0px 0px !important;
    }

    #fleet1 .ago #planet.shortHeader a.toggleHeader:hover,
    #fleet2 .ago #planet.shortHeader a.toggleHeader:hover,
    #fleet3 .ago #planet.shortHeader a.toggleHeader:hover{
       background-position: 20px 0px !important;
    }

    #fleet1 #buttonz #ago_summary {
        position: absolute !important;
        top: -440px !important;
        left: -3px !important;
        width: auto !important;
    }
    
    #fleet1 #buttonz #ago_summary .ago_info {
        width: 200px !important;
    }
    
    #fleet1 #buttonz #ago_summary .ago_calc_coords{
        min-width: 200px !important;
    }
    
    #fleet1 #buttonz  #ago_summary .ago_routine{
        margin-left: 10px !important;
        background: none !important;
    }
    
    #fleet1 #buttonz #ago_summary #ago_calc{
        padding-left: 0px !important;
    }
    
    #fleet1 #ago_summary #ago_calc .ago_calc_spacer{
        display: none;
    }
    
    #fleet1 #ago_summary td,
    #fleet2 .ago_summary_total td,
    #fleet3 .ago_summary_total td{
        background: none !important;
    }

    .ago_items_textName{
    top: 9px !important;
    }
    .ago_items_textInfo{
    top: 41px !important;
    }
    #ago_items_resource{
    background: rgba(12,16,20,0.8) !important;
    }

    #fleet1 div[class="secondcol fleft"]{
    position: relative !important;
    top: -12px !important;
    }


    #fleet1 .allornonewrap {
    padding-bottom: 10px !important;
    margin-top: -5px !important;
    }

    #fleet1 .allornonewrap table{
    padding-left: 20px !important;
    }

    #fleet1 .ago_fleet_infos{
    position: absolute !important;
    top: 50px !important;
    }

    .ago_feedback{
    box-shadow:none !important;
    }

    .text_palered{
    color: white !important;
    text-shadow: 0px 0px 1px #f00 !important;
    }

    #fleet1 #continue span{
    margin-top: 2px !important;
    }

    /* Coordonn√©es sur le bouton "Continuer" */

    #fleet1 .ago_continue:active{
       border: none !important;
    }

    #fleet1 #ago_continue_coords{
        background: none !important;
        position: absolute !important;
        top: 29px !important;
        right: 70px !important;
    }
    
    #fleet1 #ago_continue_type {
        position: relative !important;
        top: -5px !important;
        left: -55px !important;
    }

    #fleet1 #allornone img {
        vertical-align: 0 !important;
    }

    #fleet1 #ago_shortcuts{
    top: 80px !important;
    padding-bottom: 50px !important;
    }

    /* Page Flotte 2 */
    #fleet2 .c-left, 
    #fleet2 .c-right{
    display: none !important
    }

    #fleet2 .briefing{
    border: none !important;
    }

    #fleet2 #buttonz .fleetBriefing{
    margin-top: 20px !important;
    }

    #fleet2 #continue{
    position: relative !important;
    top: 40px !important;
    }

    #fleet2 #back{
    position: relative !important;
    top: 40px !important;
    left: 20px !important;
    }

    #fleet2 .ago_fleet_continue{
    background: none !important;
    width: 100px !important;
    top: 90px !important;
    right: 70px !important;
    position: absolute !important;
    }

    #fleet2 #ago_fleet_continue_1_coord{
    background: none !important;
    position: absolute !important;
    top: 15px !important;
    right: 25px !important;
    }

    #fleet2 #ago_fleet_continue_1_type{
    position: relative !important;
    right: -10px !important;
    }

    #fleet2 #ago_fleet_continue_1_mission,
    #fleet2 #ago_fleet_continue_1_mission:active{
    display: none !important;
    }

    /* Page Flotte 3 */
    #fleet3 .c-left, 
    #fleet3 .c-right{
    display: none !important
    }

    #fleet3 #roundup li{
    padding-bottom: 0px !important;
    }

    #fleet3 #roundup #expeditiontimeline{
    margin-top: 10px !important;
    }

    #fleet3 .border3px{
    border: none !important;
    }

    #fleet3 .res_wrap{
    margin: 1px 0px 0px !important;
    }

    #fleet3 #loadAllResources{
    background: none !important;
    }

    #fleet3 #start{
    position: relative !important;
    bottom: 40px !important;
    }

    #fleet3 #back{
    position: relative !important;
    bottom: 45px !important;
    }

    /* Page Galaxie */

    #galaxy #message-wrapper{
    display: block !important;
    }

    #galaxy #attack_alert{
    position: absolute !important;
    right: 20px !important;
    top: -5px !important;
    }

    #galaxy a#message_alert_box.emptyMessage,
    #galaxy a#message_alert_box_default,
    #galaxy a#message_alert_box{
    position: absolute !important;
    left: -420px !important;
    top: 0px !important;
    }

    #galaxy #helper
    {
    position: absolute !important;
    top: 16px !important;
    margin-left: -14px !important;
    }

    #galaxy #helper a, #galaxy #helper a:hover.help_selected
    {
    background: url(http://skins.aifesdespets.fr/rebuild_d/icon_help.png) 0px -3px !important;
    }

    #galaxy #helper a:hover, #galaxy  #tutorial #helper a{
    background: url(http://skins.aifesdespets.fr/rebuild_d/icon_help_hover.png) 0px -3px !important;
    }

    #galaxy #eventboxLoading {
    display: none !important;
    }

}
