@-moz-document domain("wikipedia.org") {
* {
  font-family: 'Lato', sans-serif;
}
#mw-content-text {
  margin-top: -0.75em;
}

#mw-content-text>p, #mw-content-text li, #mw-content-text dl, .plainlinks, #mw-articlefeedbackv5, #articleFeedbackv5-panel, .gallery {
  margin-left: auto !important;
  margin-right: auto !important;
  max-width: 600px !important;
}
.plainlinks {
  display: block;
}
.cquote, .column-count, .column-width, blockquote, .reflist, .references, .references-column-width {
  margin-left: auto !important;
  margin-right: auto !important;
  max-width: 900px !important;
}
blockquote{
  font-weight: 300;
  font-size: 110%;
  max-width: 800px !important;
}
.dablink {
  text-align: right;
  padding: 0;
  margin: 0;
  margin-left: -1em;
  font-weight: 300;
  width: -webkit-calc(50% - 300px);
  width: -moz-calc(50% - 300px);
  width: -ie-calc(50% - 300px);
  width: -o-calc(50% - 300px);
  width: calc(50% - 300px);
}
.dablink a {
  font-weight: 500;
}
.dablink + table + p, .dablink + p {
  margin-top: -1.5em;
}
.dablink + .dablink + table + p, .dablink + .dablink + p {
  margin-top: -3em;
}
.dablink + .dablink + .dablink + table + p, .dablink + .dablink + .dablink + table + p {
  margin-top: -4.5em;
}
h2 + .wikitable,h3 + .wikitable, h2 + .tleft, h3 + .tleft, h4 + .tleft, h2 + blockquote, h3 + blockquote, h2 + .reflist, h2 + center, h3 + center, h2 + .plainlinks, h3 + .plainlinks, h4 + .plainlinks, h2 + .column-width, h3 + .column-width, h4 + .column-width, h2 + .refbegin , h3 + .refbegin , h4 + .refbegin {
  margin-top: 2em;
}
.gallery {
  text-align: center;
}
#contentSub {
  text-align: center;
  margin-top: -1.5em;
}
#mw-articlefeedbackv5, #articleFeedbackv5-panel {
  display: block;
}
#mw-content-text dl dt{
  margin-left: -2em;
}
#mw-content-text dl dd{
  margin-left: 0em;
}
#content {
  padding-top:0!important;
  margin-top: -2.65em;
  background: none !important;
}
#bodyContent{
  border-top: gray 1px solid;
}
h1 {
  font-weight:800;
  font-size: 200% !important;
  border: none !important;
  margin-left: 2.5em;
}
#mw-content-text>h2{
  padding-top: 0.25em;
  border-bottom: 0;
  border-top: 1px solid #aaa;
  margin-bottom: -1.4em;
  margin-top: 1em;
  clear:left;
  font-weight:600;
  font-size: 200%;
}
#mw-content-text>h3{
  margin-bottom: -1.25em;
  margin-top: 0.75em;
  clear:left;
  font-weight:400;
  font-size: 175%;
}
#mw-content-text>h4{
  margin-bottom: -1.75em;
  margin-top: 0.5em;
  clear:left;
  font-weight:300;
  font-size: 150%;
}
#mw-content-text>h2 + h3, #mw-content-text>h2 + .tright + h3, #mw-content-text>h2 + .tleft + h3 {
  margin-bottom: -2.5em;
}
#mw-content-text>h3 + h4, #mw-content-text>h3 + .tright + h4, #mw-content-text>h3 + .tleft + h4 {
  margin-bottom: -2.75em;
}
h2 + h3 + h4 {
  margin-top: 2.5em !important;
  margin-bottom: -4.5em !important;
}
.wikitable, table {
  margin-left: auto !important;
  margin-right: auto !important;
}
.mainarticle, .seealso, .rellink {
  margin-top: 2em;
  text-align: left;
  margin-right: 3em;
  margin-bottom: -2em;
  font-weight: 300;
  font-size: 110%;
  width: -webkit-calc(50% - 350px);
  width: -moz-calc(50% - 350px);
  width: -ie-calc(50% - 350px);
  width: -o-calc(50% - 350px);
  width: calc(50% - 350px);
  display: inline;
  position: absolute;
  text-align: right;
}
.mainarticle a, .seealso a {
  font-weight: 500;
}
.editsection {
  float: none !important; 
  vertical-align: super;
  margin: 0 !important;
  font-size: 10px !important;
  letter-spacing: -1px;
  font-weight: bold !important;
  margin-right: 4px !important;
  opacity: 0;
}
h2:hover .editsection, h3:hover .editsection {
  opacity: 0.8;
  -webkit-transition: 2s all ease;
  -moz-transition: 2s all ease;
  -ie-transition: 2s all ease;
  -o-transition: 2s all ease;
  transition: 2s all ease;
}
.editsection a {
  font-weight: normal !important;
  font-size: 14px;
  vertical-align: middle;
}
.mw-headline {
  width: -webkit-calc(50% - 350px);
  width: -moz-calc(50% - 350px);
  width: -ie-calc(50% - 350px);
  width: -o-calc(50% - 350px);
  width: calc(50% - 350px);
  display: inline;
  position: absolute;
  text-align: right;
}
.toc, #toc {
/*
  float: left;
  position: absolute;
  top: 1.25em;
  min-width: 230px;
  max-width: -webkit-calc(50% - 325px);
  max-width: -moz-calc(50% - 325px);
  max-width: -ie-calc(50% - 325px);
  max-width: -o-calc(50% - 325px);
  max-width: calc(50% - 325px);
*/
  margin-left: auto !important;
  margin-right: auto !important;
  margin-bottom: 1em;
  padding-top: 0;
}
#toc ul {
  display: -webkit-flexbox;
  -webkit-flex-align: center;
  -webkit-flex-orient: horizontal;
  -webkit-flex-pack: justify;
}
.toc ul .toclevel-1 {
  display: inline-block;
  vertical-align: top;
  -webkit-box-flex: 1;
  -moz-box-flex: 1;
  box-flex: 1;
  padding: 0 0.5em;
  padding-bottom: 1em;
  border-top: rgba(0,0,0,0.5) solid 1px;
}

.toclevel-1 {
  font-size: 110%;
}

.toclevel-2 {
  font-size: 100%;
}

.toclevel-3 {
  font-size: 90%;
}

#toc>*,#toc #toctitle>* {
  -webkit-transition: 0.5s all ease;
  -moz-transition: 0.5s all ease;
  -ie-transition: 0.5s all ease;
  -o-transition: 0.5s all ease;
  transition: 0.5s all ease;
}
#toc #toctitle {
/*
  float: right;
  margin-right: 0.25em;
*/
}
#toc.tochidden #toctitle{
  float: none;
  text-align: center;
}
#toc.tochidden #toctitle h2, #toc.tochidden #toctitle .toctoggle {
  display: inline;
}
#toc #toctitle h2{
/*  display: block; */
  margin: 0;
  line-height:1;
}
#toc #toctitle .toctoggle {
/*  display: block;*/
  font-size: 80%;
  margin:0;
  line-height:0.75;
}
.toc ul, #toc ul {
  margin: 0;
}
.toc ul li, #toc ul li {
  margin: 0;
  line-height: 1.2;
}
#toc .tocnumber {
  font-weight: 200;
}
#toc .toctext {
  font-weight: bold;
}
#mw-articlefeedback{
  display: block;
  margin-left: auto;
  margin-right: auto;
  max-width: 600px;
}
#siteSub {
  display:none;
}
