css:
@-moz-document domain("allegro.pl") {
	div.sub-container {
	  display: inline !important; 
	  position: static; 
	  }

	#left .departments ul li h2 a, 
	#left .departments ul li h2 a:hover {
	  text-align: center;
	  color: #000;
	  }

	#left .departments ul li a {
	  padding: 3px;
	  }

	#left .departments ul > li > div.sub-container {
	  position: relative; left: -1px;
	  float: left;
	  display: inline;
	}

	#left ul li a:hover { 
	  background: transparent; 
	  /*margin-left:20px;*/
	  color: #ff6500;
	}
}
