css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain('fridaystudentportal.com') {
body>table:after {
content: "Portal redesign Â© Connor Doherty";
color: grey;
font-size: small;
text-align: center;
width:100%
}

td.allbdr h3 var:after {
display: block;
font-size: large;
content: "This is a special formatting applied to the normal Student Portal to make it look better. It is an attempt to make important things bigger, and fit more on your screen. Please note, this is optimized for the 'Gradebook' page only. Enjoy!"
}

table {
min-width: 800px;
max-width: 85em;
width: 100% !important;
}
td.allbdr.menu {
width: 20% !important;
min-width: 12em;
max-width: 14em;
font-family: "Comic Sans MS", cursive, sans-serif !important;
font-size: x-large !important;
}

td.allbdr.menu a {
font-family: "Comic Sans MS", cursive, sans-serif !important;
font-size: x-large !important;
width: 100%;
}

td.allbdr.menu a:hover {
background-color: white;
text-decoration: none !important;
}

td.allbdr:nth-child(2) table:nth-child(1) td[valign=bottom] {
text-align: center;
}

td.allbdr:nth-child(2) table:nth-child(1) td[valign=bottom] a {

font-size: large !important;
color:green !important;
}

td.allbdr:nth-child(2) table:nth-child(4) {
float:right;
display: inline !important;
margin-top: -2.5em !important;

}

td.allbdr:nth-child(2) table:nth-child(2) {
display: inline !important;
width: auto !important;
display: inline !important;
position: relative;
left:26em;
top:0;
}

td.allbdr:nth-child(2) table:nth-child(3) {
display: inline !important;
width: auto !important;
display: inline !important;

}

td.allbdr:nth-child(2) table:nth-child(1) tr:nth-child(1) th{
text-align: center !important;
font-size: x-large !important;
}

td.allbdr:nth-child(2) table:nth-child(1) tr:nth-child(2) td{
text-align: center !important;
font-size: x-large !important;
}
td.allbdr:nth-child(2) table:nth-child(1) tr:nth-child(2) b {
display:block;
font-size: medium;
}

table.subbox td.botbdrdetail:nth-child(2) {
font-weight: bold;
font-size: medium !important;
}

tr:first-child td.allbdr:first-child {
height: 60px !important;
overflow: hidden;
background-color:rgba(255,255,255,0) !important;
}
tr:first-child td.allbdr:first-child p {
float: left;
}
tr:first-child td.allbdr:first-child h2 {
float: right;
color: white !important;
}
tr:first-child td.allbdr:first-child h2 span {
color: white !important;
display: none;
}
tr:first-child td.allbdr:first-child h2:before {
color: silver !important;
content: "[Student Portal]";
}

tr:first-child td.allbdr:first-child p,tr:first-child td.allbdr:first-child img,tr:first-child td.allbdr:first-child h2 {
padding: 0;
margin: 0;
display: inline;
}

td.allbdr:nth-child(2) {
background-color: gray;
width: 80% !important;
}

td.allbdr {
background-color:rgba(255,255,255,0.7) !important;
}

td.allbdrdetail {
text-align: center !important;
font-size: 125% !important;
}

td.allbdrdetail a:nth-child(1) {
color: lightgreen !important;
}

td.allbdrdetail a:nth-child(2) {
color: black !important;
font-weight: bold;
}

body {
background-color: gray !important;
background-image: url(http://fc00.deviantart.net/fs45/f/2009/107/2/1/Blue_Wallpaper_Aurora_by_thedisturbed0ne.jpg);
background-attachment: fixed;
background-origin: left bottom;
background-repeat: repeat-x;
}
* {
border-radius: 10px;
}
}
