css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix(https://hootsuite.com/dashboard){
/*
      name: hootsuite inkyo-gama.net style.
    auther: inkyo kobayashi.(inkyogama, japan)
       url: http://inkyo-gama.net/
    status: win2000pro+firefox12+hootsuite+stylish.
      memo: hootsuite is using only twitter.
     write: 2013.1.22

      site: http://userstyles.org/styles/81936/hootsuite-inkyo-gama-net-style
*/

html {
/*
    my favorite font. 'Migu 1C'
    http://mix-mplus-ipa.sourceforge.jp/migu/
*/
    font-family: 'Migu 1C' !important;
    text-shadow: none !important;
}

/* trim.png */
.trim, .btn-spl, .btn-del, .btn-cmt, .btn-dbl, .ui-dialog-titlebar {
    background-image: url("data:image/png;base64,") !important;
}
/* texture-dark.png */
.globalNav, .ui-sortable, .ui-dialog-content {
    background-image: none !important;
    background-color: #3d2d00 !important;
}

/* quickstart */
.quickstartStream h3 {
    color: #fafad2 !important;
}
.startMenu {
    background-color: #fafad2 !important;
}
.startMenu h4 {
    color: #3d2d00 !important;
}
.startMenu .btn-sta-med {
    background-color: #ffffee !important;
    color: #3d2d00 !important;
}


/* ツイートメッセージボックス */
.messageBoxContainer .messageSection {
    background-color: #ffffee !important;
}
.messageBoxContainer .messageBox .composerBox {
    background-color: #ffffee !important;
}
.messageBoxContainer.expanded .messageBox .composerBox {
    height: 128px !important;
}
.messageBoxContainer.expanded .messageBoxMessage {
    height: 128px !important;
}
.messageBoxContainer .messageBoxMessage {
    background-color: #ffffee !important;
    color: #3d2d00 !important;
    font-size: 150% !important;
}
.messageBoxContainer .pretext {
    font-size: 150% !important;
    padding: 2px 2px 2px 2px !important;
}
.messageBoxContainer .messageTools {
    background-color: #fafad2 !important;
}

.selectProfiles {
    width: 256px !important;
}

.profileSelectorWidget {
    width: 96px !important;
}
.profileSelectorWrapper {
    width: 96px !important;
}

/* グローバル ナビ */
.globalNav {
    background-image: url(data:image/png;base64,) !important;
    background-position: 0px 392px !important;
    background-repeat: no-repeat !important;
}
.globalNav, .globalNavMenu {
    background-color: #3d2d00 !important;
}
.globalNavMenu a.firstlevel {
    color: #fafad2 !important;
}
.globalNavMenu a.firstlevel.active {
    color: #3d2d00 !important;
}
.globalNavMenu li:hover a.firstlevel .rb-a-3 {
    background-position: 0px -2150px !important;
}
.globalNavMenu li:hover a.firstlevel .rb-a-3 .text {
    color: #3d2d00 !important;
    text-shadow: none !important;
}
.globalNavMenu .sidebarNavMenu .navSubMenu {
    background-color: #3d2d00 !important;
    background-image: none !important;
}
.globalNavMenu .sidebarNavMenu .navSubMenu a {
    color: #fafad2 !important;
    border: 0px !important;
    text-shadow: none !important;
}
.globalNavMenu .sidebarNavMenu .navSubMenu a:hover {
    background-color: #fafad2 !important;
    color: #3d2d00 !important;
    text-shadow: none !important;
}
.globalNavMenu .sidebarNavMenu .active .text {
    color: #3d2d00 !important;
}

#dashboardTabs .tab .trim ._text {
    color: #fafad2 !important;
}

/* ヘッダー */
#streamTabInfo {
    background-color: #3d2d00 !important;
}
.btn-lite-sta, .btn-lite-sta:hover, .btn-lite-sta:focus {
    background-color: #fafad2 !important;
    background-image: -moz-linear-gradient(center bottom , rgb(250, 250, 210) 0pt, rgb(255, 255, 238) 100%) !important;
    color: #3d2d00 !important;
}
.ui-slider-handle {
    background-color: #fafad2 !important;
}
.tooltip {
    background-color: #3d2d00 !important;
    color: #fafad2 !important;
}

/* ストリーム */
#streamsContainer {
    background-image: none !important; /* texture-dark.png */
    background-color: #3d2d00 !important;
}
.stream {
    color: #3d2d00 !important;
    background-color: #fafad2 !important;
}
.stream .header {
    background-color: #3d2d00 !important;
    color: #fafad2 !important;
}
.stream .header .streamName {
    font-size: 13px !important;
    color: #fafad2 !important;
}
.stream .header .streamName .subTitle {
    color: #fafad2 !important;
}
.stream .content {
    color: #7a5a00 !important;
}
/* 新着ツイートアイコンは削除 */
span.status {
    background-image: none !important;
}
/* ツイート操作のドロップダウンメニュー */
.messageOptions{
    background-color: #3d2d00 !important;
    height: 20px !important;
    margin-top: -5px !important;
    padding: 2px 4px 2px 8px !important;
    position: absolute !important;
    box-shadow: 0px 3px 10px rgba(61, 45, 0, 0.75) !important;
}
.moreOptionsMenu {
    background-color: #3d2d00 !important;
    color: #fafad2 !important;
}
.moreOptionsMenu .option {
    color: #fafad2 !important;
    text-shadow: none !important;
}
.moreOptionsMenu .option:hover {
    background-color: #fafad2 !important;
    color: #3d2d00 !important;
    text-shadow: none !important;
}
/* さらに */
.trim.gap {
    height: 30px !important;
}
/* ツイート メッセージ */
.message {
    border-top: 0px solid #3d2d00 !important;
    border-bottom: 1px solid #3d2d00 !important;
    background-color: #ffffee !important;
    color: #3d2d00 !important;
    padding-bottom: 2px !important;
}
.message .actions {
    background-color: #3d2d00 !important;
}
.message a {
    color: #7a5a00 !important;
}
.message .networkAvatar {
    border: 0px solid #ffffee !important;
}
.message .networkAvatarLink {
    background-image: none !important;
}
.message>.networkName {
    font-size: 180% !important;
    font-weight: normal !important;
    margin: 4px 0px 4px 0px !important;
}
.message a.networkName {
    color: #3d2d00 !important;
}
.message .postTime {
    float: right !important;
    margin: 0px 0px !important;
}
.message a.postTime {
    color: #3d2d00 !important;
}
.message p {
    clear: both !important;
    line-height: 1.173205081em !important;
    margin: 0px -6px 0px -36px !important;
    padding-bottom: 2px !important;
    word-wrap: break-word !important;
}
.message .messageContent {
    color: #3d2d00 !important;
}
.message .retweet {
    display: inline-block !important;
    float: none !important;
    margin: 0px 2px 0px -40px !important;
}
.message .retweetedBy {
    display: inline !important;
    color: #3d2d00 !important;
}
.message a.showConversation {
    color: #3d2d00 !important;
}
.message .icon-13.preview {
    background-position: 0px -26px !important;
    border: 1px solid #3d2d00 !important;
}
/* 新着ツイート メッセージ */
.newMessage {
    background-color: #fafad2 !important;
}
.newMessage .messageContent {
    font-size: 150% !important;
}
.newMessage a {
    color: #f04000 !important;
}
/* 会話を表示 */
/* どう設定しても表示が微妙にずれてしまいます＞＜ */
.previousMessages {
    background-color: #fafad2 !important;
    border: 1px solid #3d2d00 !important;
    margin: 0px 0px 0px -36px !important;
    padding: 0px 0px 0px 0px !important;
}
.previousMessages>.comment {
    background-color: #fafad2 !important;
    border-bottom: 1px dashed #3d2d00 !important;
    display: inline-block !important;
    margin: 0px !important;
    padding: 0px !important;
    position: relative !important;
    width: 100% !important;
    top: 0px !important;
}
.previousMessages>.comment>.networkAvatarLink,
.previousMessages>.comment>.networkAvatar {
    top: 2px !important;
    left: 2px !important;
}
.previousMessages>.comment>.commentContent {
    color: #3d2d00 !important;
    font-size: x-small !important;
    margin: 0px 0px 0px 36px !important;
    padding: 0px 0px 0px 0px !important;
}
.previousMessages>.comment.originalComment {
    background-color: #ffffee !important;
    border: 0px dashed #3d2d00 !important;
    display: inline-block !important;
    margin: 0px !important;
    padding: 0px !important;
    position: relative !important;
    width: 100% !important;
    top: -2px !important;
}

/* ユーザーのドロップダウンメニュー */
.menu-dropdown {
    background-color: #3d2d00 !important;
}
.menu-dropdown .row {
    border-bottom: 0px solid #fafad2 !important;
    color: #fafad2 !important;
    font-weight: normal !important;
    text-shadow: none !important;
    /* 動作環境が満たされているのなら有効にしてください */
    /* text-transform: full-width !important; */
}
.menu-dropdown .row.selected {
    background-color: #ffffee !important;
    color: #3d2d00 !important;
}
.menu-dropdown .row:hover {
    background-color: #fafad2 !important;
    color: #3d2d00 !important;
}

/* プレビュー */
#bubblePopPane {
    max-width: none !important;
    max-height: none !important;
}
#bubblePopPane img {
    max-width: none !important;
    height: auto !important;
}

/* クイック検索 */
#quickSearchContainer .ui-panel-content .message.userResult>p {
    margin-left: 0px !important;
}
#quickSearchContainer .messageOptions {
    background-color: #3d2d00 !important;
    height: 19px !important;
    margin-top: -8px !important;
    margin-right: -7px !important;
    box-shadow: 0px 3px 10px rgba(61, 45, 0, 0.75) !important;
}

#header .quickSearch #quickSearchField {
    width: 200px;
}
#footer {
    display: none;
    height: 0px;
}

/* スリープ ダイアログ */
.ui-dialog-titlebar, .btn-cmt {
    background-color: #3d2d00 !important;
    color: #fafad2 !important;
}
.ui-dialog-section {
    background-color: #ffffee !important;
    color: #3d2d00 !important;
}
.btn-cmt {
    color: #ffffee !important;
}
.btn-cmt:hover {
    color: #3d2d00 !important;
}

/*
'fix hootsuite' script for Greasemonkey(2.4.07)
http://userscripts.org/scripts/show/62909
    以下、プログラム構文をスクリプトに追記の事。
    line245(add): $(toolsLink).attr("title",window.__hide_headertool?"hide":"show");
    line256(add): $("#showTools").attr("title",window.__hide_headertool?"hide":"show");
    *更に、括弧"[]"を削除すると良いかもしれません。（ex. "[Header]"→"Header"）
*/
.selectProfiles .linkbtn {
    background-color: #fafad2 !important;
    background-image: -moz-linear-gradient(50% 100%, rgba(250,250,210,0), rgba(255,255,238,1)) !important;
    border-radius: 3px 3px 3px 3px !important;
    border: 1px solid #9e9680 !important;
    box-shadow: 0pt 1px 0px 0px #fafad2 inset !important;
    color: #3d2d00 !important;
    font-size: x-small !important;
    margin: 1px 1px 1px 1px !important;
    padding: 3px 3px 3px 3px !important;
}
.selectProfiles .linkbtn:hover {
    background-color: #ffdf7f !important;
    background-image: -moz-linear-gradient(top, #d7a70f, #f7c72f 50%, #ffd75f 50%, #ffdf7f) !important;
    text-decoration: none !important;
}
.selectProfiles .linkbtn#showTools[title^="show"] {
    background-color: #ffdf7f !important;
    background-image: -moz-linear-gradient(top, #d7a70f, #f7c72f 50%, #ffd75f 50%, #ffdf7f) !important;
}

/*
'inline thumbnail' script for Greasemonkey
http://userscripts.org/scripts/show/106779
    firefox12では期待通りに動作しないのでサムネイルを大きめに表示。
*/
.ithumb-container .ithumb-img {
    border: 1px solid #3d2d00 !important;
    min-height: 150px !important;
    min-width: 150px !important;
}

}
