css:
@-moz-document domain("wumii.com"){
body{background-color: #151515  !important;font-family: 微软雅黑 !important;}
a{color: #92EDFF !important;} /*title and link*/
li{background-color: #151515} /* Comments */
blockquote{background-color: #151515 !important;} /* block contain*/
p{font-size:18px !important;color: #7B927E !important;}
.image-item{background-color: #151515 !important;}
.reader-image{background-color: #151515 !important;}

div#container{background-color: #151515  !important;; font-family: 微软雅黑 !important;}
div#main{background-color: #151515; font-family: 微软雅黑 !important;}
div#readContainer{background-color: #151515; font-family: 微软雅黑 !important;}
div#toolBar{background-color: #151515 !important;}
div.summary{color: #808080 !important;} /*Summary font*/
div.inner-box{color: #C7EDCC !important;}
div.sub-menu{background-color: #151515  !important;} /*Opened menu*/
div.comment-textarea{color: #C7EDCC !important;background-color: #151515  !important;}
div.tab{background-color: #555588 !important;}
div.content{color: #C7EDCC !important;} /*Main content of a news*/
div.comment-list{background-color: #151515 !important;} /* block contain*/
div.reader-title{background-color: #151515 !important;} /* Reader's title */
div.item-detail{background-color: #454545 !important;} /* Reader's title */
div.information-board{background-color: #151515  !important;font-family: 微软雅黑 !important;}
div.msgBox-content{background-color: #555555 !important;}
div.msgBox-buttons{background-color: #555555 !important;}
div.magazine-list{background-color: #555555 !important;}
div.drag-handler{background-color: #000000 !important;}
div.subscription-box{background-color: #152535 !important;}
div.subscription-nav{background-color: #152535 !important;}

span.rec-setting{background-color: #151515 !important;}
span.title{color: #C7EDCC !important;background-color: #151515 !important;}

p.title{font-size:130% !important; font-family: 微软雅黑 !important;color: #C7EDCC !important;} /*Title font*/
p.article-title{color: #C7EDCC !important; background-color: #151515 !important;background: none !important;}

a.add-friends{background-color: #151515 !important;}
a.on{background-color: #929200 !important;}

li.menu-item{background-color: transparent;} /* Top menu items */
li.cur{background-color: #151515 !important;}

h3.title{font-size: 150% !important; color: #C7EDCC !important;background-color: #555555 !important;}
h2.title{font-size: 150% !important; color: #C7EDCC !important;}
}
