/* Firefox ablak: Windows 8 scrollbars */
/* Original: Ziz4rD - http://userstyles.org/users/82707 */

scrollbar {
-moz-appearance: none !important;
border: none !important}

scrollbar {
    background: #EFEFEF !important}

scrollbar thumb {
    -moz-appearance: none !important;
    border: 1px solid #CDCDCD !important;
    background: #CDCDCD !important;
    background-clip: padding-box !important;
    -moz-transition: ease-in .2s !important}

scrollbar thumb:not(:active):hover {
    border-color: #A6A6A6 !important;
    background-color: #A6A6A6 !important;
    background-clip: padding-box !important}

scrollbar thumb:hover:active,
scrollbar thumb:active
scrollbar thumb[active="true"] {
    border-color: #606060 !important;
    background: #606060 !important;
    background-clip: padding-box !important}

scrollbar scrollbarbutton, 
scrollbar gripper, 
scrollcorner {
    -moz-appearance: none ! important;
    border: 1px solid #F0F0F0 !important;
    background: #F0F0F0 !important;
    -moz-transition: ease-in .2s !important}

scrollbar[orient="vertical"] > scrollbarbutton {
    min-height: 17px !important;
    max-height: 17px !important;
    min-width: 15px !important;
    max-width: 15px !important}

scrollbar[orient="horizontal"]  > scrollbarbutton {
    min-height: 15px !important;
    max-height: 15px !important;
    min-width: 17px !important;
    max-width: 17px !important}

scrollbar[orient="vertical"] > scrollbarbutton[type="decrement"] {
    background-image: -moz-image-rect(url("data:image/png;base64,"), 0 ,50% ,50%, 0) !important;
 	background-repeat: no-repeat !important;
 	background-position: 3px 6px !important}

scrollbar[orient="vertical"] > scrollbarbutton[type="increment"] {
    background-image: -moz-image-rect(url("data:image/png;base64,"), 0 ,100% ,50%, 50%) !important;
 	background-repeat: no-repeat !important;
 	background-position: 3px 3px !important}

scrollbar[orient="horizontal"] > scrollbarbutton[type="decrement"] {
    background-image: -moz-image-rect(url("data:image/png;base64,"), 50% ,50% ,100%, 0) !important;
 	background-repeat: no-repeat !important;
 	background-position: 6px 3px !important}

scrollbar[orient="horizontal"] > scrollbarbutton[type="increment"] {
    background-image: -moz-image-rect(url("data:image/png;base64,"), 50% ,100% ,100%, 50%) !important;
 	background-repeat: no-repeat !important;
