css:
@-moz-document domain("youtube.com") {
* { 
  -webkit-box-sizing: border-box !important; 
  -moz-box-sizing: border-box !important; 
  -ie-box-sizing: border-box !important; 
  -o-box-sizing: border-box !important; 
  box-sizing: border-box !important; 
  min-width: auto !important;
}
#alerts, #content, #copyright, #footer, #promos, #ticker .ytg-wide {
  width: auto !important;
}
.search-base #guide + #content {
  width: auto !important;
  margin: 0 !important;
}
#masthead-expanded {
  padding-top: 32px !important;
}
#content {
  max-width: /*[[mywidth]]*/;
  margin-left: auto !important;
  margin-right: auto !important;
}
.yt-uix-button-group {
  white-space: normal;
}
#yt-masthead-container * {
  max-width: 100% !important;
  display: inline-block !important;
  margin:0 !important;
}
#yt-masthead-container {
  min-width: 100% !important;
  display: block !important;
  width: 100% !important;
  margin-left: auto !important;
  margin-right: auto !important;
  text-align: center !important;
  position: fixed;
  opacity: 0;
  top: 0;
  left: 0;
  z-index: 666;
  -webkit-transition: all 0.5s linear !important;
  -moz-transition: all 0.5s linear !important;
  -ie-transition: all 0.5s linear !important;
  -o-transition: all 0.5s linear !important;
  transition: all 0.5s linear !important;
}
#yt-masthead-container:hover {
  -webkit-transition: all 0.1s linear !important;
  -moz-transition: all 0.1s linear !important;
  -ie-transition: all 0.1s linear !important;
  -o-transition: all 0.1s linear !important;
  transition: all 0.1s linear !important;
  opacity: 1;
}
#yt-masthead {
  width: 100% !important;
  max-width: /*[[mywidth]]*/ !important;
  margin-left: auto !important;
  margin-right: auto !important;
  text-align: center !important;
  height: 32px;
  background: inherit;
}
#yt-masthead-user {
  position: absolute;
  top: 0;
  right: 0;
  opacity: 0;
  display: none !important;
  -webkit-transition: all 1s linear !important;
  -moz-transition: all 1s linear !important;
  -ie-transition: all 1s linear !important;
  -o-transition: all 1s linear !important;
  transition: all 1s linear !important;
}
#yt-masthead-container:hover #yt-masthead-user {
  -webkit-transition: all 0.25s linear !important;
  -moz-transition: all 0.25s linear !important;
  -ie-transition: all 0.25s linear !important;
  -o-transition: all 0.25s linear !important;
  transition: all 0.25s linear !important;
  opacity: 1;
  display: inline-block !important;
}
#yt-masthead-user:hover {
  z-index: 999;
  background: inherit;
}
#yt-masthead-container{
  padding:0 !important;
  border:0 !important;
}
#yt-masthead-content {
  width: 100% !important;
}
.yt-uix-button {
  word-break: normal !important;
  white-space: normal !important;
}
#masthead-search {
  width: 80%;
}
#masthead-search-terms {
  width: 90% !important;
}
#masthead-search-terms label {
  width: 100% !important;
}
#search-btn {
  width: 10% !important;
}
#watch-more-related-button {
  max-width: 100%;
}
.site-left-aligned #page.watch, .site-left-aligned #page.watch #guide, .site-left-aligned .watch7-playlist, .site-left-aligned #watch7-video, .site-left-aligned #watch7-main {
  margin-left: auto !important;
  margin-right: auto !important;
}
#page-container {
  margin-top: 3em;
}
#watch7-container {
  margin-top: -3em;
  max-width: /*[[mywidth]]*/;
  margin-left: auto !important;
  margin-right: auto !important;
}

.watch-medium .watch7-playlist-bar {
  width: 100%;
  margin-left: auto !important;
  margin-right: auto !important;
  left: auto !important;
  padding-left: 0 !important;
}
#watch7-main-container {
  width: 100% !important;
  height: auto !important;
  margin-left: auto !important;
  margin-right: auto !important;
  left: auto !important;
  padding-left: 0 !important;
}
#watch7-main {
  text-align: center;
  width: 100% !important;
  height: auto !important;
  margin-left: auto !important;
  margin-right: auto !important;
  left: auto !important;
  padding:0 !important;
  margin:0 !important;
}
#watch7-video-container {
  text-align: center;
  width: 100% !important;
  height: 100% !important;
  padding:0 !important;
  margin:0 !important;
  margin-left: auto !important;
  margin-right: auto !important;
}
#watch7-video {
  padding:0 !important;
  width: 100% !important;
  height: 100% !important;
}
#watch7-player {
  width: 100% !important;
  height: 0 !important;
  padding:0 !important;
  padding-bottom: -webkit-calc(55.6% + 30px) !important;
  max-height: 100%;
}


meta[itemprop="width"][content="1920"] ~ meta[itemprop="height"][content="1080"] ~ #watch7-video-container #watch7-player, 
meta[itemprop="width"][content="1366"] ~ meta[itemprop="height"][content="768"] ~ #watch7-video-container #watch7-player, 
meta[itemprop="width"][content="1280"] ~ meta[itemprop="height"][content="720"] ~ #watch7-video-container #watch7-player, 
meta[itemprop="width"][content="853"]  ~ meta[itemprop="height"][content="480"] ~ #watch7-video-container #watch7-player {
  padding-bottom: -webkit-calc(56.25% + 30px) !important;
  padding-bottom: -moz-calc(56.25% + 30px) !important;
  padding-bottom: -ie-calc(56.25% + 30px) !important;
  padding-bottom: -o-calc(56.25% + 30px) !important;
  padding-bottom: calc(56.25% + 30px) !important;
}

meta[itemprop="width"][content="1680"] ~ meta[itemprop="height"][content="1050"] ~ #watch7-video-container #watch7-player {
  padding-bottom: -webkit-calc(62.5% + 30px) !important;
  padding-bottom: -moz-calc(62.5% + 30px) !important;
  padding-bottom: -ie-calc(62.5% + 30px) !important;
  padding-bottom: -o-calc(62.5% + 30px) !important;
  padding-bottom: calc(62.5% + 30px) !important;
}

meta[itemprop="width"][content="1440"] ~ meta[itemprop="height"][content="1080"] ~ #watch7-video-container #watch7-player,
meta[itemprop="width"][content="960"]  ~ meta[itemprop="height"][content="720"]  ~ #watch7-video-container #watch7-player,
meta[itemprop="width"][content="640"]  ~ meta[itemprop="height"][content="480"]  ~ #watch7-video-container #watch7-player,
meta[itemprop="width"][content="480"]  ~ meta[itemprop="height"][content="360"]  ~ #watch7-video-container #watch7-player {
  padding-bottom: -webkit-calc(75% + 30px) !important;
  padding-bottom: -moz-calc(75% + 30px) !important;
  padding-bottom: -ie-calc(75% + 30px) !important;
  padding-bottom: -o-calc(75% + 30px) !important;
  padding-bottom: calc(75% + 30px) !important;
}

meta[itemprop="width"][content="1680"] ~ meta[itemprop="height"][content="1050"] ~ 
#watch7-video-container #watch7-player {
  padding-bottom: -webkit-calc(80% + 30px) !important;
  padding-bottom: -moz-calc(80% + 30px) !important;
  padding-bottom: -ie-calc(80% + 30px) !important;
  padding-bottom: -o-calc(80% + 30px) !important;
  padding-bottom: calc(80% + 30px) !important;
}

#movie_player, #watch7-player embed {
  top: 0 !important;
  left: 0 !important;
  width: 100% !important;
  height: 100% !important;
  position: absolute !important;
  max-height: 100% !important;
}

#watch7-content{
  text-align: left;
  width: 66.6% !important;
  display: inline-block !important;
  float: left!important;
  vertical-align: top;
  margin-left: auto !important;
  margin-right: auto !important;
}

#watch7-sidebar {
  text-align: left !important;
  width: 33.3% !important;
  display: inline-block !important;
  float: right!important;
  margin-left: auto !important;
  margin-right: auto !important;
}
#watch-description-clip,.action-panel-content{
  width: auto !important;
}
.search-header {
  margin-top: 34px;
}
.primary-col {
  width: 100% !important;
}
.yt-lockup2-thumbnail {
  display: inline-block;
}
.yt-lockup2-content {
  padding-left: 0 !important;
}
.yt-lockup2-meta {
}

#guide, #logo-container, #masthead-upload-button-group, #yt-hitchhiker-feedback, #upload-button-menu, .video-translation-links, #watch7-branded-banner  {
  display: none !important;
}
}
