css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http://www.tumblr.com/dashboard"), url-prefix(http://www.tumblr.com/tumblelog) , url-prefix("https://www.tumblr.com/dashboard"), url-prefix("https://www.tumblr.com/tumblelog") {
#container{width:1570px !important;margin:0 !important;padding:0px 10px !important}
#left_column{
float:left !important;
width:1300px !important;
margin: 0 0 0 -20px;
}
ol#posts li.post.new_post {
width:935px !important;
padding:0px 0px 0px 245px !important;
clear:both !important;
display:block;
}
ol#posts li.post {
float:left !important;
width:500px !important;
margin: 0 0 20px 100px !important;
}
ol#posts li.post img { height:auto !important; width: 70% !important; }
.post_content .image{ height:auto !important; width: 60px !important; }

.notification {
float:left !important;
width:514px;
margin: 0 0 0px 100px !important;
}
ol#posts li.notification.single_notification {
float:left !important;
width:514px;
margin: 0 0 20px 100px !important;
}
ol#posts li.notification.first_notification {
width:514px !important;
margin: 0 0 0px 100px !important;
}
ol#posts li.notification.last_notification {
width:514px !important;
margin: 0 0 20px 100px !important;
}
ol#posts li.notification.alt {
width:514px;
}
ol#posts li.post.same_user_as_last {
margin-right: 0px !important;
}
.autopagerize_page_info {
clear:both;
}
.autopagerize_page_separator {
clear:both;
}
ol#posts {
margin:0 !important;
}
#dashboard_controls {
padding-left:20px;
}
.answer_container {
width:470px !important;
clear:both;
display:block !important;
margin:0 !important;
}

#posts .post{ clear:none !important; }


}
