css:
@-moz-document domain(meetup.com) {


table.D_tabular tbody td {
padding:0.25em !important;
}


#C_googleAds {
visibility: hidden !important;
}



#C_pageBody {
max-width:1024px !important;
width: 1024px !important;
}

#C_context {
}

#C_document {
}

.D_box{
width:866px !important;
}

.D_sponsors{
display: none;
}

.D_actions {
font-size:0.9em !important;
}

.D_tabular thead th {
font-size:0.85em !important;
padding-right: 1.5em !important;
}

.D_checkColumn{
padding:2px !important;
}

.readState {
padding:2px !important;
}

.D_subject{
padding:4px !important;
max-width:310px !important;
}


.D_subject + td{
font-size:0.9em !important;
max-width:130px !important;
}

table.D_tabular tbody td {
overflow:hidden !important;
}

.posts{
max-width:50px !important;
}

.views {
max-width:65px !important;
}

.views + td{
padding-left:10px !important;
font-size:0.9em !important;
}

.views + td .D_nobr{
font-size:1em !important;
}



.C_contextNav
{
background-color: #036 !important;
}

.C_navBox ul
{
color: White !important;
-moz-border-radius:5px !important;
}

.C_navBox ul li a
{
background-color: #036 !important;
color: White !important;
text-decoration: none !important;
}

.C_navBox ul li.select a
{
background: #258 !important;
}

.C_navBox ul li a:hover
{
background-color: #369 !important;
color: #fff !important;
}


#C_org {
}

#C_nav iframe {
visibility: hidden !important;
}

.serendipity{
}

.serendipity ul li a {
background-color: transparent !important;
color: black !important;
}

#C_nav {
}

.organizer
{
border: 1px grey !important;
-moz-border-radius:5px !important;
background-color: white !important;
}

.assistants
{
background-color: white !important;
border: 1px grey !important;
-moz-border-radius:5px !important;
font-family:arial !important;
font-size: 10px !important;
}


#C_context {
}



.divby7 .spans5{
width:580px !important;
}

.divby7 .spans2{
width:286px !important;
padding:0 !important;
margin:0 !important;
}

.divby7 .last .D_colbody {
margin-left:0 !important;
}



.divby6 .spans4{
width:580px !important;
}

.divby6 .spans2{
width:286px !important;
padding:0 !important;
margin:0 !important;
}

.divby6 .spans2.date, .divby6 .spans2.date .first{
width:256px !important;
padding:0 !important;
margin:0 !important;
}

.divby6 .first .D_colbody{
width:100% !important;
}

.divby6 .first .D_colbody .D_box{
width:100% !important;
}

.divby6 .first .D_colbody .D_box .D_boxbody{
margin:0px !important;
padding:10px !important;
}





.spans4 .D_colbody .location{
width:130px !important;
}


.spans5 .D_colbody .D_box{
width:570px !important;
}

.divby2 .first{
width:100% !important;
}

.divby2 .last{
width:100% !important;
}

.divby2 .last .D_colbody .D_box{
width:570px !important;
}

.D_boxsection {
padding:1px !important;
}

.D_eventSummary{
width:100% !important;
}

.D_bbcode {
overflow:inherit !important;
}


.IE_lastCol .D_box{
width:100% !important;
}


.D_minorHeading {
padding-left:5px;
}

#attendeeList .D_actions {
margin:0 0 1em 1.5em !important;
}


/***************************/
/* alternate thread colors */
/***************************/

.thread{
background: #F4F4F4 !important;
}

.thread.tOdd{
background: #FFF !important;
}


/**********************/
/* post edited - font */
/**********************/

p.meta {
font-size:0.8em !important;
}


}
