css:
html body a {
  color: #666666;
}

html body .App_Rdio  .logo.rdio {
  background-size: 71px auto;
  background-image: url(data:image/png;base64,);
}

html body .App_Rdio  {
  background-color: #333 !important;
}

html body .App_Rdio  .App_Header {
  background-image: -webkit-gradient( linear, left top, left bottom, from(rgba(30,30,30,1)), to(rgba(41,41,41,.95)));
  box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.5);
  -webkit-box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.5);
}

html body .App_Rdio  .footer {
  background-image: -webkit-gradient( linear, left top, left bottom, from(rgba(41,41,41,.95)), to(rgba(30,30,30,1)));
  box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.5);
  -webkit-box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.5); 
}

html body .section_header {
  color: rgb(210,210,210);
  text-shadow: none;
}

html body .App_MainNav .section .Expander,
html body .App_PeopleSidebar .Expander,
html body .App_PeopleSidebar .Person {
  text-shadow: none; 
}
.Expander{
	color: #777
}

html body .App_MainNav li.selected a:hover {
  color: #555555;
}

html body .App_MainNav .scroll_content a:hover {
  color: white;
}

html body .Album .album_title {
  color: #008fd5;
}

html body .App_Header .user_nav a {
  color: #555;
}

html body .App_PlayerFooter .text_metadata .song_title {
  color: #999;
}
.metadata a{
	color: #555 !important
}
.name .truncated_line{
	color: #008fd5 !important
}
html body .Track .info .name a {
  color: #777;
}

html body .Profile_Queue_List .header {
  border: 0;
}

html body .SourceList .scrollable_content {
  border-top: none;
}

html body .TrackList .Track,
html body .Comments_Editor,
html body .Comments_Comment,
html body .Comments .critic_review {
  border-bottom: none;
}
.Comments{
	display: none;
}

html body .TrackList.boxed {
  border: 0;
}

html body .SourceList_SourceWithTracksWrapper .TrackList {
  border-top: 1px solid rgb(80,80,80);
}

html body .TrackList > .Track.selected {
  background: #444;
}

html body .SourceList_SourceWithTracksWrapper .TrackList {
  border-top: 0;
}

html body .catalog_header .catalog_name {
  color: #666666;
}

html body .tracklist_toolbar {
  text-shadow: none;
  border-bottom: 0;
}

html body .catalog_header {
  color: #555;
}

html body .App_Main .right_sidebar,
html body .App_Main .App_MainNav {
  background: #333;
  box-shadow: 0 0 10px #333 inset;
}

html body .App_Main .App_MainNav {
  margin: 0;
  padding: 0px 20px;
}

html body .App_Main .right_sidebar {
  margin: 0;
  padding: 0px 20px 0px 15px;
  width: 180px;
  margin-left: 15px;
}

html body .App_Main .app_main {
  background: rgb(65,65,65);
}

html body .App_Main .app_main > .Scrollable {
  padding: 0 5px 0 10px;
  margin-left: 240px;
  box-shadow: 0 0 10px #333 inset;
}

html body .Comments .critic_review .content,
html body .Comments_Comment p {
  color: #89949b;
  overflow-x: hidden;
}
.ListItem,.draggable,.ui-draggable{
	border: 0 !important;
	
}
.ui-draggable{
	box-shadow: none;
	border: none;
}
.ui-draggable{
	border: 0 !important;
	padding: 0;
	margin: 0
}
.small_multiline{
	border-top: 1px dotted #444 !important;
	border-bottom: 1px dotted #444 !important;
}
a.playlist{
	background: none;
}
a.playlist:hover,.browse a:hover,.your_music a:hover{
	background: #444444
}
.selected a{
	background: #098fd5;
	color: white !important;
}
.second_line{
	color: #555
}
.catalog_info,.catalog_info a,.playlist_authors {
	color: #555 !important
}
.index{
	color: #444 !important
}
.speaker{
	display: none !important;
}
.is_playing{
	background: #252525;
}
.list_page_search_box{
	background: #444444 !important;
	color: white;
	border: none;
}
input.unstyled, .search_box_container{
	color: #777 !important;
}
a.song_title,a.artist_title{
	color: white !important;
}
.track,.handle{
	background: #333 !important
}
.bar{
	background: #098fd5 !important
}
button.prev{
	color: aqua;

}
.ActionMenu .button{
	background: #353535;
	border-color: #333;
	text-shadow: none;
	color: #777
}
.has_icon .icon{
	text-shadow: none;
	opacity: .2 !important;
}
a.username_link{
	text-shadow: none;
	color: #777
}
.settings_button,.notifications_button,.people_sidebar_button{
	border-left: 0 !important;
	opacity: .2;
}
.duration{
	color: #777 !important
}
.ActionMenu .pill{
	display: none !important;
}
.pill,.SourceEditButtons,.PlayButton{
	opacity: .2;	
}
.left_controls,.repeat,.shuffle,.queue,.PeopleRow{
	opacity: .2;
}
