.Zec {
   border-top: 1px solid #d9371a;
   background: #e0141e;
   background: -webkit-gradient(linear, left top, left bottom, from(#e0141e), to(#e0141e));
   background: -webkit-linear-gradient(top, #e0141e, #e0141e);
   background: -moz-linear-gradient(top, #e0141e, #e0141e);
   background: -ms-linear-gradient(top, #e0141e, #e0141e);
   background: -o-linear-gradient(top, #e0141e, #e0141e);
   font-family: Tahoma !important;
   font-size:13px !important;
   text-decoration: none !important;
   color: white !important;
   margin: 5px 0 !important;
   padding: 7px 14px;
   -webkit-border-radius: 9px;
   -moz-border-radius: 9px;
   border-radius: 9px;
   -webkit-box-shadow: rgba(0,0,0,1) 0 1px 0;
   -moz-box-shadow: rgba(0,0,0,1) 0 1px 0;
   box-shadow: rgba(0,0,0,1) 0 1px 0;
   text-shadow: rgba(0,0,0,.4) 0 1px 0;
   color: white;
   font-size: 14px;
   font-family: Georgia, serif;
   text-decoration: none;
   vertical-align: middle;
   }
.Zec:hover {
   border-top-color: #28597a;
   background: #28597a;
   color: #ccc;
   }
.Zec:active {
   border-top-color: #1b435e;
   background: #1b435e;
   }
   
