css:
namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.google.), url-prefix("https://www.google."), url-prefix("http://news.google."), url-prefix("https://mail.google.com")
{
/* Background */
body, #cnt.big {
background: #fff url("http://i46.tinypic.com/15gsc54.jpg") fixed bottom left !important;
background-repeat:no-repeat !important;
background-position:right top !Important;
color: #fff !important;
}

/* Napis Google */
#lga:after {
	display:block;
	content:'Google';
	font:normal 136px/220px 'Trebuchet MS',serif;
	color:#fff;
	text-shadow: 0 1px 0 #ccc, 0 2px 0 #c9c9c9, 0 3px 0 #bbb, 0 4px 0 #b9b9b9, 0 5px 0 #aaa, 0 6px 1px rgba(0,0,0,.1), 0 0 5px rgba(0,0,0,.1), 0 1px 3px rgba(0,0,0,.3), 0 3px 5px rgba(0,0,0,.2), 0 5px 10px rgba(0,0,0,.25), 0 10px 10px rgba(0,0,0,.2), 0 20px 20px rgba(100,100,100,.15);
	-webkit-transform: scaleY(.85);
	-moz-transform: scaleY(.85);
	transform: scaleY(.85);
	position: absolute;
	position: absolute; top:10px; left:10%;
}

#lga div {
	padding:0 !important;
}

#prm, #hplogo,
#cpBackgroundImg {
	display:none !important;
}

#gbx3, #gbx4 {background-color:#4d6077 !important;border-bottom: 1px solid #3b495a !important;}
.gbm {z-index:9999 !important;}

#gb div#gbx1{background:transparent !important; border-color:transparent !Important;}


/* search form */ 
div#searchform {top:210px!important; min-width: 300px!important; max-width: 620px!important;}
form#tsf {min-width: 300px!important; max-width: 620px!important;}
div.tsf-p {padding-left: 110px !important; padding-right: 100px!important;}

/* Header */
#appbar, #rshdr.rshdr, #appbar > div[style*="border-bottom"], #extabar > #topabar > div {
background:transparent !important;
border:none !important;
}

/*Top Nav */

#hdtb > div {background:#bcb8b9 !important; border-color:transparent !Important;}
#top_nav > div {background:#bcb8b9 !important; border-color:transparent !Important;}


/* Left */
#leftnav, #leftnav #hidden_modes, #leftnav #hmp  {background:none !important;}
#leftnav li.mitem a:hover, #leftnav .mitem.msel .kls:hover, #leftnav #ms #showmodes:hover {background:#cccccc !important;}
#leftnav .lnsep {border-color:#5C5B5B !important;}


    div#leftnav { right: 0px !important; }
    #center_col { margin-left: 20px !important; }

/* Efekt wyszukiwania */ 
 .a, cite, cite a:link, cite a:visited, .cite, .cite:link, #mbEnd cite b, #tads cite b, #tadsb cite b, #ans > i, .bc a:link {
    color: #006633 !important;
    font-style: normal !important;
}

/* Wynik wyszukania */  
#resultStats, .ab_center_col {
    color: #333333 !important;
    font-size: 12px !important;
      line-height: 43px !important;
}


[id="lgpd"], 
div#footer.ctr-p,
div#logocont, /* google logo: form */
/*div#gbx1,  Przycisk wylogowania*/
div#pmolnk,
div#pmocntr2.pmoabs,
span#taw, /* ads at top of search results */
a#cp-sol,
div#prm,
div#rhscol, /* right sidebar, mostly ads */
div#botstuff, /* related searches: bottom */
p#bfl, /* search help, feedback: bottom */
div#fll, /* advertising programs, business solutions: bottom */
 /* change background image: bottom */
[id="abar_button_opt"] /* usuwa przycisk "opcje" */

/*REKLAMA BOCZNA */
table.iY > tr > td:first-child + td {
  display: none !important
}

table.iY > tr > td:first-child + td + td,

/*REKLAMA DOLNA i GÓRNA*/
div.Zs,
div.mq

{ display: none !important;
}
/*gmail informacje o działaniu*/
.acts {
    background-color: rgb(0,102, 51)!important;
}
.fs {
 
    background-color: rgb(102,102,102) !important;
}

.thead {
    background: none repeat scroll 0% 0% rgb(255,255, 51)!important;
    font-size: 80% !important;
    color: rgb( 51, 51, 51)!important;
}

}
