css:
@-moz-document url-prefix("https://apps.facebook.com/simcitysocial/") {
div#pagelet_bluebar,
div#rightCol
{
 display: none !important;
}

html#facebook,
body,
body>div._li,
div#globalContainer,
div#content,
div#content>div,
div#mainContainer,
div#contentCol,
div#contentArea,
div#pagelet_canvas_content,
div#app_content_225496380820004,
div#pagelet_iframe_canvas_content,
div#pagelet_iframe_canvas_content>div,
iframe#iframe_canvas,
div#contentCol>div#contentArea
{
 width: 100% !important;
 height: 100% !important;
 margin: 0px !important;
 padding: 0px !important;
 border: none 0px !important;
}

div#contentCol>div#contentArea
{
 display: block !important;
}

html#facebook
{
 overflow: scroll !important;
}

body
{
 width: 2800px !important;
 height: 1900px !important;
}
}

@-moz-document url-prefix("https://simcity.game.playfish.com/g/fb/simcity/") {
div#leaderboard_container,
div#console_container>div,
div#menu_bar_buttons>a,
div#menu_bar_buttons>div.game_counter_container_all>div
{
 display: none !important;
}

div#console_container>div#game_container,
div#menu_bar_buttons>div.game_counter_container_all>div#game_counter_simcitysocial,
div#console_container>div#menu_bar
{
 display: block !important;
}

div#console_container>div#menu_bar
{
 width: 0px !important;
 height: 0px !important;
 margin: 0px !important;
 padding: 0px !important;
 border: 0px !important;
}

div#game_counter_simcitysocial
{
 position: absolute !important;
 top: 0px !important;
 left: 0px !important;
 right: auto !important;
 bottom: auto !important;
 display: block !important;
 z-index: 100 !important;
}

html,
body#canvasPage,
div#content,
div#console_container,
div#game_container,
div#flashcontent,
object#simcity
{
 width: 100% !important;
 height: 100% !important;
 margin: 0px !important;
 padding: 0px !important;
 border: none 0px !important;
 display: block !important;
}
}
