css:
@-moz-document domain("thephotoforum.com") {
.postbody, .postrow, .eventbody, .postdetails, .userinfo, .postfoot {
        background:#93939A!important;
        color:#000004 !important;
}

.lightbox div {
        background: #43434A  !important;
        color:#000004 !important;
}

.quote_container, .after_content {
    color: black !important;
}

.forumrow, .activitybit, .blockbody {
      background:#D3D3DA !important;
      color:#000004 !important;
}

.bbcode_quote {
      background: #B3B3C8 !important;
}


.threadinfo, .threadlastpost {
      background-color:#C3C3CA  !important;
      color:#000004 !important;
}

.threadstats, .threadpostedin {
      background-color: #A3A3AA !important;
      color:#000004 !important;
}

a {
    color: #5888A9 !important;
}

.threadinfo a, .threadbit a,  .activitybit a, .blockbody a, .forumrow a {
    color: #206089 !important;
}

.postbody a, .postrow a, .eventbody a, .postdetails a, .userinfo a, .postfoot a {
    color:#185881  !important;
}

.button {
     background-color: #B3B3BA !important;
     background-image:none !important;
}
.button:hover {
     background-color: #C3C3CA !important;
     background-image:none !important;
}
}
