css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forums.penny-arcade.com") {
    /* Kill all the colors */
    body {
        background-color: #666666 !important;
    }
    #header, .pixels {
        background: #666666 !important;
        height: 110px !important;
    }
    #header h1,
    #header ul {
        display: none !important;
    }
        
    .ProfileOptions, .BeforeCommentHeading, .PageControls.Top {
        top: 165px !important;
    }
    #Frame, #FootCrumbs {
        background: #ffffff !important;
    }
    #Foot, #footer {
        display: none;
    }
    a,
    .Number,
    .SpoilerTitle input,
    #Panel .About dl dt
    {
        color: #000000 !important;
    }
    .Message a {
        color: #0055A2 !important;
    }
    .PageTitle h1,
    .CommentForm h2,
    .HomepageTitle,
    .Box h4,
    .H
    {
        background: none !important;
        border: none !important;
        box-shadow: none !important;
        color: #000000 !important;
        text-shadow: none !important;
    }
    .DataTable thead td {
        background: none !important;
        color: #000000 !important;
        text-shadow: none !important;
        border: none !important;
    }
    .CategoryName, .DiscussionName {
        background: none !important;
    }
    textarea:focus, input:focus,
    .Inputbox input:focus{
        background: #f6f6f6 !important;
    }
    .Button, .NavButton, .ProfileButtons,
    .PageControl a:not(.Previous),
    .HasNew {
        background: #dddddd !important;
        color: #000000 !important;
        text-shadow: none !important;
        box-shadow: none !important;
    }
    .PageControl a.Highlight {
        background: #aaaaaa !important;
    }
    .Previous, .Next, .Ellipsis, .Elipsis {
        background-color: #dddddd !important;
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale") !important; /* Firefox 10+, Firefox on Android */
        -webkit-filter: grayscale(100%) !important; /* Chrome 19+, Safari 6+, Safari 6+ iOS */
    }
    .Bookmark {
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale") !important; /* Firefox 10+, Firefox on Android */
        -webkit-filter: grayscale(100%) !important; /* Chrome 19+, Safari 6+, Safari 6+ iOS */
    }
    .UserSpoiler {
        background: none !important;
    }
    code, pre {
        background: #eeeeee !important;
        border: 1px solid #aaaaaa !important;
    }
    .Popup .Body {
        background: #ffffff !important;
    }
    .OptionsTitle {
        background: none !important;
        width: 50px !important;
        font-size: 12px !important;
        line-height: inherit !important;
    }
    
    /* Remove avatars except on mouseover */
    .PhotoWrap {
        display: none !important;
        background: none !important;
        width: auto !important;
    }
    .PhotoWrap:before {
        background: none !important;
    }
    .ProfilePhoto {
        background: none !important;
    }
    .Author:hover .PhotoWrap {
        display: block !important;
    }
    .AuthorInfo {
        display: block !important;
        clear: both !important;
    }
    
    /* Remove signatures */
    .Signature {
        display: none !important;
    }
    
    /* Shrink images/videos except on mouseover */
    .bbcode_img, .Excerpt > img {
        height: 50px !important;
        filter: url("data:image/svg+xml;utf8,<svg xmlns=\'http://www.w3.org/2000/svg\'><filter id=\'grayscale\'><feColorMatrix type=\'matrix\' values=\'0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0.3333 0.3333 0.3333 0 0 0 0 0 1 0\'/></filter></svg>#grayscale") !important; /* Firefox 10+, Firefox on Android */
        -webkit-filter: grayscale(100%) !important; /* Chrome 19+, Safari 6+, Safari 6+ iOS */
    }    
    .bbcode_img:hover, .Excerpt > img:hover {
        height: auto !important;
        filter: none !important;
        -webkit-filter: none !important;
    }
    .VideoPreview img {
        height: 50px !important;
        width: 50px !important;
    }
    .VideoPreview:hover img {
        height: auto !important;
        width: auto !important;
    }
    
    
    /* Remove icons */
    .ReactSprite {
        background: none !important;
    }
    
    /* Shrink big text */
    .Username {
        font-size: 12px !important;
    }
    
    /* Remove more fluff */
    .Permalink {
        background: none !important;
    }
    .Permalink time:before {
        border: none !important;
    }
}
