css:
@-moz-document url-prefix("http://www.deviantart.com/submit/") {
/* -- ADD FILES BUTTON -- */
.stash-upload #stash-form .smbutton {
  background: url("http://fc00.deviantart.net/fs71/f/2013/024/a/a/bigblue_grad_by_miontre-d5smdig.png") repeat-x #005E7E !important;
  border-radius: 4px !important;
  border: #1D4253 1px solid !important;
  box-shadow: 0px 1px 0px rgb(0, 184, 211) inset !important;
  }

.stash-upload:hover #stash-form:hover .smbutton {
  background: url("http://fc03.deviantart.net/fs70/f/2013/024/0/d/bigblue_hover_grad_by_miontre-d5smdv9.png") repeat-x #00749B !important;
  border-radius: 4px !important;
  border: #1D4253 1px solid !important;
  box-shadow: 0px 1px 0px rgb(0, 184, 211) inset !important;
  }

.stash-upload .smbutton span {
  color: #FFFFF9 !important;
  text-shadow: none !important;
  font-weight: normal !important;
  border-top: #00B8D3 1px solid !important}


/* -- PAGE COLOURS -- */
#ile-container, #output #ile-contents .bubbleview {background-color: #D6DED4 !important;}
body div#output #not-overhead #overhead-collect {background: url("http://fc04.deviantart.net/fs70/f/2013/024/1/2/overhead_grad_by_miontre-d5sme2d.png") repeat-x #B2C3B0 !important;}
html {background-color: #D4DFD0 !important;}


/* -- FILE CONTAINER -- */
.oh-stashtitle, .oh-stashlogo-name, .oh-stashsubmit {color: #3E4D49 !important;
  font-family: Trebuchet MS, sans-serif !important;
  letter-spacing: 0px !important;
  font-size: 15pt !important;}

#ile-container .ile-heading, #ile-container h1 {color: #3E4D49 !important;
  font-family: Trebuchet MS, sans-serif !important;
  font-size: 13pt !important;
  margin-bottom: 3px !important;}

.stash-upload-box {background-color: #BFCEC3 !important;
  border: none !important;
  box-shadow: none !important;
  padding: 24px !important;
  margin-bottom: 28px !important;}

#ile-container .ile-section-preview {background-color: #BFCEC3 !important;
  border: none !important;
  box-shadow: none !important;
  padding: 24px !important;
  padding-bottom: 12px !important;
  margin-bottom: 28px !important;}

#ile-container .ile-preview {padding: 0 !important;}

.stash-upload-box .modal-links {margin-bottom: 0 !important;}

body div#output #dv7 .stash-upload-box .instruction, body div#output #dv7 .stash-upload-box .instruction-nodrag {color: #333D3C !important;}

body div#output #dv7 .stash-upload-box .instruction.or-text {color: #465250 !important;}

.stash-upload-box .edit-modal-link, .stash-upload-box .stash-modal-link {color: #2C3635 !important;}

#ile-contents input.ile-title-input, #ile-contents textarea.ile-description-input, #ile-contents .ile-keywords .ile-keywords-inner {box-shadow: none !important;
  border: #ADBAB1 1px solid !important;
  border-radius: 0px !important;}

#ile-contents input.ile-title-input {margin-bottom: 14px !important;}

body.stash-page #ile-contents div.ile-description a.ile-preview-description, #ile-contents .ile-description .ile-preview-description {background-image: none !important; margin-bottom: 4px !important;}

body.stash-page #ile-contents div.ile-description a.ile-preview-description.close, #ile-contents .ile-description .ile-preview-description.close {color: #383838 !important;}

body.stash-page #ile-contents div.ile-description a.ile-preview-description, #ile-contents .ile-description .ile-preview-description:hover {color: #383838 !important;}

#ile-contents .previewcontainer .innerpreviewcontainer {box-shadow: none !important;
  border: #ADBAB1 1px solid !important;
  background: #FFFFFF !important;}

#ile-contents .previewcontainer .innerpreviewcontainer > span {font-family: Trebuchet MS, sans-serif !important;
  font-size: 9pt !important;
  margin-bottom: 4px !important;}


/* -- DUPLICATE BUTTON -- */
#oh-stashbacktab, .stash-page #overhead-collect #overhead tbody tr td.f, #overhead tbody tr td.f #overhead-collect #overhead tbody tr td.oh-stashinactivetab {height: 38px !important;}


/* -- DROPDOWN MENUS -- */
.popup2-white div.blockmenu a:hover,
.popup2-white div.blockmenu .selected span,
.popup2-white div.blockmenu .selected,
.popup2-white div.blockmenu .stash-file-invisible:hover + a.f,
.popup2-white div.blockmenu .stash-file-invisible:hover + a.f span{
    color: #FFF !important;
    background: url("http://fc02.deviantart.net/fs70/f/2013/024/9/3/hovermenu_grad_by_miontre-d5smenp.png") repeat-x #39798D !important;
    text-shadow: none !important;
    outline: none !important;}

.popup2-white div.blockmenu a:hover span {background: transparent !important; color: #FFF !important; text-shadow: none !important;}

body div.popup2-white {background: #E8ECE6 !important;
  border: #FFFFFF 2px solid !important;
  outline: none !important;
  border-radius: 0px !important;
  box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.2), 0px 0px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.2), 0px 0px 0px 1px #52695C !important;}

.popup2-white .blockmenu a.f span {color: #2C3635 !important;}

div.pager2 a.f, div.pager2 span.f {font-size: 9pt !important;}

#ile-contents .ile_mod .smbutton.smbutton-white.smbutton-stash-white, #ile-contents .ile_mod .smbutton.smbutton-white.smbutton-stash-white.popup-open {background: #E8ECE6 !important;
  border: #FFFFFF 2px solid !important;
  outline: none !important;
  border-radius: 0px !important;
  box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.2), 0px 0px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.2), 0px 0px 0px 1px #52695C !important;}

body .smbutton-white span {color: #2C3635 !important; font-size: 9pt !important;}


/* -- MATURE BUTTON --*/
#ile-container .ile-mature .ile-preview-option {background: #DAE4D9 !important; border-color: #89A08E !important;}


/* -- CATEGORY SELECTOR -- */
div.modal #modal-form-category input.isearch, div.inline-category-picker #modal-form-category input.isearch {box-shadow: none !important;
  border: #ADBAB1 1px solid !important;
  border-radius: 0px !important;}

#modal-form-category div.pager-light div.page2 a.more-selected {background: url("http://fc02.deviantart.net/fs71/f/2013/024/0/6/catpick_grad_by_miontre-d5smf4g.png") repeat-x #485A4F !important;}

#modal-form-category a.f.more {background: url("http://fc06.deviantart.net/fs70/f/2013/024/0/a/catpick_arrows2_by_miontre-d5smca3.png") 100% -10px no-repeat !important;
}



#modal-form-category div.pager-light div.page2 a.selected {background: url("http://fc02.deviantart.net/fs71/f/2013/024/0/6/catpick_grad_by_miontre-d5smf4g.png") repeat-x #485A4F !important;}

#modal-form-category div.pager-light div.page2 a.selected, #modal-form-category div.pager-light div.page2 a.more-selected, .editmode div#ile-contents #modal-form-category div.pager-light div.page2 a.selected, .editmode div#ile-contents #modal-form-category div.pager-light div.page2 a.more-selected {border-color: #415248 !important;}

#modal-form-category div.pager-light div.page2 a.f:hover {color: #3B5A4A !important;}

.ile-section-category .cat-preview {border-color: #ADBAB1 !important;}

#modal-form-category div.pager-light div.page2 a.more-selected:hover {color: white !important;}


/* -- OPTIONS POPDOWNS -- */
#ile-container .ile-section-options a.ile-options-button.depressed {box-shadow: 0px 3px 18px 0px #A1ADA1 inset !important;
  border-color: #89A08E !important;
  border-bottom: none !important;}

.ile-options-popdown {
  background-color: #C1CEC1 !important;
  border-color: #89A08E !important;}

.ile-publish-section .ile-publish-bubble {background-color: #D6DED4 !important;
  box-shadow: none !important;
  border: none !important;}

#ile-container .ile-section-options a.ile-options-button.depressed span {border: none !important;}

#ile-container .ile-section-options a.ile-options-button {height: auto !important;}

#ile-container .ile-section-options a.ile-options-button.has-title span.subtext {color: grey !important;
  font-size: 8pt !important;}

#ile-container .ile-section-options a.ile-options-button.depressed span {color: #2E302E !important;}

#ile-container .ile-section-options a.ile-options-button.has-title > span {font-size: 9pt !important;}

#ile-container .ile-section-options a.ile-options-button > span, #ile-container .ile-section-options a.ile-options-button.has-title > span {padding: 8px !important;}

#ile-container .ile-section-options a.ile-options-button {box-shadow: none !important;
  border-color: #89A08E !important;}

#ile-container .ile-section-options a.ile-options-button > span {color: #2E302E !important;}

.ile-license-option {color: #2E302E !important;}


/* -- HEADER BUTTONS -- */
.smbutton-white {background: url("http://fc00.deviantart.net/fs70/f/2013/024/a/e/whiteheaderbutton_grad_by_miontre-d5smijr.png") repeat-x #D7DED5 !important;
  border: #909E91 1px solid !important;}

.smbutton-white:hover {background: url("http://fc04.deviantart.net/fs71/f/2013/024/9/9/whiteheaderbutton_hover_grad_by_miontre-d5smiuc.png") repeat-x #E1E6E0 !important;}

td.oh-ile-submit-nav-buttons .ile-submit-button {background: url("http://fc08.deviantart.net/fs71/f/2013/024/a/5/greenheaderbutton_grad_by_miontre-d5smj28.png") repeat-x #B2E040 !important;}

td.oh-ile-submit-nav-buttons .ile-submit-button:hover {background: url("http://fc09.deviantart.net/fs70/f/2013/024/1/d/greenheaderbutton_hover_grad_by_miontre-d5smji1.png") repeat-x #C0EB2F !important;}

td.oh-ile-submit-nav-buttons .ile-submit-button span {border-top: #F1F9D0 1px solid !important;}


/* -- FOOTER BUTTONS -- */
#ile-container .ile-section-footer-buttons a.ile-save-exit-button, #ile-container .ile-section-footer-buttons a.ile-go-back-button, #ile-container .ile-section-footer-buttons a.ile-submit-button.smbutton-white, #ile-container .ile-section-footer-buttons a.ile-continue-button.smbutton-white {border-color: #909E91 !important;}

#ile-container .ile-section-footer-buttons a.ile-submit-button.smbutton-white {background: url("http://fc08.deviantart.net/fs70/f/2013/024/7/f/footersubmit_grad_by_miontre-d5smjst.png") repeat-x #B2E040 !important;}

#ile-container .ile-section-footer-buttons a.ile-submit-button.smbutton-white:hover {background: url("http://fc07.deviantart.net/fs71/f/2013/024/b/f/footersubmit_hover_grad_by_miontre-d5smjz6.png") repeat-x #C0EB2F !important;}


/* -- FOOTER LINKS -- */
.ile-section-agreements, .ile-section-remember {border-color: #89A08E !important;}

.ile-section-agreements label a {color: #33759F !important;}


/* -- FEATURES -- */
#ile-container .ile-feature {background: #C1CEC1 !important;
  box-shadow: none !important;
  border-color: #89A08E !important;}

#ile-container .ile-feature.disabled {background: #E4EAE3 !important;}

#ile-container .ile-feature:hover {box-shadow: none !important; border: 2px solid #498091 !important; padding: 9px !important;}

#ile-container .ile-feature .ile-feature-title {font-family: Trebuchet MS, sans-serif !important;
  margin-left: 14px !important;}

#ile-container .ile-feature-premium-content .ile-feature-items, #ile-container .ile-feature-downloads .ile-feature-downloads-items {box-shadow: none !important;
  background: #D6DED4 !important;
  border: none !important;}

#ile-container .ile-feature-downloads.disabled .ile-feature-downloads-items {box-shadow: none !important;
  background: #EFF5ED !important;
  border: none !important;}

#ile-container .ile-feature-premium-content .ile-feature-item, #ile-container .ile-feature-downloads .ile-feature-downloads-item {color: #0D142E !important;}

#ile-container .ile-feature-print-submission .ile-feature-autoprint-box, #ile-container .ile-feature-print-submission .ile-feature-autoprint-box-message, #ile-container .ile-feature-premium-content .ile-feature-premium-content-message {border-top: #89A08E 1px dotted !important;}


/* -- STEP No -- */
.oh-step .box {border-color: #848783 !important;}


/* -- PC PAGE INTRO BOX -- */
.ile-publish-section .ile-publish-bubble {background: #BFCEC3 !important;}

.ile-publish-paid-downloads-attachment {background: #D6DED4 !important;}

.ile-publish-paid-downloads-attachment > .attachment-edit > span {color: black !important;}
.ile-publish-paid-downloads-attachment > .attachment-edit {border-color: #859088 !important; border-radius: 5px !important; background: #D2DDD5 !important;}

.ile-attachment-edit-popup {background: #E8ECE6 !important;
  border: #FFFFFF 2px solid !important;
  border-radius: 0px !important;
  outline: none !important;
  box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.2), 0px 0px 2px rgba(0, 0, 0, 0.2), 0px 1px 1px rgba(0, 0, 0, 0.2), 0px 0px 0px 1px #52695C !important;}

.ile-attachment-edit-popup-item {border: none !important;}
.ile-attachment-edit-popup-item:first-child {border-radius: 0px !important;}

.ile-attachment-edit-popup-item:hover {background: url("http://fc02.deviantart.net/fs70/f/2013/024/9/3/hovermenu_grad_by_miontre-d5smenp.png") repeat-x #39798D !important;}

.ile-attachment-edit-popup-item > span {color: black !important;}

.ile-publish-paid-downloads .stash-upload-container .smbutton span, .ile-publish-paid-downloads-setterms .intro-text a.terms-what-is {color: #33759F !important; text-decoration: none !important;}
.ile-publish-paid-downloads .stash-upload-container .smbutton span:hover, .ile-publish-paid-downloads-setterms .intro-text a.terms-what-is:hover {text-decoration: underline !important;}

.ile-publish-section .ile-feature-title {font-family: Trebuchet MS, sans-serif !important;}


/* -- PC PAGE LICENSE SELECT -- */
#ile-container div#ile-contents .ile-publish-paid-downloads-setterms .terms-option.selected a.terms-option-title {box-shadow: none !important;
  background: #C1CEC1 !important;
  border: 2px solid #498091 !important;
  color: black !important;
  text-shadow: none !important;}

.ile-publish-paid-downloads-setterms .terms-option .terms-option-title {background: transparent !important;
  border: 1px #89A08E solid !important;}

.ile-publish-paid-downloads-setterms .terms-options {border: none !important; background: #E4EAE3 !important;}

.ile-publish-paid-downloads-setterms .terms-option.first .terms-option-title {border-top: 1px #89A08E solid !important; border-bottom: none !important;}

.ile-publish-paid-downloads-setterms .terms-option.last .terms-option-title {border-top: none !important;}

.ile-publish-paid-downloads-setterms .terms-option .terms-option-title, .ile-publish-paid-downloads-setterms .terms-option .terms-option-info {color: #33759F !important;}

.ile-publish-paid-downloads-setterms .terms-option .terms-option-info {background: transparent !important; box-shadow: none !important; border: 1px #89A08E solid !important; border-left: none !important;}

.ile-publish-paid-downloads-setterms .terms-option.first .terms-option-info {box-shadow: none !important; border: 1px #89A08E solid !important; border-left: none !important;}

.ile-publish-paid-downloads-setterms .terms-option.last .terms-option-info {box-shadow: none !important; border: 1px #89A08E solid !important; border-top: none !important; border-left: none !important;}


/* -- PC PAGE LICENSE -- */
.ile-publish-paid-downloads-setterms .terms-content .terms-type {border-color: #89A08E !important; background: #E8EDE9 !important;}


/* -- PC PAGE PRICE SELECT -- */
.ile-publish-paid-downloads-setprice .price-label {font-family: Trebuchet MS, sans-serif !important;}

.ile-publish-paid-downloads-setprice .price-denominations #price-currency-earnings {color: #29A314 !important;}


/* -- PRINTS PAGE BOXES -- */
.bubbleview .altview {background: #BFCEC3 !important; border: none !important;}


/* -- PRINTS PAGE TABS -- */
div.productTab div.middlePrintTab {background: #DAE4D9 !important; border-color: #C2D0BF !important;}

div.productTabSelected div.middlePrintTab {background: #BFCEC3 !important; border: none !important;
  color: #33759F !important;}


/* -- PRINTS PAGE BUTTONS -- */
a.AdjustImageButton, #submit input[type="button"], #submit input[type="submit"], #submit input[type="reset"], #submit input[type="file"], #submit button, #modalspace input[type="submit"], #modalspace input[type="button"], #submit input[type="button"]:active, #submit input[type="submit"]:active, #submit input[type="reset"]:active, #submit input[type="file"]:active, #submit button:active, #modalspace input[type="submit"], #modalspace input[type="button"], a.AdjustImageButton:active {
  background: url("http://fc08.deviantart.net/fs71/f/2013/024/9/7/printsbutton_grad_by_miontre-d5smkm1.png") repeat-x #BDD1B4 !important;
  border-color: #93A98F !important;
  font-family: Trebuchet MS, sans-serif !important; font-size: 9pt !important;
  color: #2C2E2C !important;
  text-shadow: none !important;
  box-shadow: 0 1px #EBF1E6 inset !important;}

a.AdjustImageButton:hover, #submit input[type="button"]:hover, #submit input[type="submit"]:hover, #submit input[type="reset"]:hover, #submit input[type="file"]:hover, #submit button:hover, #modalspace input[type="submit"]:hover, #modalspace input[type="button"]:hover {background: url("http://fc08.deviantart.net/fs71/f/2013/024/f/5/printsbutton_hover_grad_by_miontre-d5smksb.png") repeat-x #C2D5B9 !important;}


/* -- PRINTS PAGE INPUT -- */
input.itext, textarea.itext, textarea#printdesc {box-shadow: none !important; border-color: #ADBAB1 !important; border-radius: 0px !important;}


#ile-container .ile-section-print-submission-content a, #submit a.u {color: #33759F !important;}
#submit a.u:hover {color: #33759F !important;}


/* -- IMAGES -- */
#not-overhead .oh-submiticon {background-image: url("http://fc07.deviantart.net/fs71/f/2013/024/1/0/submit_uploadarrow_by_miontre-d5sm6tw.png") !important;
  background-repeat: no-repeat !important;
  background-position: 8px 5px !important;}
  
.ile-publish-paid-downloads-setprice .price-icon {background-color: transparent !important;}
}
