css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("metafilter.com") {
    html { 
        margin: 0 !important;
        padding: 0 10px !important;
        background-color: #eee !important;
        background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAYAAAAGCAYAAADgzO9IAAAABGdBTUEAALGPC/xhBQAAAAlwSFlzAAAOwgAADsIBFShKgAAAABp0RVh0U29mdHdhcmUAUGFpbnQuTkVUIHYzLjUuMTAw9HKhAAAARUlEQVQYV02OyQ3AAAjDGJ15udmg8oO2jygihgjZXUUzo1WlEaHurkKAulsz8wNsEeLooHDKcOBc6PtDrqkWM3sB4T3zAMg2dle3kJw9AAAAAElFTkSuQmCC) !important;
    }
    
    body {
        max-width:1000px !important; /* Shouldn't be set to lower than 680px */
        margin:0 auto !important;
        padding:0 !important;
        background: transparent !important;
        color: #000 !important;
        position: relative !important;
        font: 12px/16px Verdana, sans-serif !important;
    }
    
    .copy, .smallcopy {
        color: #000 !important;
    }
    
    #logo,#yellowbar,.welcome { display:none !important; }
    
    .mefimessages {
        position: absolute !important; 
        top:0 !important;
        right:0 !important;
    }

    .mefimessages a, 
    .mefimessages a:visited, 
    .mefimessages a:active, 
    .mefimessages a:hover {
        color: #fff !important;
    }
    
    #maintab {
        border: none !important;
        text-align: left !important;
        margin: 0 !important;
        padding:0 !important;
    }
    #maintab a {
        border: none !important;
        background: transparent !important;
    }
    #maintab .selected a, 
    #maintab a:hover {
        text-decoration: underline !important;
    }
    
    #page {
        position: relative !important;
    }
    
    #page #menu, 
    #page .side, 
    #sidemenu {
        position:absolute !important;
        top: 0 !important;
        right: 0 !important;
        margin:0 !important;
        width: 180px !important;
        float: none !important;
        font-size: 0.9em !important;
    }
    
    #sidemenu .sidebar {
        width: 180px !important;
        margin:0 !important;
        padding:5px 0 0 0 !important;
    }
    
    #sidemenu .sidebar>* {
        padding: 0 5px !important;
    }
    
    
    #posts, 
    p.monthday+div>div {
        margin-right: 200px !important;
    }
    
    p.monthday+div>div:first-child {
        margin-right: 0 !important;
        width: 180px !important;
        font-size: .9em !important;
    }
    
    #posts>h2, 
    #scontent>h2 {
        color: #000 !important;
        font-size: 20px !important;
        margin:10px 0 !important;
        padding:0 !important;
    }
    
    #posts .posttitle, h1.posttitle {
        color: #000 !important;
        margin:0 !important;
        padding: 7px !important;
        border-top-left-radius: 3px !important;
        border-top-right-radius: 3px !important;
        font-size: 20px !important;
        line-height: 120% !important;
        /*font-family: 'Averia serif', Averia, serif !important;*/
    }
    
    #posts .posttitle a {
        color: #000 !important;
    }
    
    .posttitle .smallcopy {
        line-height:20px !important;
    }
    
    .post, .copy, 
    #page .copy, 
    #page .comments { 
        text-align: justify !important;
        margin: 0 !important;
        color: #222 !important;
        padding: 5px 7px !important;
        border-radius: 3px !important;
    }
    #page .posttitle+.copy {
        border-top-left-radius: 0px !important;
        border-top-right-radius: 0px !important;
    }
    
    .copy>div {
        border: none !important;
        margin: 0 !important;
        padding:0 !important;
    }
    
    #page .comments { 
        margin: 0 15% !important;
    }
    
    .comments+br+br {
        display:none !important;
    }
    
    .post .smallcopy:last-child, .comments .smallcopy:last-child, .copy .smallcopy:last-child {
        color: #333 !important;
        border-top: 1px dashed #fff !important;
        display:block;
        margin-top:5px !important;
        padding-top:2px !important;
        padding-left:10px !important;
    }
    .new:not(a) {
        color: #333 !important;
    }
    
    .tags {
        background-color: transparent !important;
        text-align: right !important;
        color: #000 !important;
        width: 105px !important;
    }
    
    .tags+br {
        display:none !important;
    }
    
    #topline {
        line-height: 20px !important;
        border-top: 22px solid #888 !important;
        text-align:left !important;
    }
    
    #navglobal {
        margin:0 !important;
        padding: 0 !important;
        width: 100% !important;
        border-top: 1px solid rgba(255,255,255,.3) !important;
        border-bottom: 1px solid rgba(255,255,255,.3) !important;
    }

    #navglobal li a { 
        display: inline-block !important;
        margin: 0 !important;
        padding: 3px 10px 4px 10px !important;
        color: #fff !important;
    }
    
    #navglobal a:hover, 
    #bottomline a:hover, 
    .mefimessages a:hover { 
        color: rgba(255,255,255,.7) !important; 
    }
    
    #navglobal a[href='http://podcast.metafilter.com/'] { display: none !important; }
    
    #bottomline {
        margin:0 !important;
        padding:5px !important;
        height: 32px !important;
        border-bottom-left-radius: 3px !important;
        border-bottom-right-radius: 3px !important;
    }
    
    #navseldom+br { clear:left !important; }
    
    #topline, #bottomline {
        background-color: rgba(128,128,128,.1) !important;
    }
    
    #topline a, #bottomline a {
        color: #fff !important;
    }
    
    #search { width: 120px !important; }
    #search input[type='submit'] { display: none !important; }
    
    blockquote { 
    	margin:1em .5em !important; 
    	padding-left:5px !important; 
    	border-left:3px solid #888888 !important; 
    }
    
    
    /* Footer */ 
    #footer { border-top-right-radius: 3px;border-top-left-radius: 3px; padding-bottom:5px !important; border:none !important; margin-top:2em !important;}
    #footer * {color:#fff !important;}
    #footer>div { display:none !important; }
    #footer>div+div+div+div { display:inline !important; width:100% !important; text-align:right !important; padding:0 !important; margin:8px 0 3px 0 !important;}
    #footer div { background-color:transparent !important; border:none !important; }
    #footer .footpad { padding:0 !important; margin:0 10px 0 0 !important;}
    #footer .footpad * { display: inline !important; }
    #footer .footpad form { display:none !important; }
    #footer .footpad br { display:none !important; }
    #footer .footpad strong { color: #dcdcdc !important; }
    
    .fineprint>br  { display:none !important; }
    .fineprint { padding-left:1em !important; }
    .fineprint a { font-weight: bold !important; }

    .hglt { color: #fff !important; }
    
    input, textarea { background-color: #fff !important; border: 1px solid #aaa !important; }
    
    #page p.copy.whitesmallcopy {
        margin: 0 15% !important;
    }
}

@-moz-document domain("www.metafilter.com") {
    /* ORIGINAL: DARK: 004D73 LIGHT: 006699 */
    #topline, blockquote {
        border-color: #006699 !important;
    }
    #navglobal, #footer  { background-color: #004D73 !important; }
    #topline, #bottomline { background-color: #006699 !important; }
    
    .sidebar { background-color: rgba(0,77,115,.1) !important; }
    .post, .copy, #page .copy, #page .comments, .posttitle {
        background-color: rgba(0,102,153,.1) !important;
    }
    
    .tags {
        background-color: #D6E0E6 !important;
    }
    
    a { color: #006699 !important; }
    a:visited { color: #004D73 !important; }
    a:hover { color: #4D839D !important; }
    
    .vid img { visibility: hidden !important; }    
    .vid { background-image: url(data:image/png;base64,) !important; }
    #triangle { border-color: transparent transparent transparent #006699! important; }
    
    
    /* USERPAGE */
    .copy.usertable {
        background-color: transparent !important;
    }
    
    /* FAVORITES PAGE */
    div[id^='fav'] { margin:2em 0 !important; }
    div[id^='fav'] h1 a[href^='http://www'] { color: #006699 !important; }
    div[id^='fav'] h1 a[href^='http://ask'] { color: #426746 !important; }
    div[id^='fav'] h1 a[href^='http://projects'] { color: #9F0005 !important; }
    div[id^='fav'] h1 a[href^='http://metatalk'] { color: #666666 !important; }
    
    /* POPULAR FAVORITES */
    div[style='width:49%;float:left;']+div[style='width:49%;float:left;'] { float:right !important;}
}

@-moz-document domain("ask.metafilter.com") {
    /* ORIGINAL: DARK 213423 LIGHT 426746 */
    #topline, blockquote { border-color: #426746 !important; }
    #navglobal, #footer  { background-color: #213423 !important; }
    #topline, #bottomline { background-color: #426746 !important; }

    .sidebar { background-color: rgba(33,52,35,.1) !important; }
    .post, .copy, #page .copy, #page .comments, .posttitle { 
        background-color: rgba(66,103,70,.1) !important; 
    }
    a { color: #426746 !important; }
    a:visited { color: #213423 !important; }
    #page .best { background-color: rgba(66,103,70,.35) !important; }
}

@-moz-document domain("projects.metafilter.com") {
    /* ORIGINAL: DARK 661100 LIGHT 911F08 */
    #topline, blockquote { border-color: #911F08 !important; }
    #navglobal, #footer  { background-color: #661100 !important; }
    #topline, #bottomline { background-color: #911F08 !important; }

    #menu { background-color: rgba(159,0,5,.1) !important; }
    .post, .copy, #page .copy, #page .comments, .posttitle { background-color: rgba(159,0,5,.1) !important; }
    a,a:visited { color: #9F0005 !important; }
    
    /* VOTE BOXES */
    .votenumber, .voteforit {
        background-color: rgba(159,0,5,.1) !important;
        border-radius: 3px !important;
    }
    
    .votenumber a {
        background-color: transparent !important;
    }
    
    /* COMMENTS BY POSTER */
    .bestleft {
        border-color: #911F08 !important;
    }
    
    /* POSTED TO THE BLUE */
    .post span[style='background-color:#1e4242;padding:2px;margin-bottom:5px;'] {
        background-color: transparent !important;
    }
    .post span[style='background-color:#1e4242;padding:2px;margin-bottom:5px;'] a {
        color: #006699 !important;
    }
}

@-moz-document domain("music.metafilter.com") {
    /* ORIGINAL: DARK 222222 LIGHT 333333 */
    /* USED: DARK 222222 LIGHT 444444 */
    #topline, blockquote { border-color: #444444 !important; }
    #navglobal, #footer  { background-color: #222222 !important; }
    #topline, #bottomline { background-color: #444444 !important; }

    .post, .copy, #page .copy, #page .comments, .posttitle { background-color: rgba(68,68,68,.1) !important; }
    a,a:visited { color: #444444 !important; }
    
    #boxy { width: 100% !important; }
    #boxyleft { width: 60% !important; }
    #boxyright { width: 310px !important; float:right !important;}
    #boxyright ol { list-style:none !important; }
}

@-moz-document domain("jobs.metafilter.com") {
    /* ORIGINAL: DARK cccccc LIGHT ffffff */
    #topline, blockquote { border-color: #ffffff !important; }
    #navglobal, #footer  { background-color: #cccccc !important; }
    #topline, #bottomline { background-color: #ffffff !important; }
    
    .mefimessages a, .mefimessages a:visited, .mefimessages a:active, .mefimessages a:hover { color: #000 !important; }
    #topline a, #bottomline a { color: #000 !important; }
    #topline a:hover, #bottomline a:hover { color: #777 !important; }
    
    #boxyleft {width:60%!important; }
    #boxyleft>.jobtitle { margin-left:0!important; }
    #boxyright { width:200px !important; float:right !important;}
    #boxyright>h1 { font-size: 1em !important; font-weight:bold !important; font-family:verdana !important;margin:0!important;}
    #boxyright { margin:0 !important; padding:10px!important;}
    
    .copy, #boxyleft>.jobtitle, #boxyright { background-color: rgba(255,255,255,.8) !important; }
}

@-moz-document domain("irl.metafilter.com") {
    /* ORIGINAL: DARK cccccc LIGHT ffffff */
    /* USED: DARK 23384E LIGHT 556A80 */
    /* 243746 54687A */
    #topline, blockquote { border-color: #54687A !important; }
    #navglobal, #footer  { background-color: #243746 !important; }
    #topline, #bottomline { background-color: #54687A !important; }

    #boxyright { background-color: rgba(36,55,70,.1) !important; }
    .center-panel { background-color: rgba(36,55,70,.1) !important; margin:0 !important; }
    
    a,a:visited { color: #54687A !important; }
    
    #boxyleft>div { margin-left:0!important;}
    #boxyright { width:200px !important; float:right !important;}
}

@-moz-document domain("metatalk.metafilter.com") {
    /* ORIGINAL: DARK 333333 LIGHT 666666 */
    #topline, blockquote { border-color: #666666 !important; }
    #navglobal, #footer  { background-color: #333333 !important; }
    #topline, #bottomline { background-color: #666666 !important; }

    .sidebar { background-color: rgba(51,51,51,.1) !important; }
    .post, .copy, #page .copy, #page .comments, .posttitle { background-color: rgba(102,102,102,.1) !important; }
    a,a:visited { color: #666666 !important; }
    
    #posts {
        padding-right: 0 !important;
    }
}


@-moz-document domain("faq.metafilter.com") {
    /* ORIGINAL: DARK CCCC00 LIGHT CCCC99 */
    #topline, blockquote { border-color: #CCCC99 !important; }
    #navglobal, #footer  { background-color: #CCCC00 !important; }
    #topline, #bottomline { background-color: #CCCC99 !important; }

    .sidebar { background-color: rgba(204,204,0,.1) !important; }
    .post, .copy, #page .copy, #page .comments, .posttitle { background-color: rgba(204,204,0,.1) !important; }

}
