css:
@-moz-document url('http://www.tumblr.com/dashboard'), url-prefix('http://www.tumblr.com/dashboard/0'), url-prefix('http://www.tumblr.com/dashboard/1'), url-prefix('http://www.tumblr.com/dashboard/2'), url-prefix('http://www.tumblr.com/dashboard/3'), url-prefix('http://www.tumblr.com/dashboard/4'), url-prefix('http://www.tumblr.com/dashboard/5'), url-prefix('http://www.tumblr.com/dashboard/6'), url-prefix('http://www.tumblr.com/dashboard/7'), url-prefix('http://www.tumblr.com/dashboard/8'), url-prefix('http://www.tumblr.com/dashboard/9'),url('https://www.tumblr.com/dashboard'), url-prefix('https://www.tumblr.com/dashboard/0'), url-prefix('https://www.tumblr.com/dashboard/1'), url-prefix('https://www.tumblr.com/dashboard/2'), url-prefix('https://www.tumblr.com/dashboard/3'), url-prefix('https://www.tumblr.com/dashboard/4'), url-prefix('https://www.tumblr.com/dashboard/5'), url-prefix('https://www.tumblr.com/dashboard/6'), url-prefix('https://www.tumblr.com/dashboard/7'), url-prefix('https://www.tumblr.com/dashboard/8'), url-prefix('https://www.tumblr.com/dashboard/9') {

.identity-refresh, .identity {
background: url('http://static.tumblr.com/dd7b2184f77338d458f0b2cafd41f99c/1gvy1zg/Uvhmiytvu/tumblr_static_312e1f.png') repeat !important;}

#dashboard_index, #dashboard_edit_post, #dashboard_submissions, #dashboard_drafts, #dashboard_post_queue, #main_doc_page, #dashboard_settings, #dashboard_create_tumblelog, #dashboard_members, #dashboard_tumblelog_settings, #dashboard_messages_inbox, #notifications_index, #tagged_display, #dashboard_useraction_followers, #dashboard_posts_likes, #dashboard_useraction_following, #dashboard_lookup_lookup_contacts, #dashboard_promoted_directory, #dashboard_group_members, #main_help, #dashboard_messages_submissions, #dashboard_posts_editing, #dashboard_upload_static_file, #dashboard_account_create_tumblelog, #tagged_actions_display, .plexi, #settings_actions_tumblelog, #settings_actions_account, #settings_actions_dashboard, #settings_actions_apps, #settings_actions_notifications, #activity_actions_index, input, textarea {
font-family: 'Gibson', 'Lucida Sans Unicode', 'GNU Unifont', Helvetica, monospace;}

#discover_button {display:none;}

.flat-button.big {display:none;}

.recommended-unit-container.blog-card-compact {display:none;}

.post_avatar {
  background-color: none;
  background:transparent!important;}

.sidebar_link.avatar_align {display:none;}

.reblog_count{
display:none;
opacity:0;
}

.post .post_info a.reblog_follow_button {display:none;}

.post_full .post_notes_label .note_link_current {display:none!important;}

.identity-refresh .l-container.l-container--two-column-dashboard .l-content {
/* padding:10px!important; */
background-color: transparent;
}

.left_column, .right_column {
background-color: transparent!important;
}

.l-header-container--refresh {
background-color: rgba(0,0,0,0.2)!important;}

.post_avatar .post_avatar_link {
  background-color: #201e15 !important;}


.identity-refresh .post_full .post_permalink:not(.no-transition--dogear)  {
border-color: #312e1f #312e1f transparent transparent;
}

.identity-refresh .controls_section li.controls_section_item:hover {
background: rgba(0,0,0,0.2)!important;
}

.controls_section.follow_list.recommended_tumblelogs, .controls_section.follow_list.trending_tumblelogs {
display:none;}

#right_column .controls_section li a.spotlight {
display:none;
}

.remnant_ad.new-gemini-dash-ads, .remnant-header {display:none!important;}

li.radar_header {display: none !important;}

#tumblr_radar, .radar_content, .radar_controls, .radar_footer {
display: none;
opacity: 0;
text-indent: -9999px;
}

#right_column::after { display:none;}

.post_full .post_permalink:before {border-right: 16px solid #201e15 !important;}

}
