css:
/* ::::::::::Do Not Remove This Line:::::::::::::: */

@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/* ::::::::::Bookmarks Toolbar with Text Only::::::::::::::*/

#bookmarksBarContent toolbarbutton[container="true"] .toolbarbutton-text 
{
margin: 0px 2px 0px 2px !important; 
}

#bookmarksBarContent toolbarbutton[container="true"] .toolbarbutton-icon
{ 
display: none !important; 
}

#bookmarksBarContent toolbarbutton[container="true"]  .toolbarbutton-menu-dropmarker 
{
display: -moz-box !important;
padding-right: 2px !important;
padding-left: 2px !important;
}

/* ::::::::::::::::::FindBar:::::::::::::::::: */
#FindToolbar {
color: #FFFFFF !important;
background: #000000 !important;
}

#FindToolbar .toolbarbutton-text {
color: #FFFFFF !important;
}

/*::::::::::::::Background color of loading page::::::::::::::*/

tabbrowser tabpanels 
{
background-color: rgb(255, 255, 255) !important; 
}


/*::::::::::::::Tabs::::::::::::::*/

.tabbrowser-tabs
{
-moz-appearance: none !important;
background: #000000 !important;
}

.tabbrowser-tab:not([selected="true"]):hover
{
background: #000000 url("data:image/png;base64,") repeat-x center !important;
}

.tabbrowser-tab[selected="true"]
{
min-width: 150px !important;
background: #000000 url("data:image/png;base64,") repeat-x center !important;
}

.tabs-bottom 
{
background: #000000 url("data:image/png;base64,") repeat-x bottom !important;
}

.tabbrowser-tab:not([selected="true"]) .tab-text
{
color: #ABABAB !important;
}

.tabbrowser-tab[selected="true"] .tab-text
{
color: #FFFFFF !important;
}

.tabbrowser-tab:not([selected="true"]):hover .tab-text
{
color: #FFFFFF !important;
}

/* ::::: Status Bar Style ::::: */

statusbarpanel
{
-moz-appearance  : none !important;
border: none !important;
background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;
color: white !important;
}

/* :::::::::::::::::::::::::::::::::::::: Vista Theme ::::::::::::::::::::::::::::::::::::::::: */

/*=======Global Styles Toolbox & Toolbar=====*/

toolbox,
#toolbar-menubar,
#nav-bar,
#PersonalToolbar
{
border: 0 !important;
}

toolbox,
toolbar .toolbarbutton-text,
.menubar-text 
{
color: white !important;
}

/*=======Toolbox=====*/

toolbox 
{
-moz-appearance: none !important;
background-image: url("data:image/png;base64,") !important;
color: white !important;
}

/*=======Navigation Toolbar=====*/

#nav-bar 
{
-moz-appearance: none !important;
background: #000000 url("data:image/png;base64,") repeat-x center !important;
}

/*=======Personal/Bookmarks Toolbar=====*/

#PersonalToolbar 
{
-moz-appearance: none !important;
background: #000000 url("data:image/png;base64,") repeat-x bottom !important;
}


/*=====Sidebar & Organizer - Header & Panel=====*/

#placesToolbar
{
-moz-appearance: none !important;
background: #0f0f14 url("data:image/png;base64,") repeat-x center !important;
border: 0 !important;
}

sidebarheader,
#organizerScopeBar
{
-moz-appearance: none !important;
background: #22697E url("data:image/png;base64,") repeat-x center !important;
color: white !important;
border: none !important;
}

#placesView tree,
#infoPane,
#bookmarks-view,
#historyTree 
{
background-color: rgb(238,243,250) !important;
}

#splitter,
#contentSplitter,
#sidebar-splitter 
{
width: 2px !important;
height: 2px !important;
min-width: 1px !important;
min-height: 1px !important;
background-color: rgb(193,209,227) !important;
border: none !important;
}

#contentSplitter grippy,
#splitter grippy 
{
visibility: hidden !important;
}

/*=====URL Bar & Search Bar=====*/

#searchFilter,
#urlbar,
.searchbar-textbox 
{
opacity: 1 !important;
}

.searchbar-textbox:hover,
#urlbar:hover,
#searchFilter:hover
{
opacity: 1 !important;
}

.searchbar-textbox[focused="true"],
#urlbar[focused="true"],
#searchFilter[focused="true"] 
{
opacity: 1 !important;
}
