css:
@-moz-document
url-prefix("http://www.imdb.com/board"),
url-prefix("http://www.imdb.com/title/"),
url-prefix("http://www.imdb.com/name/"),
url-prefix("http://www.imdb.com/user/"),
url-prefix("http://www.imdb.com/help"),
url-prefix("http://www.imdb.com/updates"),
url-prefix("http://www.imdb.com/mymovies/") {

/* All links */
a:link {color: #003399 !important}
a:visited {color: #581C90 !important}

/* Title/Name pages */
a.link.empty {color: #B7B7B7 !important}
a.link.selected {color: white !important}
a.main, a.glossary, a.btn {color: black !important}
a.in_production {color: red !important}

/* Header links */
#navbar a {color: white !important}
#navbar ul.main_nav li.nb_hover a {color: black !important}
#navbar ul.main_nav li ul.sub_nav li a {color: #252F3F !important}

/* News Flash link (attached to the header) */
#site-stripe a {color: white !important}

/* "[Click to see full summary]" (on Update pages) */
#summaryb {color: blue !important}

/* Script-generated links in Update History */
A[class="likified"] {color: black !important}
}

/* Go Button & Other Buttons */

.nb_primary, .btn.small, .btn.large, #showtimesLocationFormDiv > P > INPUT:last-child { background: -moz-linear-gradient(bottom,  #333333 8px, #777777) !important;
border-color: #777777 !important;
border-bottom: 1px solid !important;
color: #999999 !important;
max-height: 21px !important;  
margin-top: 1px !important;
}

/*---------- main section -------------*/


#navbar {
 -moz-box-shadow: #555555 0px 0px 3px !important; 
border: none !important; 
border-bottom: 1px solid !important; 
-moz-border-bottom-colors: #000000 #181818 #181818 !important; 
}

/* main bg */
#content-2-wide,
#pagecontent, #content-1, #footer
 { background: #BCCDE0 !important; }

/*-------- comments ----------------*/

td[bgcolor="#ffffff"] {
background: Black none !important;
}
td[bgcolor="#006699"] {
background: #3c3c3c none !important;
}
#tn15main table.boards th {
background: #4c4c4c none !important;
}

@-moz-document domain("imdb.com") {

/* body bg */
body, #wrapper,
#root
{ background: #5c5c5c !important; }
}

@-moz-document domain('www.imdb.com') {
	#supertab{ display: none; }
}

@-moz-document domain("imdb.com") {

#sidebar > BR,
#top_ad_wrapper,
#top_rhs_wrapper,
#bottom_ad_wrapper,
#afs_sponsored_links,
#sponsored_links_afc_div_MIDDLE_CENTER,
#sponsored_links_afc_div_BOTTOM_CENTER,
#sponsored_links_afc_div_middle_center,
#sponsored_links_afc_div_bottom_rhs,
#amazon-affiliates,
.after_ad {display: none}

}

@-moz-document domain("imdb.com") {

/* body bg */
body, #wrapper,
#root
{ background: #DADBDD !important; }
}
