css:
@-moz-document url("http://www.allmystery.de/"), 
url("https://www.allmystery.de/"), 
url-prefix("http://www.allmystery.de/themen"), 
url-prefix("https://www.allmystery.de/themen"),
url-prefix("http://www.allmystery.de/ena/"), 
url-prefix("https://www.allmystery.de/ena/"),
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/artikel/"),
url-prefix("http://www.allmystery.de/stats/"),
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/chat/"), 
url-prefix("https://www.allmystery.de/chat/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/wiki/"),
url-prefix("http://www.allmystery.de/pn"), 
url-prefix("https://www.allmystery.de/pn"), 
url-prefix("http://www.allmystery.de/fcg"), 
url-prefix("https://www.allmystery.de/fcg"), 
url-prefix("http://www.allmystery.de/am"), 
url-prefix("https://www.allmystery.de/am"), url-prefix("https://www.allmystery.de/videos/")  {
body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/65b02a_2007-spiral17.jpg") !important;
    background-attachment:fixed !important;
    }

.header {
    background-color: rgba(00,00,00,0.5) !important;
    background-image: none !important;
    border-radius: 10px 10px !important;
    }

.tabnav a {
    background-color: #3f2000 !important;
    color: #ffffff !important;
    border-radius: 10px 10px !important; 
    }

.tabnav a:hover, a.tabnav_selected, a.tabnav_js_selected {
    background-color: rgba(00,00,00,0.5) !important;
    border-radius: 10px 10px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

.mcell {
    background: transparent !important;
    border: 1px solid #3f2000 !important;
    border-radius: 10px !important;
    }

.mcell_inhalt {
    background-color: rgba(00,00,00,0.5) !important;
    border-bottom: 1px solid #8C5916 !important;
    border-radius: 10px 10px !important;
    }

.mcell2, .mcell_left, .mcell_status {
    background-color: rgba(00,00,00,0.5) !important;
    border-bottom: 1px solid #8C5916 !important;
    border-radius: 10px 10px !important;
    }

.mcell_inhalt2 {
    background-color: rgba(00,00,00,0.5) !important;
    border-bottom: 1px solid #8C5916 !important;
    border-radius: 10px 10px !important;
    }

.mcell_inhalt3 {
    background-color: rgba(00,00,00,0.5) !important;
    border-bottom: 1px solid #8C5916 !important;
    border-radius: 10px 10px !important;
    }

.sufu {
    border: 1px solid #000000 !important;
    border-radius: 10px 10px !important;
    }

#moreMenu {
    border: 1px solid transparent !important;
    background-color: #3f2000 !important;
    }

body {
    color: #000000 !important;
    }

.at_in {
    color: #000000 !important;
    }

.sub_info_p, .sub_info_p_check {
    color: #000000 !important;
    }

.sub_info {
    color: #000000 !important;
    }

.post {
    background-color: #8C5916 !important;
    border-bottom: 3px solid transparent !important;
    border-top: 3px solid transparent !important;
    border-radius: 15px 15px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    opacity: 0.9 !important;  
    }

.post:nth-child(even), .paginatorLast, .paginator, .MainOverview td.Category, .MainOverview td.LastPost, td, tr, .in, .start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last, .videos .videoitem, .videos .videoitem:nth-of-type(even) {
    background-color: #7D4905 !important;
    border-radius: 15px 15px;
    box-shadow: 2px 2px 10px #000000 !important;
    opacity: 0.9 !important;   
    }

.fm td, .fh td, .fd td {
    border-radius: 15px 15px !important;
    }

.header_info {
    border-radius: 15px 15px !important;
    border-color: transparent !important;
    border-width: 1px !important;
    border-style: solid !important;
    background-color: #D67900 !important;
    }

.message, .message_info {
    border-radius: 15px 15px !important;
    border-color: #3f2000 !important;
    border-width: 1px !important;
    border-style: solid !important;
    background-color: #8C5916 !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

#intro {
    background: #8C5916 !important;
    border-color: rgba(00,00,00,0.5) !important;
    border-radius: 15px 15px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

.Voting .ui-widget-content .ui-state-default {
    background: #000000 !important;
    border-radius: 3px 3px !important;
    border: 1px solid #ffffff !important;
    }

.Voting {
    color: #ffffff !important;
    }

.Voting-bar-filling, .Voting-bar {
    background: #000000 !important;
    border-radius: 3px 3px !important;
    border: 1px solid transparent !important;
    }
  
.Voting-bar-filling:hover  {
    border: 1px solid #ffffff !important;
    } 
  
.Voting-voted .Voting-bar-filling  {
    background: #786105 !important;
    }

textarea {
    border-radius: 15px 15px;
    box-shadow: 2px 2px 10px #000000 !important;
    }

.footer hr {
    background-color: rgba(00,00,00,0.5) !important;
    color: #1B8001 !important;
    }

input {
    border-color: #3f2000 !important;
    border-radius: 15px 15px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

.paginator {
    border-bottom: 1px solid #3f2000 !important;
    }

img.smallCategoryImage {
    border-radius: 15px 15px !important;
    }

.useravatar {
    border-radius: 5px 5px !important;
    }

.finfo {
    border-right: solid 0px transparent !important;
    }

.fbeitrag {
    border-left: solid 0px transparent !important;
    }

.blogs-prevpost.even {
    background-color: #8C5916 !important;
    opacity: 0.9 !important;  
    border: 1px solid transparent !important;
    border-radius: 15px 15px !important;
    }

.blogs-prevpost {
    background-color: #7D4905 !important;
    opacity: 0.9 !important;   
    border: 1px solid transparent !important;
    border-radius: 15px 15px !important;
    }

.voteup {
    background-color: #8C5916 !important;
    border: 1px solid transparent !important;
    border-radius: 5px 5px !important;
    }

.quick_thread {
    background-color: #8C5916 !important;
    }

#se .quick_item {
    background-color: #8C5916 !important;
    }

#se {
    background-color: #8C5916 !important;
    }

.quick_item_header {
    background: #7D4905 !important;
    border-bottom: 1px transparent !important;
    border-top: 1px solid transparent !important;
    border-radius: 15px 15px !important;
    }

.quick_user_view {
    background-color: #8C5916 !important;
    }

.quick_tags {
    background-color: #8C5916 !important;
    border-radius: 15px 15px !important;
    }

.fm td  {
    background-color: #705B04 !important;
    } 

div.thread_vote_container_active span {
    color: green !important;
    }

div.thread_vote_container {
    background-color: #473900 !important;
    border-radius: 5px 5px !important;
    }

.verweis {
    color: #ff0000 !important;
    border: 1px solid #ff0000 !important;
    border-radius: 5px 5px !important;
    }

a.mod {
    color: #BE0808 !important;
    }

#chat_messages {
    background-color: rgba(120,97,5,0.9) !important;
    border-radius: 5px 5px !important; 
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }
}

@-moz-document url("https://www.allmystery.de/fcgi/?m=messages_log"), 
url-prefix("https://www.allmystery.de/artikel/"), 
url-prefix("https://www.allmystery.de/stats/"), 
url-prefix("https://www.allmystery.de/wiki/"), 
url-prefix("https://www.allmystery.de/fcgi/?m=settings"), 
url-prefix("https://www.allmystery.de/static/impressum/"), 
url-prefix("http://www.allmystery.de/artikel/"), 
url-prefix("http://www.allmystery.de/stats/"), 
url-prefix("http://www.allmystery.de/wiki/"), 
url-prefix("http://www.allmystery.de/fcgi/?m=settings") 
 {
.main {
    background-color: rgba(120,97,5,0.8) !important;
    border-radius: 8px 8px !important; 
    }

}
