tab.view-button {
   -moz-appearance: none !important; 
}       
        
.view-button[href*="dalnet"   ]{ background: #F3E3CF !important; }
.view-button[href*="efnet"    ]{ background: #E8E7D0 !important; }
.view-button[href*="freenode" ]{ background: #D4EAE4 !important; }
.view-button[href*="hybridnet"]{ background: #D1EAEF !important; }
.view-button[href*="moznet"   ]{ background: #D8E7F9 !important; }
.view-button[href*="quakenet" ]{ background: #E4E4FA !important; }
.view-button[href*="serenia"  ],
.view-button[href*="landoleet"]{ background: #EDE1F8 !important; }
.view-button[href*="slashnet" ]{ background: #F5DFF0 !important; }
.view-button[href*="undernet" ]{ background: #F8DFE9 !important; }
.view-button[href*="webbnet"  ]{ background: #FCDFDC !important; }

.view-button[href*="dalnet"   ][selected='true']{ background: #C8AD65 !important; }
.view-button[href*="efnet"    ][selected='true']{ background: #A9B66F !important; }
.view-button[href*="freenode" ][selected='true']{ background: #78BDA1 !important; }
.view-button[href*="hybridnet"][selected='true']{ background: #69BDBC !important; }
.view-button[href*="moznet"   ][selected='true']{ background: #6FB9D8 !important; }
.view-button[href*="quakenet" ][selected='true']{ background: #9AAFE1 !important; }
.view-button[href*="serenia"  ],
.view-button[href*="landoleet"][selected='true']{ background: #C2A4D8 !important; }
.view-button[href*="slashnet" ][selected='true']{ background: #DD9DBA !important; }
.view-button[href*="undernet" ][selected='true']{ background: #E49CA1 !important; }
.view-button[href*="webbnet"  ][selected='true']{ background: #E1A17E !important; }
