body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/3bb131_katzen-haustiere-tiere-lustige-bilder-hd.jpg") !important;
    background-size: 100% 100% !important;
    background-attachment:fixed !important;
    }

.header {
    background-color: rgba(00,00,00,0.5) !important;
    background-image: none !important;
    border-radius: 5px 5px !important;
    border: 1px solid #000000 !important;
    }

.tabnav a {
    background-color: rgba(00,00,00,0.5) !important;
    color: #ffffff !important;
    border-radius: 5px 5px !important;
    }

.tabnav a:hover {
    background-color: #5C5C5C !important;
    border-radius: 4px 4px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

a.tabnav_selected {
    background-color: transparent !important;
    border-radius: 4px 4px !important;
    box-shadow: 2px 2px 15px #000000 !important;
    }

.mcell_inhalt, .mcell, .mcell2, .mcell_left, .mcell_status, .mcell_inhalt2, .mcell_inhalt3 {
    background-color: rgba(00,00,00,0.5) !important;
    border: 1px solid #000000 !important;
    border-radius: 5px 5px !important;
    }

.sufu {
    border: 1px solid #000000 !important;
    border-radius: 5px 5px !important;
    }

#moreMenu {
    border: 1px solid transparent !important;
    background-color: #5C5C5C !important;
    }

.post {
    background-color: #4F4F4F !important;
    border-bottom: 3px solid transparent !important;
    border-top: 3px solid transparent !important;
    border-radius: 5px 5px !important;
    opacity: 1.0 !important; 
    }

.post:nth-child(even), .paginatorLast, .paginator, .MainOverview td.Category, .MainOverview td.LastPost, td, tr, .start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last, .videos .videoitem, .videos .videoitem:nth-of-type(even) {
    background-color: #3D3B3C !important;
    border-radius: 5px 5px !important;
    opacity: 1.0 !important;  
    }

.fm td, .fh td, .fd td {
    border-radius: 5px 5px !important;
    }

.header_info {
    border-radius: 5px 5px !important;
    border-color: transparent !important;
    border-width: 1px !important;
    border-style: solid !important;
    background-color: #545454 !important;
    }

.message, .message_info {
    border-radius: 15px 15px !important;
    border-color: #545454 !important;
    border-width: 1px !important;
    border-style: solid !important;
    background-color: #545454 !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

#intro {
    background: #545454 !important;
    border-color: rgba(00,00,00,0.5) !important;
    border-radius: 5px 5px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

textarea {
    border-radius: 5px 5px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

.footer hr {
    border-radius: 5px 5px !important;
    background-color: #545454 !important;
    color: #1B8001 !important;
    }

input {
    border-color: #545454 !important;
    border-radius: 5px 5px !important;
    box-shadow: 2px 2px 10px #000000 !important;
    }

.paginator {
    border-bottom: 1px solid #545454 !important;
    }

img.smallCategoryImage {
    border-radius: 5px 5px !important;
    }

.useravatar {
    border-radius: 5px 5px !important;
    }

.finfo {
    border-right: solid 0px transparent !important;
    }

.fbeitrag {
    border-left: solid 0px transparent !important;
    }

.blogs-prevpost.even {
    background-color: #4F4F4F !important;
    }

.blogs-prevpost {
    background-color: #4F4F4F !important;
    }

.voteup {
    background-color: #4F4F4F !important;
    }

.blogs-prevpost span {
    color: #ffffff !important;
    }

.main  {
    background-color: rgba(00,00,00,0.5) !important;
    border: 1px solid #000000 !important;
    border-radius: 5px 5px !important;
    }
  
div.main {
    margin-top: 17px !important;
    }

Voting-bar-filling, .Voting-bar, .Voting .ui-slider-horizontal .ui-slider-handle, .Voting-bar-filling, div.thread_vote_container, .in, .voteup, .blogs-prevpost {
    border-radius: 5px 5px !important;
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }
}
