css:
@namespace url("http://www.w3.org/1999/xhtml");

@font-face {
  font-family: 'Roboto';
  font-style: normal;
  font-weight: 400;
  src: local('Roboto Regular'), local('Roboto-Regular'), url(https://themes.googleusercontent.com/static/fonts/roboto/v7/Xyjz-jNkfiYuJf8UC3Lizw.woff) format('woff');
}

@-moz-document domain("dziennik.librus.pl") {
    body {
        font-family: 'Roboto', sans-serif !important;
    }

    div.container {
        background: rgba(0, 0, 0, 0) !important;
        background-position: 0px 0px;
        font-size: 13px;
        background: -moz-linear-gradient(top,  rgba(206,206,206,0.65) 0%, rgba(0,0,0,0) 100%) !important; /* FF3.6-16 */
        background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(206,206,206,0.65)), color-stop(100%,rgba(0,0,0,0))) !important; /* WebKit */
        background: -webkit-linear-gradient(top,  rgba(206,206,206,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* Nowy WebKit */
        background: -o-linear-gradient(top,  rgba(206,206,206,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* Opera 11.10+ (Presto i Ragnarok) */
        background: -ms-linear-gradient(top,  rgba(206,206,206,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* IE10+ */
        background: linear-gradient(to bottom,  rgba(206,206,206,0.65) 0%,rgba(0,0,0,0) 100%) !important; /* W3C, FF17+ */
        filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#a6cecece', endColorstr='#00000000',GradientType=0 ) !important; /* Internet Explodery 6-9 */
    }

    div.uczen_text_menu {
        font-weight: 400 !important;
    }

    div.copyright {
        float: none !important;
        text-align: center !important;
        margin: 2px 2px 2px 2px !important;
    }

    div.head {
        background: rgba(0, 0, 0, 0) !important;
        background-position: 0px 0px;
    }

    h1 {
        font-family: 'Roboto' !important;
    }
    
    td {
        font-family: 'Roboto', sans-serif !important;
    }
    
    div.topline {
        display: none;
    }
    
    div.toplineHD {
        display: none;
    }
    
    td.head {
        display: none;
    }
}
