css:
@-moz-document domain("4chan.org") {
* {
  position: relative;
  font-family: 'Lato', sans-serif;
  box-sizing: border-box;
}
.thread .backlinkHr,
.thread .sideArrows {
  display: none;
}
.thread .opContainer .post.op {
background: -moz-linear-gradient(top,  rgba(0,0,0,0.1) 0%, rgba(0,0,0,0) 100%);
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,rgba(0,0,0,0.1)), color-stop(100%,rgba(0,0,0,0)));
background: -webkit-linear-gradient(top,  rgba(0,0,0,0.1) 0%,rgba(0,0,0,0) 100%);
background: -o-linear-gradient(top,  rgba(0,0,0,0.1) 0%,rgba(0,0,0,0) 100%);
background: -ms-linear-gradient(top,  rgba(0,0,0,0.1) 0%,rgba(0,0,0,0) 100%);
background: linear-gradient(to bottom,  rgba(0,0,0,0.1) 0%,rgba(0,0,0,0) 100%);
filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='#1a000000', endColorstr='#00000000',GradientType=0 );



}
.thread .post {
  width: 100%;
  display: block !important;
  border: 0;
  border-top: 1px solid black;
  padding-top: 0.25em;
  border-top: 1px solid #aaa;
  min-height: 5.5em;
}
.thread .postInfo, .thread .backlink  {
  width: 15% !important;
}
              
.thread .file {
  width: 55% !important;
}
.thread .file .fileInfo {
  
  margin:0 !important;
      }

.thread .postMessage,  .thread .summary{
  margin-left: 16% !important;
  margin-right: 52% !important;
  max-width: 29% !important;
  display: block !important;
  margin:0;
}
.thread .postMessage {
  margin-top: -4.5em !important;
}
.thread .backlink {
  margin-top: 2em;
  text-align: right !important;
            position: absolute;
            bottom:0;
}
.thread .backlink strong {
  display: none;
      }
.thread .backlink span {
  margin-right: 0.2em;
}
.thread .postInfo, .thread .summary {
  display: inline-block !important;
  text-align: right !important;
  line-height: 0.95;
}
          
.thread .backlink {
  display: inline-block !important;
}

.thread .file {
  display: inline-block !important;
  text-align: left !important;
  float: right !important;
}
.fileThumb {
  width: 100%;
  text-align: center !important;
  display: block !important;
  float: none !important;
  margin:0 !important;
}
.fileThumb .expanded {
  max-width: 100% !important;
  height: auto !important;
  margin: 0 auto !important;
  float: none !important;
  margin:0 !important;
}
  
.thread .postInfo .nameBlock {
  font-size: 1.2em;
  display: block;  
  letter-spacing:-1px;
}
  
.thread .postInfo .dateTime {
  display: block;  
  font-size: 0.9em;
  line-height: 1.75;
}
.post input[type=checkbox] {
  visibility: hidden;
    float: left;
  }
.post:hover input[type=checkbox] {
  visibility: visible;
  }
.thread .postInfo .postNum {
  font-size: 2em;
  letter-spacing: -5px;
}
.thread .postInfo .postNum a {
  letter-spacing: 0px;
      }
.thread .postInfo .postNum span {
  letter-spacing: 0px;
  float: left;
  font-size: 0.75em;
}
.threadHideButton{
  margin:0 !important;
  margin-bottom: 2px !important;
  display: inline-block;
  float:right !important;
  display :none;
}
 .reply:target, .reply.highlight {   
  background: #CCC!important;
  border: 1px solid #CCC!important;
  padding: 2px;
}
@font-face {
  font-family: 'Lato';
  font-style: normal;
