@-moz-document domain("openoffice.sblo.jp"){
/*======================================
  LifbreOffice de iko liquid compact 2014/06
  @-moz-document domain("openoffice.sblo.jp")
  @-moz-document url-prefix("")
  @-moz-document url("")
  @-moz-document regexp("")
  ======================================*/
/*--- unwanteds ---*/
dummy#dummy.dummy {display: none !important;}

/*--- generic containers ---*/
div {box-sizing: border-box; -moz-box-sizing: border-box;}
body{
 font-family: inherit !important;
 font-size: medium !important; font-weight: normal !important;
 line-height: 1.4em !important;
 }
#container {
 width: auto !important;
 background-color: green;
 background-image: none !important;
 }

/*--- header ---*/
#banner {
 display: block !important;
 background: none !important;
 height: 3em !important;
 min-height: inherit !important;
 margin: 0 !important;
 padding: 0 !important;
 width: 100% !important;
 }
#banner h1 {padding: 0 !important; font-size: 1.5em;}
#banner .description {font-size: 1em;}

/*--- footer ---*/

/*--- main contents ---*/
#content, #links {position: relative !important;}
#content {background-color: white;}
#content {width: 100% !important; margin: 0 -250pt 0 0 !important;}
#content>* {width: auto !important; margin: 0 250pt 0 0 !important;}
#links {width: 250pt !important; margin: 0 !important;}
#links {top: 0 !important; left: 0 !important;}
#links {background-color: green;}

.blogbody {padding: 0 0.2em 0 0.5em !important;}

.comments-body>input,
.comments-body>textarea
 {width: 90% !important; max-width: inherit !important;}

 #calendar table {width: 100% !important;}
.sidetitle {color: black !important;}}
