css:
@-moz-document domain("slickdeals.net") {
body {background: black !important; color: #B0AEAC !important;}
.global_wrap, .panel_dealalerts, .panel {background: #111111 !important;}
a, legend, b {color: #639E2B !important;}
a:hover, .colitem.relatedlistbit:hover p.title {color: #AAF563 !important;}
a:visited {color: #375819 !important;}
.alt1, .alt2 {color: #B0AEAC !important; background: #1A1A1A !important; border-bottom: 1px solid #383635 !important;}
h1, h2, h3, h4, .fieldset, #global_footer_notice, #global_footer_nav > a, .perpage, #results_table tbody td.replies strong, #results_table tbody td.views strong {color: #B0AEAC !important;}
#global_nav, .thead, .sd_postbit_head, #refine .section {background: #1C2E2E !important;}
#global_nav #global_nav_v3 {background: none !important;}
#topwrapper, #logoarea {background-color: black !important;}
.tborder {background: #838383 !important;}
.forumbutton.blue, .forumbutton {background-color: #1F1F1F !important; border-bottom: 1px solid #DDC; border-left: 1px solid #DDC; border-right: 1px solid #DDC;}
.forumwrap_top, #global_footer_extra a.top_link, .firstpost, .forumbuttonbox2_br, #search_wrap, #filter_forum_sidebar.search #refineblock {background: #1A1A1A !important;}
.userbar, .sd_postbit_body .sd_postbit_usersection, .vBulletin_editor {background: #1A1A1A !important;}
.firstthread_postcontent, .userinfo, .sd_postbit_user, .sd_postbit_body .sd_postbit_usersection ul, .sd_postbit_body .sd_postbit_usersection .user_avatar, .wikicontents, .sd_postbit_body_content, .search_tabs.tantab, #results_table tbody tr, #results_table td.attachment, #results_table.sf_threadstarted td.title {background: #1F1F1F !important;}
.in_for a, .vote_display, .thread_feature_buttons_active, .thread_feature_lastbutton, #results_body, div {background: #1A1A1A !important;}
.search_pagenav {background: #1F1F1F !important; border: 1px solid #314433 !important;}
.pagenav_box .search_pagenav_current {background-color: #CDCAC1 !important; color: #1C2E1C !important;}
.perpage_links, .forumwrap_bottom .pagenav_wrapper {background-color: #111111 !important;}
table {background-color: 1A1A1A !important; border-color: #3B3B3B !important;}
.forum_options {background-color: #111111 !important;}
.forum_category_select, .forum_options_right {background-color: #1A1A1A !important; border: 1px solid #D0D7E0 !important;}
.forum_legend ul li, .forum_rules div span, .sd_postbit_body .sd_postbit_usersection li, #results_table span {color: #B0AEAC !important;}
.postdate, a.rel-block:hover {background-color: #1C2E2E !important;}
.attachment-preview {background: #0C1B0C !important; border: 1px solid #5F5F5F !important;}
.attachment-preview-title {background: #082B1B !important; border-bottom: 1px solid #5F5F5F !important;}
.in_for {background: #1A1A1A !important; border: none !important;}
.vote_action {color: #A26062 !important; background: #1A1A1A !important;}
.thread_feature_buttons {border-top: none; border-left: none; border-right: none;}
.forumwrap_bottom, .colitem.relatedlistbit a.rel-block, .info_container .related_deals_inner, .sd_postbit_body, .simple .dealitem {background-color: #1A1A1A !important;}
#results_table tbody th {background: #1C2E2E !important;}
.ranksreditor {color: #5850E0 !important;}
.quotebox_content {background: #1F1F1F url("http://static.slickdealscdn.com/images/slickdeals/quotebg.png") no-repeat !important; border: 1px solid #292929 !important;}
.tborder {background: #1B1B1B !important;}
.tcat {background: #0D2012 !important;}
textarea {background: #1F1F1F !important; color: #B0AEAC !important;}
td {background: #1A1A1A !important; color: #B0AEAC !important;}
.firstthread_sigarea {border-top: 1px solid #616161 !important; background: #1A1A1A !important;}
#search_meta, .results_header {color: #B0AEAC !important; background: #1A1A1A !important;}
#search_meta input.submit {background-color: #00B61E !important;}
#refine tr.forum label.forum.selected {color: #913434 !important;}
ul#global_nav_v3 li .sub *, ul#global_nav_v3 li .sub {background: #312F2F !important;}
ul#global_nav_v3 .sub ul li h2 a.nav_link {border-bottom: 1px solid #1A1A1A !important;}
}
