css:
@-moz-document url("http://www.allmystery.de/"), url("https://www.allmystery.de/"), url-prefix("http://www.allmystery.de/themen"), url-prefix("https://www.allmystery.de/themen"), url-prefix("http://www.allmystery.de/ena/"), url-prefix("https://www.allmystery.de/ena/"), url-prefix("http://www.allmystery.de/artikel/"), url-prefix("https://www.allmystery.de/artikel/"), url-prefix("http://www.allmystery.de/stats/"), url-prefix("https://www.allmystery.de/stats/"), url-prefix("http://www.allmystery.de/chat/"), url-prefix("https://www.allmystery.de/chat/"), url-prefix("http://www.allmystery.de/wiki/"), url-prefix("https://www.allmystery.de/wiki/"), url-prefix("http://www.allmystery.de/pn"), url-prefix("https://www.allmystery.de/pn"), url-prefix("http://www.allmystery.de/fcg"), url-prefix("https://www.allmystery.de/fcg"), url-prefix("http://www.allmystery.de/am"), url-prefix("https://www.allmystery.de/am"), url-prefix("https://www.allmystery.de/videos/")  {
body.page, body.popup, .titelg {
    background-image:url("http://static.allmystery.de/upics/5128cb_schafe.jpg");
    background-color: transparent;
    background-attachment:fixed;
    }

.header {
    background-color: rgba(00,00,00,0.5);
    background-image: none;
    border-radius: 5px 5px;
    }

.tabnav a {
    background-color: rgba(00,00,00,0.5);
    color: #ffffff;
    border-radius: 5px 5px; 
    }

.tabnav a:hover, a.tabnav_selected, a.tabnav_js_selected {
    background-color: #5C5C5C;
    border-radius: 4px 4px;
    box-shadow: 2px 2px 10px #000000;
    }

.mcell {
    background: transparent;
    border: 1px solid #3f2000;
    border-radius: 5px;
    }

.mcell_inhalt {
    background-color: rgba(00,00,00,0.5);
    border-bottom: 1px solid #545454;
    border-radius: 5px 5px;
    }

.mcell2, .mcell_left, .mcell_status {
    background-color: rgba(00,00,00,0.5);
    border-bottom: 1px solid #545454;
    border-radius: 5px 5px;
    }

.mcell_inhalt2 {
    background-color: rgba(00,00,00,0.5);
    border-bottom: 1px solid #545454;
    border-radius: 5px 5px;
    }

.mcell_inhalt3 {
    background-color: rgba(00,00,00,0.5);
    border-bottom: 1px solid #545454;
    border-radius: 5px 5px;
    }

.sufu {
    background-color: #5C5C5C;
    color: black;
    border: 1px solid #000000;
    border-radius: 5px 5px;
    }

#moreMenu {
    border: 1px solid transparent;
    background-color: #5C5C5C;
    }

body {
    color: #000000;
    }

a {
    color: #000000;
    }

.at_in {
    color: #000000;
    }

.sub_info_p, .sub_info_p_check {
    color: #000000;
    }

.sub_info {
    color: #000000;
    }

.post, .post:nth-child(even), .paginatorLast, .paginator, .fd, .fh {
    background-color: #3D3B3C;
    border-radius: 5px 5px;
    box-shadow: 2px 2px 10px #000000;
    opacity: 1.0;   
    }

.fm td, .fh td, .fd td, .start .subselector, .start .areas .area, .start .areas .area:nth-of-type(odd), .start .areas .area .main, .start .areas .area .last, .videos .videoitem, .videos .videoitem:nth-of-type(even) {
    border-radius: 5px 5px;
    }

.header_info {
    border-radius: 5px 5px;
    border-color: transparent;
    border-width: 1px;
    border-style: solid;
    background-color: #545454;
    }

.message, .message_info {
    border-radius: 15px 15px;
    border-color: #545454;
    border-width: 1px;
    border-style: solid;
    background-color: #545454;
    box-shadow: 2px 2px 10px #000000;
    }

#intro {
    background: #545454;
    border-color: rgba(00,00,00,0.5);
    border-radius: 5px 5px;
    box-shadow: 2px 2px 10px #000000;
    }

textarea {
    background-color: #545454;
    border-radius: 5px 5px;
    box-shadow: 2px 2px 10px #000000;
    }

.footer hr {
    background-color: #545454;
    color: #1B8001;
    }

input {
    background-color: rgba(00,00,00,0.5);
    border-color: #545454;
    border-radius: 5px 5px;
    box-shadow: 2px 2px 10px #000000;
    }

.paginator {
    border-bottom: 1px solid #545454;
    }

img.smallCategoryImage {
    border-radius: 5px 5px;
    }

.useravatar {
    border-radius: 5px 5px;
    }

.finfo {
    border-right: solid 0px transparent;
    }

.fbeitrag {
    border-left: solid 0px transparent;
    }


.blogs-prevpost.even {
    background-color: #4F4F4F;
    }

.blogs-prevpost {
    background-color: #4F4F4F;
    }

.voteup {
    background-color: #4F4F4F;
    }

.blogs-prevpost span {
    color: #ffffff;
    }

div.post.warn {
    background-color: rgba(152,0,0,0.8) !important;
    box-shadow:inset 50px 20px 80px #ff0000 !important;
    }

}
