css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://www.hubski.com), 
               url-prefix(http://hubski.com),
               url-prefix(https://www.hubski.com),
               url-prefix(https://hubski.com)
{

a { 
font-size: 35px !important;
}
body 
{ 
font-family: Sans-Serif;
font-size: 35px !important;
}

div {
font-size: 35px !important;
}

span {
font-size: 35px !important;
}

div.main {
padding: 0px !important;
}

div.leftmain {
  background-color: #FEFEFE;
  padding: 10px;
  position: inherit !important;
  width: 100% !important;
  margin: 0px !important;

}

div.middlefeed {
width: 100% !important;
margin: 0px !important;


}

div.middlepage {
width: 100% !important;
margin: 0px !important;


}

div.plusminus, div.longplusminus {
  height: 100px !important;
  width: 100px !important;
}

.score {
  height: 90px;
  width: 90px;
}

img {
min-height: 60px !important;
}

.title a:link, .feedtitle a:link, .similartitle a:link {
  color: #595C5F;
  font-weight: bold ;
  text-decoration: none;
}

.box:nth-child(odd) { 
background-color: #F7F4F4;
}

.feedtitle {
padding: 0px 0px 10px 0px;
}

.pubtext, .comm {
  font-family: Sans-Serif;
  font-size: 40px !important;
}

.topimage 
{
  height: 90px;
  width: 90px;
  }
  
  .searchbox {

    width: 74%;
    height: 60px !important;

}

input[type="image"] {
 height: 60px !important;
}

input[type="submit"] {
 min-height: 60px !important;
 font-size: 35px !important;
}


.outercomm { 
 background-color: #F7F7F7;
 border-top:solid 1px #E8E8E8;
 margin-top: 5px;
}

#colorbox { 
left: 0px !important;
top: 0px !important; 
width: 100% !important;
height: 100% !important;
position: fixed !important;
}

#cboxWrapper {
width: 100% !important;
height: 100% !important;
}

#cboxContent {
background: none repeat scroll 0% 0% rgb(255, 255, 255);
overflow: scroll !important;
width: 100% !important;
height: 100% !important;
}


textarea {
  max-width: 100% !important;
  width: 100% !important;
  min-height: 400px !important;
}

.comhead {
padding-bottom: 55px !important;
display: block;

}
}
