css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("videogamesawesome.com") {

/* WEBFONTS */

/* GLOBAL RESET */

a {
 border-radius: 0px !important;
 border: none !important;
 text-decoration: none !important;
}

form {
 border: none !important;
}

body#bp-default {
}

#content > .padder {
 padding-right: 40px !important;
}

/* TOP */
#wp-admin-bar {
 min-width: 1050px!important;
 width: 100%!important;
 background: #000 !important;
 opacity: 1 !important;
 box-shadow: 0px -5px 10px #222 inset, 0px 1px #000;
}

#wp-admin-bar .padder {
 background: none !important;
}

#wp-admin-bar .padder ul.main-nav {
 width: 1050px !important;
}

#wp-admin-bar .padder {
 min-width: 1050px !important;
 max-width: 1050px !important;
 width: 1050px !important;
 position: static !important;
 margin: auto 0 !important;
}

#wp-admin-bar a,
.main-nav a{
 color: #eee !important ;
}
.main-nav a:hover{
 color: #fff!important;
}

.main-nav li:hover {
 text-shadow: 1px 1px #000 !important; 
 background-color: #08b !important;
 box-shadow: 0px 0px 0px 1px #222 inset;
}

.main-nav > li:hover {
 box-shadow: 0px 1px 0px 1px #222 inset;
}

.main-nav > li ul {
 border: none !important;
 box-shadow: 0px 0px 0px 1px #222 inset; 
}

.main-nav > li > ul li:hover {
 box-shadow: 0px 0px 0px 1px #222 inset;
}

/* HEADER */
#header {
background: url('http://test.angelinaballerina.com/Thomas/usa/img/bg-gray-sunburst.jpg') !important;
background-repeat: no-repeat;
background-size: 100% 100% !important;
}

/* NAVIGATION */

#header {
 position: relative !important;
}

#header > h1 + div  {
 background: #222 !important; 
 box-shadow: 0px 0px 0px 1px #222 inset , 0px 0px 2px 0px #fff inset, 0px 20px 20px 0px #444 inset, 0px 5px 5px #333 !important;
}

#nav {
 width: 100% !important;
 padding: 0 !important;
 background: none !important;

}

ul#nav li a {
 background: none !important;
 text-transform: uppercase;
 font-size: 14px !important;
 font-family: Tahoma, sans-serif !important;
 
 color: #ddd !important;
 text-shadow: 1px 1px #000;
 padding: 5px !important;
 white-space: nowrap;
}

ul#nav li {
 display: table-cell!important;
 overflow: hidden!important;
 margin:0 !important;
 padding:10px !important;
 border-radius:0!important;
 border: none !important;
 float: none !important;

 box-shadow: -1px 0px 0px #222 inset, -2px 0px 0px #444 inset;
 background: none !important;
 
 vertical-align: middle !important;
  
 whitespace: nowrap;
}

#nav li.sfhover,
input[type=submit],
input[type=button],
input[type=reset],
a.button.logout,
button.button {
 color: #fff !important;
 background: #0088bb !important;
 box-shadow: 0px 0px 0px 1px #222 inset, 0px 0px 2px 0px #fff inset, 0px 20px 10px 0px #0ad inset;
}

#nav li.selected,
#nav > li.current-menu-item,
#nav > li.current_page_item,
#nav > li.current_page_ancestor {
 background: #5c5 !important;
 box-shadow: 0px 0px 0px 1px #222 inset, 0px 0px 2px 0px #fff inset, 0px 20px 10px 0px #6c6 inset, 0px 1px 1px 2px #6f6 inset;
}

#nav li.selected > a,
#nav > li.current-menu-item > a,
#nav > li.current_page_item > a,
#nav > li.current_page_ancestor > a {
 color: #fff !important;
 text-shadow: 1px 1px #000, 0px 0px 2px #222, 0px 0px 8px #fff !important;
}


#search-form > select {
  height: 22px;
}
    
input[type=submit] {
  vertical-align: middle;
  padding: 0 !important;
  min-height: 24px !important;
  font-size: 14px;
  border: none !important;
}

#search-terms {
 text-indent: 10px !important;
}

input[type=submit] {
 padding-left: 6px !important;
 padding-right: 6px !important;
 border: none !important;
 min-width: 60px !important;
 color: #fff !important;
 display: inline-block !important;
}

input[type=submit]:hover,
input[type=button]:hover,
input[type=reset] {
 text-shadow: 0px 0px 2px #fff;
}

/* CONTAINER */
#container,
.padder {
 border-radius: 0 !important;
 border: none !important;
}

/* POSTS */

.post {
 position: relative;
}

.post,
.post .post-content {
 border-radius: 0 !important;
 padding: 0 !important;
 border: 0 !important;
}

.post-content .posttitle,
.posttitle + .date {
 background-color: #ddd !important;
 font-family: Verdana, sans-serif;
}

.post-content .posttitle {
 padding: 15px 15px 0px 15px !important;
}

.post-content .posttitle,
.post-content .posttitle a {
 color: #333 !important;
 text-shadow: 1px 1px #fff, 1px 2px #ccc;
}

.post-content .posttitle a:hover {
 color: #000 !important;
}

.posttitle + .date {
 margin: 0 !important;
 font-family: Verdana, sans-serif !important;
 padding: 0px 0px 15px 15px !important;
 margin-bottom: 2px !important;
 background-color: #ddd !important;
 box-shadow: 0px 1px #fff, 0px 2px #000 !important;
}

.posttitle + .date a {
 color: #333 !important;
}

.posttitle + .date a:hover {
 color: #000 !important;
 text-shadow: 1px 1px #ccc;
}

.entry {
 padding: 15px !important;
}

.entry center p a {
 background-color: #444 !important;
 min-width: 200px !important;
 display: inline-block !important;
 padding: 2px !important;
 border: solid 1px #555 !important;
 box-shadow: 0px 0px 0px 1px #333 inset, 0px 0px 10px 1px #4f4f4f inset;
 color: #ccc !important;
}

.entry > p:first-child > strong:first-child {
  font-size: 16px !important;
}
.entry center p a.more-link {
 margin-top: 5px !important;
 display: inline-block !important;
 font-size: 15px !important;
}

.entry .page-link {
 margin: 10px -25px -15px -25px !important;
 padding: 5px 20px 10px 20px !important;
 width: 100%;
 display: inline-block !important;
 position: relative !important;
 background: #333 !important;
 clear: left;
 border-bottom: solid 1px #555 !important;
 border-top: solid 1px #333 !important;
 box-shadow: 0px 5px  5px #333;
}

.entry .page-link p {
 max-width: 700px !important;
}

.entry .page-link p a,
.page-numbers {
 background: #333 !important;
 margin-left: 3px !important;
 margin-top: 5px !important;
 border: solid 1px #444 !important;
 box-shadow: none !important;
 min-width: 20px !important;
 max-width: 20px !important;
 display: inline-block;
 text-align: center;
 color: #fff !important;
}

.entry .page-link a:hover,
.entry center p a:hover,
.page-numbers:hover:not(.current) {
 color: #fff !important;
 background: #08e !important;
 text-shadow: 1px 1px 0px #000!important;
 box-shadow: none !important;
}

.page-numbers.current {
 background: #666 !important;
}

.post p.postmetadata {
 font-family: Verdana, sans-serif !important;
 padding: 0 20px 10px 20px;
 box-sizing: border-box;
 -moz-boz-sizing: border-box;
}

.entry:after,
.post p.postmetadata:before,
.post p.postmetadata:after
  {
  content: '';
  clear: both;
  display: block;
}

.post p.postmetadata a {
 color:#ccc !important;
}

.post p.postmetadata a:hover {
 color: #fff;
 text-shadow: 1px 1px #000;
}

.post p.postmetadata a:before {
 content: "\220e\a0";
}

/* NAV */
#nav-below,
#nav-above {
 font-family: Verdana, sans-serif !important;
 text-decoration: none !important;
}

#nav-below .alignLeft a {
 background: #fff !important;
}

/* POST */
a[rel=prev],
a[rel=next],
#nav-above a,
#nav-below a {
 z-index: 9999 !important;
 margin: 0 !important;
 padding: 5px 10px 5px 10px !important;
 background: #333 !important;
 vertical-align: middle;
 display: inline-block;
 color: #ddd !important;
 text-shadow: 1px 1px #000;
 border-bottom: solid 1px #555 !important;
}

a[rel=prev]:hover,
a[rel=next]:hover,
#nav-below a:hover,
#nav-above a:hover  {
 background: #08b !important;
 box-shadow: 0px -1px #333 inset, 0px -2px #ccc inset;
}

#comments {
 margin-top: 5px !important;
 border: none !important;
}

#comments > h3:first-child {
 background-color: #333 !important;
 box-shadow: 0px 1px #666, 0px 2px #444 !important;
 color: #999 !important;
 text-shadow: 1px 1px #000;
 padding: 10px !important;
 font-family: Arial, sans-serif !important;
}

ul.item-list li {
 border: none !important;
}

.commentlist li,
ul.item-list li,
.activity-inner,
.activity-comments ul {
 border: none !important;
 border-top: solid 1px #3a3a3a !important;
 padding: 8px !important;
 margin: -1px 0 0 0 !important;
 box-shadow: 0px 20px 30px -30px #555 inset !important;
}

.activity-header > p:first-child {
 background: none !important;
 margin-bottom: 4px !important;
}

.activity-content {
 margin-bottom: 5px !important;
}

.activity-comments ul {
 background: none !important;
 border-radius: 0 !important;
 padding: 0 !important;
}

.activity-inner {
 border-left: solid 1px #666 !important;
}

.commentlist > li.comment,
.activity-item {
  border: 1px solid #3a3a3a !important;
}

.commentlist .comment-avatar-box {
 margin: 0 8px 0 0 !important;
}

.commentlist .comment-meta {
 margin-top: 0 !important;
}

.commentlist .comment-options {
 margin-bottom: 0 !important;
}

.commentlist .children {
 margin-top: 10px !important;
}

#comments ol.commentlist {
 border: none !important;
}

#respond {
 margin-top: 5px !important;
 margin-bottom: 5px !important;
 border: solid 1px #333 !important;
 border-radius: 0 !important;
}

#reply-title {
 color: #ccc !important;
 text-shadow: 1px 1px #000;
 margin: -15px -15px 15px -15px !important;
 padding: 10px 15px 10px 15px !important;
 background: #333 !important;
 box-shadow: 0px 1px #444, 0px 10px 50px -20px #111 inset;
}

#respond .comment-avatar-box {
 margin-right: 15px !important;
}

#cancel-comment-reply-link {
 vertical-align: middle !important;
 color: #ccc!important;
 text-shadow: 1px 1px #000;
}

#cancel-comment-reply-link:hover {
 color: #fff !important;
}

p.form-allowed-tags {
 width: auto !important;
}

/* SIDEBAR */
#sidebar {
 border: none !important;
}

#sidebar a:hover {
 text-shadow: 1px 1px #000;
}

#sidebar > .padder {
 background: #333 !important;
 margin-left: -16px !important;
 border-radius: 0 !important;
 border: solid 1px #333 !important;
 border-right: none !important;
 border-top: none !important;
 margin-bottom: 10px !important;
 border-bottom: solid 1px #999 !important;
}

#sidebar-me {
 background: #bbb !important;
 margin: 0 !important;
 padding: 0 !important;
 margin: -19px !important;
 padding: 19px !important;

 padding-top: 10px !important; 

 border-bottom: solid 1px #333 !important;
 box-shadow: 0px -50px 20px #bbb inset;
}

#sidebar-me,
#sidebar-me a {
 color: #333 !important;
}

#sidebar-me a:hover {
 color: #000;
 text-shadow: 1px 1px #fff;
}

#sidebar-me .avatar {
 border-color: #666 !important;
 margin-top: 5px !important;
}

#sidebar > .padder div .widgettitle {
 background: #ddd !important;
 color: #333 !important;
 text-shadow: 1px 1px #fff;
 box-shadow: 0px 1px 0px #111, 0px 2px 0px #666, 0px 5px 5px #333;
}

#sidebar > .padder div .widgettitle a {
 color: #333 !important;
}

#sidebar-me a.button.logout {
 color: #fff !important;
 border-radius: 3px !important;
 padding: 7px !important;
}

#sidebar-me a.button.logout:hover {
 text-shadow: 0px 0px 2px #fff !important;
}

/* MISC */

.activity {
 border-radius: 0 !important;
 border-color: #ccc !important;
}

#message.info,
.info p {
 border-radius: 0 !important;
}

.profile-fields {
 border: solid 1px #333 !important;
}

table tr td.label {
 border-right: solid 1px #333 !important;
}

span.activity, div#message p {
 background: #333 !important;
}

.user-nicename + .activity {
 margin-left: 5px !important;
}

input[alt^=PayPal] {
 padding: 5px !important;
 background-color: #ccc;
 border-bottom: solid 2px #eee !important;
}

#whats-new-form,
#whats-new-submit {
 clear: both !important;
 border: none !important;
 padding: 0 !important;
 margin: 0 0 10px 0 !important;
}

#whats-new-options {
 overflow: visible !important;
 height: auto !important;
 display: inline-block !important;
 clear: both;
 width: 100%;
 margin-top: 5px !important;
}

#whats-new-submit {
 float: right !important;
}

#content .padder .item-list-tabs {
 clear: both !important;
 margin-left: 0px !important;
}

.item-list-tabs, 
.item-list-tabs ul {
 margin: 0 0 15px 0 !important;
 display: inline-block;
 width: 100%;
}

div#subnav.item-list-tabs ul li.last {
 margin: 0 !important;
}

.item-list-tabs ul,
.item-list-tabs ul li {
 display: inline-block !important;
 margin: 0 !important;
 vertical-align: middle;
 padding: 6px !important;
}

.item-list-tabs select {
 margin-right: 15px !important;
}

div[role=main]:not(.page) {
 margin-top: 15px !important;
}

/* FOOTER */
#footer,
#footer-widgets {
 margin: 0 !important;
 border-radius: 0 !important;
}

#footer-widgets {
 border-top: solid 1px #555 !important;
 box-shadow: 0px -5px 5px #222 inset;
}

#footer a {
 color: #ccc !important;
 text-shadow: 1px 1px #000;
 padding: 4px !important;
}

#footer a:hover {
 box-shadow: 0px -4px #ccc inset;
}

/* LIVE */
body.page-id-27 .padder.one-column {
 padding: 15px !important;
}

#chat_embed {
 height: 560px !important;
}

#livewrapperthingy {
 overflow: visible !important;
 margin: 0 !important;
 padding: 0px !important;
 background: #333 !important;
 border-top: solid 3px #666 !important;
 border-bottom: solid 3px #666 !important;
}

#livewrapperthingy2 {
 overflow: visible !important;
 margin: 0 !important;
 position: relative !important;
}

/*MISC*/

.bbp-template-notice {
 border-radius: 0 !important;
}

img.default-avatar {
 border-color: #ccc !important;
}

img.turbo-avatar {
 border-color: #08b !important;
 box-shadow: 0px 0px 4px 1px #08b;
}

img.mod-avatar {
 box-shadow: 0px 0px 4px 1px #2b2;
}

h2.pagetitle {
 font-family: Arial Black, Arial, sans-serif !important;
 color: #fff !important;
 text-shadow: 1px 1px #000;
}

.bbp-body ul li.bbp-forum-freshness > a,
.bbp-body ul li.bbp-forum-freshness > p {
 float: left !important;
 clear: both !important;
}


/* SEARCH */
#search-bar {
 vertical-align: middle !important;
 padding-right: 15px !important;
}

#search-which {
 line-height: 14px !important;
}
    
    
}
